INSERT INTO `t_bookkey` (`bn`, `en`, `tn_f`, `tn_s`, `tn_a`, `tn_o`, `intro`) VALUES
(1, 'Genesis', 'தொடக்க நூல்', 'தொடக்க நூல்', 'தொநூ', 'ஆதியாகமம்', '<p>திருவிவிலியத்தில் முதற்கண் இடம்பெறும் இந்நூல் இஸ்ரயேல் மக்களின் வரலாற்றை மனித இனத்தின் தோற்றத்துடன் இணைத்துக் காட்டுகின்றது.</p><p>இவ்வுலகும் மனிதனும் உருவானதற்கு முதல் காரணம் இறைவனே ஆவார்; கடவுள் மனிதனுடன் கொண்டுள்ள உறவு, படைப்பிலிருந்தே தொடங்குகிறது; ஆனால் மனிதன் பாவத்தின் மூலம் இவ்வுறவை முறித்து, துன்பத்திற்கும் சாவுக்கும் தன்னையே உள்ளாக்கிக் கொள்கிறான்; ஆயினும் கடவுள் மனிதன்மேல் தொடர்ந்து கனிவு காட்டுகிறார் என்று இந்நூல் வலியுறுத்துகின்றது.</p><p>அனைத்து மானிடரையும் மீட்குமாறு, கடவுள் ஆபிரகாமைத் தேர்ந்தெடுத்து, அவர்தம் வழிமரபினர் வரலாற்றில் தாமே செயல்பட்டு, மீட்புப் பணியைத் தொடர்ந்தாற்றுகிறார் என்பதை இந்நூல் விரித்துரைக்கின்றது. ஆபிரகாம், ஈசாக்கு, யாக்கோபுக்குத் தாம் அளித்த வாக்குறுதியை இறைவன் நிறைவேற்றுவார் என்ற நம்பிக்கையுடன் இந்நூல் முற்றுப்பெறுகின்றது.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>உலகையும் மனித இனத்தையும் கடவுள் படைத்தல் 1:1 - 2:25</li><li>மனிதனின் வீழ்ச்சி - துன்பத்தின் தொடக்கம் 3:1 - 24</li><li>ஆதாம் முதல் நோவா வரை 4:1 - 5:32</li><li>நோவாவும் வெள்ளப் பெருக்கும் 6:1 - 10:32</li><li>பாபேல் கோபுரம் 11:1 - 9</li><li>சேம் முதல் ஆபிரகாம் வரை 11:10 - 32</li><li>மூதாதையர்: ஆபிரகாம், ஈசாக்கு, யாக்கோபு 12:1 - 35:29</li><li>ஏசாவின் வழிமரபினர் 36:1 - 43</li><li>யோசேப்பும் அவருடைய சகோதரரும் 37:1 - 45:28</li><li>இஸ்ரயேலர் எகிப்து நாட்டில் குடியேறுதல் 46:1 - 50:26</li></ol>'),
(2, 'Exodus', 'விடுதலைப் பயணம்', 'விடுதலைப் பயணம்', 'விப', 'யாத்திராகமம்', '<p>ஒடுக்கப்பட்ட இஸ்ரயேல் மக்கள் எகிப்து நாட்டினின்று விடுதலை பெற்றது மீட்பு வரலாற்றில் ஒரு முக்கியக் கட்டமாகும். கடவுளே முன்வந்து தம் மக்களின் அடிமைத்தளையை அறுத்து, விடுதலை நோக்கி அவர்களை அழைத்துச் சென்ற மாபெரும் பாஸ்கா நிகழ்ச்சியை ‘விடுதலைப் பயணம்’ என்னும் இந்நூல் விரித்துரைக்கின்றது.</p><p>ஆண்டவராகிய கடவுள் தாம் நல்கவிருக்கும் வளநாட்டை நோக்கி இஸ்ரயேல் மக்களைப் பேராற்றலோடு மோசேயின் மூலம் அழைத்துச் செல்கின்றார். வழியில், சீனாய் மலையடியில் அவர்களோடு உடன்படிக்கை செய்து, பத்துக் கட்டளைகளை வழங்கி, தமது உரிமைச் சொத்தாகிய அவர்களைத் தமக்கே உரிய அரச குருத்துவ இனமாகப் புனிதப்படுத்துகின்றார். ஆயினும், அம்மக்கள் இவ்வுடன்படிக்கையை மீறும்பொழுது, அவர்களைத் தண்டித்துத் தூய்மையாக்கி மீண்டும் ஏற்றுக் கொள்கின்றார். இந்நிகழ்ச்சிகள் இந்நூலின் முற்பகுதியில் இடம்பெற்றுள்ளன.</p><p>இஸ்ரயேல் மக்களின் சமய அமைப்புகளை நெறிப்படுத்துமாறு கடவுள் தரும் பல்வேறு ஒழுங்குமுறைகள் இந்நூலின் பிற்பகுதியில் காணப்படுகின்றன.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>இஸ்ரயேலர் எகிப்தினின்று விடுதலை பெறல் 1:1 - 15:21<ul>அ) எகிப்தில் அடிமைத்தனம் 1:1 - 22</ul><ul>ஆ) மோசேயின் பிறப்பும் இளமைப் பருவமும் 2:1 - 25</ul><ul>இ) மோசேயின் அழைப்பு 3:1 - 4:31</ul><ul>ஈ) மோசேயும் ஆரோனும் பார்வோனிடம் விடுதலை கேட்டல் 5:1 - 11:10</ul><ul>உ) பாஸ்கா - எகிப்தினின்று வெளியேறல் 12:1 - 15:21</ul></li><li>செங்கடல் முதல் சீனாய் மலை வரை 15:22 - 18:27</li><li>பத்துக் கட்டளைகள் - உடன்படிக்கை நூல் 19:1 - 24:18</li><li>உடன்படிக்கைக் கூடாரம் - வழிபாட்டு ஒழுங்குமுறைகள் 25:1 - 40:38</li></ol>'),
(3, 'Leviticus', 'லேவியர்', 'லேவியர்', 'லேவி', 'லேவிய‌ராகமம்', '<p>பழங்கால இஸ்ரயேலர் தம் கடவுளின் தூய தன்மையும் அவரை வழிபடுவதற்கான முறைகளும், அவற்றை நிறைவேற்றும் குருக்களுக்கான நெறிகளும் அவ்வினத்தார் கடைப்பிடிக்க வேண்டிய ஒழுங்குகளும் இந்நூலில் இடம் பெறுகின்றன.</p><p>‘உன்மீது நீ அன்புகூர்வதுபோல், உனக்கு அடுத்திருப்பவர் மீதும் அன்புகூர்வாயாக!’ என்னும் ஆண்டவர் இயேசுவின் இரண்டாம் பெரிய கட்டளை இந்நூலில் (19:18) இடம் பெறுவது குறிப்பிடத்தக்கது.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>காணிக்கைப் பலிகளுக்கான சட்டங்கள் 1:1 - 7:33</li><li>ஆரோன், அவர்தம் புதல்வர் ஆகியோரின் திருநிலைப்பாட்டிற்கான நெறிமுறைகள் 8:1 - 10:20</li><li>குருக்களின் தூய்மையும் தீட்டும் பற்றிய சட்டங்கள் 11:1 - 15:33</li><li>பாவக் கழுவாய் நாள் 16:1 - 34</li><li>தூய்மையான வாழ்விற்கும் வழிபாட்டிற்குமான சட்டங்கள் 17:1 - 27:34</li></ol>'),
(4, 'Numbers', 'எண்ணிக்கை', 'எண்ணிக்கை', 'எண்', 'எண்ணாகமம்', '<p>‘எண்ணிக்கை’ என்னும் இத்திருநூல் இஸ்ரயேலரின் வரலாற்றில், அவர்கள் சீனாய் மலையை விட்டுப் புறப்பட்டு, வாக்களிக்கப்பட்ட நாட்டின் கிழக்கு எல்லையை அடைந்ததுவரை நாற்பது ஆண்டுகளாக நிகழ்ந்தவற்றின் தொகுப்பாகும். சீனாய் மலையினின்று புறப்படும் முன்னும் யோர்தானுக்குக் கிழக்கே மோவாபில் ஒரு தலைமுறை கடந்த பின்னும் மோசே செய்த கணக்கெடுப்பின் காரணமாக இந்நூல் இப்பெயரைப் பெறுகிறது.</p><p>மேலும் காதேசு-பர்னேயாவில் இஸ்ரயேலருக்கு நேர்ந்த இன்னல்களும், அம்மக்கள் கடவுளுக்கும் மோசேக்கும் எதிராகச் செய்த கிளர்ச்சியும் இந்நூலில் விரித்துரைக்கப்படுகின்றன. ஆயினும் கடவுள், மக்கள்மேல் அக்கறைகொண்டு அவர்களின் குறைகளைப் பொருட்படுத்தாது அவர்களை ஏற்றுக்கொள்ளும் அன்பையும் இந்நூல் எடுத்துக்காட்டுகின்றது. அதுபோன்று, கடவுளுக்கும் மக்களுக்கும் மோசே உண்மையுடன் பணியாற்றுவது இந்நூலில் சிறப்பிடம் பெறுகின்றது.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>இஸ்ரயேல் மக்கள் சீனாய் மலையைவிட்டுப் புறப்பட ஆயத்தப்படுதல் 1:1 - 9:29<ul>அ) மக்கள்தொகை முதல் கணக்கெடுப்பு 1:1 - 4:49</ul><ul>ஆ) சட்டங்களும் விதிமுறைகளும் 5:1 - 8:26</ul><ul>இ) இரண்டாம் பாஸ்கா 9:1 - 23</ul></li><li>சீனாய் மலை முதல் மோவாபு வரை 10:1 - 21:35</li><li>மோவாபில் நிகழ்ந்தவை 22:1 - 32:42</li><li>எகிப்து தொடங்கி மோவாபு வரையிலான விடுதலைப் பயண நிகழ்ச்சிகளின் சுருக்கம் 33:1 - 49</li><li>யோர்தானைக் கடக்குமுன் கொடுக்கப்பட்ட கட்டளைகள் 33:50 - 36:13</li></ol>'),
(5, 'Deuteronomy', 'இணைச் சட்டம்', 'இணைச் சட்டம்', 'இச', 'உபாகமம்', '<p>இஸ்ரயேல் மக்கள் பாலை நிலத்தில் நெடும் பயணம் செய்து, கானான் நாட்டில் நுழைவதற்குச் சற்றுமுன், அவர்களுக்கு மோசே வழங்கிய பேருரைகளின் தொகுப்பாக ‘இணைச் சட்டம்’ என்னும் இந்நூல் அமைந்துள்ளது.</p><p>இந்நூலில் தரப்பட்டுள்ள செய்திகளாவன:</p><ol><li>கடந்த நாற்பதாண்டுகளில் நிறைவேறிய சிறப்பான நிகழ்ச்சிகளை மக்களுக்கு மோசே நினைவுறுத்துகையில் பாலை நிலத்தின் வழியாகக் கடவுள் அவர்களை எவ்வாறு வழிநடத்தினார் என்றும் அவருக்கு அவர்கள் எவ்வாறு பணிந்து நடக்க வேண்டும் என்றும் அவர்களுக்கு உணர்த்துகிறார்.</li><li>பத்துக் கட்டளைகளையும், சிறப்பாக முதற் கட்டளையையும் அவர்கள் பின்பற்றி, ஆண்டவருக்கு மட்டுமே அவர்கள் ஊழியம் செய்ய வேண்டுமென்று மோசே வற்புறுத்துகிறார். மேலும் வாக்களிக்கப்பட்ட நாட்டில் இஸ்ரயேலரின் வாழ்க்கைத் தடமாக இருக்க வேண்டிய பல்வேறு சட்டங்களை நினைவூட்டுகிறார்.</li><li>அவர்களோடு கடவுள் செய்துகொண்டுள்ள உடன்படிக்கையை நினைவுறுத்தி அதன் நிபந்தனைகளை அவர்கள் நிறைவேற்றுமாறு அவர்களை அழைக்கிறார்.</li><li>இறைமக்களின் அடுத்த தலைவராக யோசுவாவை அவர் ஏற்படுத்துகின்றார். இறுதியாக, கடவுளின் உண்மைத் தன்மையைப் போற்றிப் புகழ்ப்பா ஒன்று பாடி, இஸ்ரயேல் குலங்களுக்கு ஆசி வழங்கியபின், யோர்தான் ஆற்றுக்குக் கிழக்கே மோவாபு நாட்டில் இறக்கின்றார்.</li></ol><p>கடவுள் இஸ்ரயேல் மக்கள் மீது பேரன்பு கொண்டு, அவர்களுக்கு விடுதலை அளித்துத் தம் ஆசியை வழங்குகிறார். இதனை அவர்தம் மக்களும் நினைவில் கொண்டு அவர்மீது அன்பு கூர்ந்து அவரைப் பணிந்தால் அவர்கள் வாழ்வு பெற்று அவர்தம் ஆசியைத் தொடர்ந்து பெறுவர் என்பதே இந்நூலின் மையக் கருத்து ஆகும். ‘கட்டளைகளுள் முதன்மையானது எது?’ என்ற வினாவிற்கு இயேசு கிறிஸ்து தந்த விடை இந்நூலில் (6:4-6) இடம் பெற்றுள்ளது சிறப்புக்குரியது.</p><p><b>குறிப்பு :</b> எபிரேய மூலத்தில் ஒருமையும் (நீ, உன்…) பன்மையும் (நீங்கள், உங்கள்…) கலந்து காணப்படுகின்றன. ஆயினும் இந்நூலில் பொருள் இலக்கண அமைதிக்கேற்ப அவை முறைப்படுத்தப்பட்டுள்ளன.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>மோசேயின் முதல் பேருரை 1:1 - 4:49</li><li>மோசேயின் இரண்டாம் பேருரை 5:1 - 26:19<ul>அ) பத்துக் கட்டளைகள் 5:1 - 10:22</ul><ul>ஆ) சட்டங்கள், நியமங்கள், எச்சரிப்புகள் 11:1 - 26:19</ul></li><li>கானான் நாட்டில் நுழைவதற்கான அறிவுரைகள் 27:1 - 28:68</li><li>உடன்படிக்கையைப் புதுப்பித்தல் 29:1 - 30:20</li><li>மோசேயின் இறுதி மொழிகள் 31:1 - 33:29</li><li>மோசேயின் இறப்பு 34:1 - 12</li></ol>'),
(6, 'Joshua', 'யோசுவா', 'யோசுவா', 'யோசு', 'யோசுவா', '<p>இறைவனால் தேர்ந்துகொள்ளப்பட்டு, மோசேக்குப் பின் இஸ்ரயேல் மக்களின் தலைவராகச் செயல்பட்டவர் யோசுவா. இவர் கானான் நாட்டின் பல்வேறு பகுதிகளைக் கைப்பற்றி, இஸ்ரயேலின் குலங்களுக்குப் பிரித்துக் கொடுத்ததை விரித்துக் கூறுகிறது ‘யோசுவா’ என்னும் இந்நூல்.</p><p>இந்நூலில் காணக்கிடக்கும் நிகழ்ச்சிகளுள், யோர்தான் ஆற்றைக் கடத்தல், எரிகோவின் வீழ்ச்சி, வாக்களிக்கப்பட்ட நாட்டில் குடியேறுதல், உடன்படிக்கையைப் புதுப்பித்தல் ஆகியவை குறிப்பிடத்தக்கவை.</p><p>முன்பு மோசேயின் மூலம் இஸ்ரயேலரை வழிநடத்திய அதே ஆண்டவர், தொடர்ந்து யோசுவாவின் வாயிலாகவும் தம் மக்களுக்குத் தாமே முன்னின்று போரிட்டு, அவர்களுக்கு வெற்றியை அருளினார் என்பது இந்நூலின் மையக் கருத்தாகும்.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>கானான் நாட்டைக் கைப்பற்றல் 1:1 - 12:24<ul>அ) யோசுவா தலைமைப் பொறுப்பேற்றல் 1:1 - 18</ul><ul>ஆ) இஸ்ரயேலரின் வெற்றிகள் 2:1 - 11:23</ul><ul>இ) தோல்வியுற்ற மன்னர்களின் பெயர்கள் 12:1 - 23</ul></li><li>நாட்டைப் பங்கிட்டுக் கொடுத்தல் 13:1 - 21:45<ul>அ) யோர்தானுக்குக் கிழக்கே உள்ள பகுதி 13:1 - 33</ul><ul>ஆ) யோர்தானுக்குக் மேற்கே உள்ள பகுதி 14:1 - 19:51</ul><ul>இ) அடைக்கல நகர்கள் 20:1 - 9</ul><ul>ஈ) லேவியர்க்குரிய நகர்கள் 21:1 - 45</ul></li><li>கிழக்கே குடியேறிய குலத்தார் 22:1 - 34</li><li>யோசுவாவின் இறுதி மொழிகள் 23:1 - 16</li><li>செக்கேமில் உடன்படிக்கையைப் புதுப்பித்தல் 24:1 - 33</li></ol>'),
(7, 'Judges', 'நீதித் தலைவர்கள்', 'நீதித் தலைவர்கள்', 'நீத', 'நீதிபதிகள்/நியாயாதிபதிகள் ஆகமம்', '<p>‘நீதித் தலைவர்கள்’ என்னும் இந்நூல் இஸ்ரயேலர் கானான் நாட்டைக் கைப்பற்றியதற்கும் அவர்களிடையே முடியாட்சி தொடங்கியதற்கும் இடைப்பட்ட காலத்தைப் பற்றியதாகும். இக்காலக் கட்டத்தில் இறைவன் இஸ்ரயேல் மக்களுக்குத் தாம் தெரிந்துகொண்டோர் மூலமாக விடுதலை அளித்து அவர்களைப் பாதுகாத்து வந்தார். இவர்களுள் பெரும்பாலோர் வலிமைமிகு வீரர்களாகவும் படைத் தலைவர்களாகவும் ஆளுநர்களாகவும் இருந்தனர்.</p><p>இஸ்ரயேலரின் வாழ்வும் வெற்றியும் கடவுளிடம் அவர்கள் கொண்டிருந்த நம்பிக்கையைப் பொறுத்தே அமைந்திருந்தது என்பதையும் அவர்களது நம்பிக்கையின்மை அவர்களுக்கு அழிவையே கொணர்ந்தது என்பதையும் இந்நூல் வலியுறுத்திக் கூறுகின்றது. அவர்கள் இறைவனைக் கைவிட்டு அவதியுற்ற காலத்திலும் மனம்மாறி அவரிடம் திரும்பிவந்தால், அவர் தம் மக்களைத் தவறாது பாதுகாத்து அவர்களுக்கு விடுதலை அளிப்பார் என்பதையும் இந்நூல் சுட்டிக் காட்டுகின்றது.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>யோசுவாவின் இறப்புவரை நிகழ்ந்தவை 1:1 - 2:10</li><li>இஸ்ரயேலின் நீதித் தலைவர்கள் 2:11 - 16:31</li><li>பல்வேறு நிகழ்ச்சிகள் 17:1 - 21:25</li></ol>'),
(8, 'Ruth', 'ரூத்து', 'ரூத்து', 'ரூத்', '', '<p>இரண்டு பெண்கள்; இருவரும் கைம்பெண்கள். ஒருவர் வயதான நகோமி; மற்றவர் இளம் பருவத்தினரான ரூத்து. இருள், சாவு, அழிவு அனைத்தும் சேர்ந்து இவர்களது வாழ்க்கையில் புகுந்து கடவுளின் திட்டத்தையே சீர்குலைக்க முயல்கின்றன. ஆனால் இறைவன் இத்தீமைகளையெல்லாம் முறியடித்துத் தம் திட்டத்தை நிறைவேற்றுவதை ‘ரூத்து’ என்னும் இத்திருநூல் அழகுற எடுத்துக் காட்டுகிறது.</p><p>மனித வரலாற்றில், துயரக் கட்டங்களையெல்லாம் கடவுள் வெற்றி மகுடமாக மாற்றித் தருகின்றார் என்பதே விவிலிய நூலின் அடிப்படைக் கருத்து. அத்தகைய மாபெரும் செயல்களில் பெண்களுக்கும் அரியதொரு பங்கை அளிக்கிறார் என்பது இந்நூலால் விளங்குகிறது.</p><p>இவ்வரலாறு நிகழுமிடம் பெத்லகேம் என்னும் தாவீதின் நகர். காலம் வாற்கோதுமை அறுவடைக் காலம். நகோமியின் அருமை மருமகளான ரூத்தின் வழியாக இறையருள் செயலாற்றுகிறது; இவ்விரு கைம்பெண்களின் வாழ்க்கை மீண்டும் மலர்கிறது. இயேசு கிறிஸ்துவின் மூதாதையர் அட்டவணையில் ரூத்தின் பெயரும் இடம் பெறுகிறது. இவற்றை இந்நூலில் காண்க.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>ரூத்துடன் நகோமி பெத்லகேமுக்குத் திரும்பல் 1:1 - 22</li><li>ரூத்து போவாசைச் சந்தித்தல் 2:1 - 3:18</li><li>போவாசு ரூத்தை மணமுடித்தல் 4:1 - 22</li></ol>'),
(9, '1 Samuel', 'சாமுவேல் - முதல் நூல்', '1 சாமுவேல்', '1 சாமு', '', '<p>‘1 &amp; 2 சாமுவேல்’ என்னும் நூல்களில் இஸ்ரயேல் அரசுரிமையின் தொடக்க வரலாறு காணப்படுகிறது. நீதித் தலைவர்களின் காலம் அரசுரிமையின் காலமாக மாறியது குறித்த நிகழ்ச்சிகள் ‘1 சாமுவேல்’ என்னும் இந்நூலில் இடம் பெறுகின்றன. மேலும், நீதித் தலைவர்களின் வரிசையில் இறுதியாக வந்தவரான சாமுவேல், இஸ்ரயேலின் முதல் அரசரான சவுல், சிறுபருவத்தே இறைவனால் தேர்ந்தெடுக்கப்பட்டு இஸ்ரயேலின் பேரரசராக உயர்த்தப்பட்ட தாவீது ஆகிய மூவரை மையமாகக் கொண்டு ஏற்பட்ட சமுதாய மாற்றமும் இந்நூலில் இடம் பெறுகிறது.</p><p>கடவுளுக்கு உண்மை உள்ளவர்களாய் இருந்தபோது வெற்றியும், அவருக்கு உண்மையற்றவர்களாய் நடக்கும்போது அழிவும் ஏற்படும் என்னும் கருத்து இந்நூலில் தெளிவாக்கப்படுகிறது. ஆண்டவரே இஸ்ரயேலின் உண்மையான அரசர் என்று கருதப்பட்டார். ஆனால், மக்களின் விருப்பத்திற்கு இணங்கி அவர் அவர்களுக்கு ஓர் அரசரைத் தேர்ந்தெடுக்கிறார். ஆயினும் அரசரும் இஸ்ரயேல் மக்களும் கடவுளின் ஆட்சிக்கு உட்பட்டவர்கள் என்பதும், செல்வர் வறியோர் ஆகிய எல்லா மக்களின் உரிமைகளும் கடவுளின் திருச்சட்டத்தின் கீழ் சமமாகக் காக்கப்பட வேண்டும் என்பதும் இந்நூலில் வலியுறுத்தப்படுகின்றன.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>இஸ்ரயேலின் தலைவர் சாமுவேல் 1:1 - 7:17</li><li>சவுல் அரசராதல் 8:1 - 10:27</li><li>சவுல் ஆட்சியின் முற்பகுதி 11:1 - 15:35</li><li>தாவீதும் சவுலும் 16:1 - 30:31</li><li>சவுல், அவர்தம் புதல்வர்கள் ஆகியோரின் இறப்பு 31:1 - 13</li></ol>'),
(10, '2 Samuel', 'சாமுவேல் - இரண்டாம் நூல்', '2 சாமுவேல்', '2 சாமு', '', '<p>‘1 சாமுவேல்’ என்னும் நூலின் தொடர்ச்சியான ‘2 சாமுவேல்’, அரசர் தாவீதின் ஆட்சி வரலாற்றைக் கூறுகிறது. முதல் நான்கு அதிகாரங்கள், தெற்கே யூதாவின் மேல் அவர் ஆட்சி புரிந்ததையும், பின்னைய அதிகாரங்கள், வட பகுதியான இஸ்ரயேல் உட்பட நாடு முழுவதன்மேலும் அவர் ஆட்சி புரிந்ததையும் விரித்துரைக்கின்றன. தாவீது தம் அரசை விரிவுபடுத்தவும், தம் நிலையை உறுதிப்படுத்தவும் நாட்டிலுள்ள எதிரிகளோடும் வேற்றரசுகளோடும் போராடியதை இந்நூல் படம்பிடித்துக் காட்டுகிறது.</p><p>தாவீது ஆண்டவரிடம் ஆழ்ந்த நம்பிக்கை கொண்டிருந்ததோடு, தம் மக்களின் முழு நம்பிக்கையையும் பெற்றிருந்தார். இருப்பினும் சில நேரங்களில் தம் தவறான நோக்கங்களை நிறைவேற்றிக் கொள்வதற்காக எந்தப் பாவத்தையும் செய்யத் தயங்காதவராய் இருந்திருக்கிறார். ஆயினும், அவருடைய பாவங்களை இறைவாக்கினர் நாத்தான் அவருக்குச் சுட்டிக்காட்டியபோது, அவர் அவற்றை அறிக்கையிட்டுக் கடவுள் அளித்த தண்டனையை ஏற்றுக்கொள்ளவும் செய்தார்.</p><p>தாவீதின் வாழ்க்கையும் அவர்தம் வெற்றிகளும் இஸ்ரயேல் மக்களின் மனத்தில் ஆழமாய்ப் பதிந்துவிட்டன. எனவேதான் பிற்காலத்தில் நாடு தொல்லைக்குட்பட்ட நேரங்களில், அவரைப்போல் தங்களுக்காகப் போராடக்கூடிய ‘தாவீதின் மகன்’ தங்களுக்கு அரசராய் மீண்டும் வரவேண்டுமென்று அவர்கள் மிகவும் எதிர்பார்த்தனர்.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>யூதாவின் மீது தாவீதின் ஆட்சி 1:1 - 4:12</li><li>அனைத்து இஸ்ரயேலின் மீதும் தாவீதின் ஆட்சி 5:1 - 24:25<ul style="list-style-type: none;"><li>அ) முற்பகுதி 5:1 - 10:19</li><li>ஆ) தாவீதும் பத்சேபாவும் 11:1 - 12:25</li><li>இ) துன்பங்களும் தொல்லைகளும் 12:26 - 20:26</li><li>ஈ) பிற்பகுதி 21:1 - 24:25</li></ul></li></ol>'),
(11, '1 Kings', 'அரசர்கள் - முதல் நூல்', '1 அரசர்கள்', '1 அர', 'இராஜாக்கள் - முதல் நூல்', '<p>‘1 &amp; 2 சாமுவேல்’ என்னும் திருநூல்களில் இஸ்ரயேல் அரசுரிமையின் தொடக்க வரலாறு காணப்படுகிறது. ‘1 &amp; 2 அரசர்கள்’ என்னும் இந்நூல்கள் அதன் தொடர்ச்சியாக அமைந்துள்ளன.</p><p>‘1 அரசர்கள்’ மூன்று பகுதிகளைக் கொண்டுள்ளது:</p><ol><li>இஸ்ரயேல் மக்களினம் முழுவதன் மேலும் சாலமோன் அரசுரிமை பெறுதல்; அவர் தந்தை தாவீது இறத்தல்.</li><li>சாலமோனின் ஆட்சியும் மாட்சியும்; எருசலேமில் அவர் எழுப்பிய கோவிலின் சிறப்பு.</li><li>நாடு தெற்கு வடக்கு என்ற இரண்டு அரசுகளாகப் பிரிதல்; அவற்றைக் கி.மு. 850 வரை ஆண்ட அரசர்களின் வரலாறு.</li></ol><p>இவ்விரு நூல்களிலும் ஒவ்வொரு அரசனும் ஆண்டவருக்கு அவன் காட்டிய பற்றுறுதிக்கேற்ப, தீர்ப்பு வழங்கப்படுகிறான்; ஆண்டவரிடம் காட்டும் பற்றுறுதி நாட்டுக்கு வெற்றியைத் தருகின்றது; மாறாக, வேற்றுத் தெய்வ வழிபாடும் கீழ்ப்படியாமையும் அழிவையே கொணர்கின்றன. இந்த அளவுகோலின்படி வடநாட்டு அரசர்கள் எல்லாருமே தீயவழியில் நடந்தார்கள் என்றும், தென்னாட்டு அரசர்களில் சிலரும் அவ்வாறு நடந்தார்கள் என்றும் காட்டப்படுகிறது.</p><p>பல இறைவாக்கினர் சிலைவழிபாட்டினின்றும் ஆண்டவருக்குக் கீழ்ப்படியாமையினின்றும் மக்களைத் தடுத்து நிறுத்தினர். அவர்களுள் எலியா தலைசிறந்தவராக இந்நூலில் காட்டப்படுகின்றார்.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>தாவீது அரசரின் இறுதி நாள்கள் 1:1 - 2:12</li><li>சாலமோன் அரசராதல் 2:13 - 46</li><li>சாலமோனின் ஆட்சி 3:1 - 11:43<ul>அ) ஆட்சியின் முற்பகுதி 3:1 - 4:34</ul><ul>ஆ) எருசலேம் கோவில் கட்டுதல் 5:1 - 8:66</ul><ul>இ) ஆட்சியின் பிற்பகுதி 9:1 - 11:43</ul></li><li>பிளவுபட்ட நாடு 12:1 - 22:53<ul>அ) வடகுலங்களின் கிளர்ச்சி 12:1 - 14:20</ul><ul>ஆ) யூதா - இஸ்ரயேல் அரசர்கள் 14:21 - 16:34</ul><ul>இ) இறைவாக்கினர் எலியா 17:1 - 19:21</ul><ul>ஈ) இஸ்ரயேல் அரசன் ஆகாபு 20:1 - 22:40</ul><ul>உ) யோசபாத் அகசியா அரசர்கள் 22:41 - 53</ul></li></ol>'),
(12, '2 Kings', 'அரசர்கள் - இரண்டாம் நூல்', '2 அரசர்கள்', '2 அர', 'இராஜாக்கள் - இரண்டாம் நூல்', '<p>‘2 அரசர்கள்’ என்னும் இத்திருநூல் பிளவுண்ட யூதா - இஸ்ரயேல் அரசுகளின் வரலாற்றை ‘1 அரசர்கள்’ விட்ட இடத்திலிருந்து தொடர்கிறது.</p><p>இந்நூல் இரு பகுதிகளைக் கொண்டது:</p><ol><li>கி.மு. 850 முதல் கி.மு. 722 (சமாரியாவின் வீழ்ச்சி) வரையிலான வடக்கு - தெற்கு அரசுகளின் வரலாறு.</li><li>சமாரியாவின் வீழ்ச்சியிலிருந்து, கி.மு. 586 (எருசலேமின் வீழ்ச்சி) வரையிலான யூதா அரசர்களின் வரலாறு. இந்நூல் யோயாக்கின் அரசனது விடுதலையுடன் முடிவுறுகிறது.</li></ol><p>யூதா, இஸ்ரயேல் ஆகியவற்றின் அரசர்களும் மக்களும் ஆண்டவருக்கு அஞ்சி நடக்காததாலேயே அந்நாடுகள் பேரழிவுக்குள்ளாயின என்பதை இந்நூல் மிகவும் வற்புறுத்திக் கூறுகிறது. எருசலேம் வீழ்ச்சியுற்றதும், யூதா நாட்டு மக்கள் பாபிலோனுக்கு நாடுகடத்தப்பட்டதும் இஸ்ரயேலரின் வரலாற்றில் பெரும் திருப்புமுனையாக அமைந்துள்ளன.</p><p>இந்நூலில் எலியாவின் பதிலாளான இறைவாக்கினர் எலிசாவின் புதுமைகள் சிறப்பிடம் பெறுகின்றன.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>பிளவுபட்ட நாடு (தொடர்ச்சி) 1:1 - 17:41<ul>அ) இறைவாக்கினர் எலிசா 1:1 - 8:15</ul><ul>ஆ) யூதா, இஸ்ரயேல் அரசர்கள் 8:16 - 17:4</ul><ul>இ) சமாரியாவின் வீழ்ச்சி 17:5 - 41</ul></li><li>யூதா அரசு 18:1 - 25:30<ul>அ) எசேக்கியாவின் ஆட்சி 18:1 - 21:26</ul><ul>ஆ) யோசியாவின் ஆட்சி 22:1 - 23:30</ul><ul>இ) யூதாவின் இறுதி அரசர்கள் 23:31 - 24:20</ul><ul>ஈ) எருசலேமின் வீழ்ச்சி 25:1 - 30</ul></li></ol>'),
(13, '1 Chronicles', 'குறிப்பேடு - முதல் நூல்', '1 குறிப்பேடு', '1 குறி', 'நாளாகமம் - முதல் நூல்', '<p>‘சாமுவேல்’ மற்றும் ‘அரசர்கள்’ ஆகிய நூல்களில் குறிக்கப் பெற்ற நிகழ்ச்சிகளே ‘குறிப்பேடு’களில் வேறொரு கோணத்தில் காட்டப்படுகின்றன. ‘முதலாம் குறிபேட்டின்’ இரு அடிப்படைக் கருத்துக்களாவன:</p><ol><li>இஸ்ரயேல் மற்றும் யூதா அரசுகளுக்கு ஏற்பட்ட வீழ்ச்சிகளுக்கிடையிலும் கடவுள் தம் வாக்குறுதிகளைத் தொடர்ந்து காத்ததோடு, யூதாவில் இருந்தவர்கள் வழியாக, தம் மக்களுக்கான திட்டத்தைச் செயல்படுத்திக் கொண்டு வந்தார். இதற்குச் சான்றாக தாவீது மற்றும் சாலமோன் ஆகியோர் ஆற்றிய பெரும் சாதனைகளும், யோசபாத்து, எசேக்கியா, யோசியா, ஆகியோர் செய்த சமயச் சீர்திருத்தங்களும், மக்கள் கடவுளிடம் கொண்டிருந்த பற்றுறுதியும் விளங்குகிறன.</li><li>எருசலேம் கோவிலைக் கட்டியெழுப்பியவர் சாலமோனே. ஆயினும், அங்கு தொடங்கிய இறைவழிபாட்டு ஒழுங்குமுறைகளையும் அவற்றுக்கான குருத்துவ, லேவிய அமைப்புகளையும் ஏற்படுத்தியவர் தாவீதே.</li></ol><h2>நூலின் பிரிவுகள்</h2><ol><li>வழிமரபு அட்டவணை 1:1 - 9:44</li><li>சவுலின் இறப்பு 10:1 - 14</li><li>தாவீதின் ஆட்சி 11:1 - 29:30<ul>அ) தொல்லைகளும் சாதனைகளும் 11:1 - 22:1</ul><ul>ஆ) கோவிலைக் கட்டுவதற்கான தயாரிப்பு 22:2 - 29:30</ul></li></ol>'),
(14, '2 Chronicles', 'குறிப்பேடு - இரண்டாம் நூல்', '2 குறிப்பேடு', '2 குறி', 'நாளாகமம் - இரண்டாம் நூல்', '<p>‘இரண்டாம் குறிப்பேடு’ என்னும் இந்நூல் ‘முதலாம் குறிப்பேட்டின்’ தொடர்ச்சியாகும். இதன் முதற் பகுதி சாலமோனது ஆட்சியின் தொடக்கம் முதல் அவரது இறப்பு வரையிலான நிகழ்ச்சிகளை விரித்துரைக்கிறது. இரண்டாம் பகுதி அரசர் சாலமோனின் மகனும் அவருக்குப்பின் வந்தவனுமான ரெகபெயாமுக்கு எதிராக எரொபவாமின் தலைமையில் வடநாட்டுக் குலங்கள் கிளர்ந்தெழுந்ததை விளக்குகிறது. மூன்றாம் பகுதி எருசலேம் வீழ்ச்சியுற்ற கி.மு. 586 வரையிலான தென்னாட்டுக் குலங்கள் பற்றிய வரலாற்றைக் கொண்டுள்ளது.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>சாலமோனின் ஆட்சி 1:1 - 9:31<ul>அ) முற்பகுதி 1:1 - 17</ul><ul>ஆ) கோவில் கட்டப்படல் 2:1 - 7:10</ul><ul>இ) பிற்பகுதி 7:11 - 9:31</ul></li><li>வட நாட்டுக் குலங்களின் கலகம் 10:1 - 19</li><li>யூதாவின் அரசர்கள் 11:1 - 36:12</li><li>எருசலேமின் வீழ்ச்சி 36:13 - 23</li></ol>'),
(15, 'Ezra', 'எஸ்ரா', 'எஸ்ரா', 'எஸ்ரா', '', '<p>‘எஸ்ரா’ என்னும் இந்நூல் ‘குறிப்பேட்டின்’ தொடர்ச்சியாகும். கி.மு. 538இல் பாரசீக மன்னர் சைரசு பாபிலோனியாவைக் கைப்பற்றினார். அதே ஆண்டில் அவர் இஸ்ரயேல் மக்களுக்கு விடுதலை அளித்து யூதாவுக்குத் திரும்பிச் செல்ல அனுமதித்தார். செருபாபேலின் தலைமையில் இஸ்ரயேல் மக்களுள் ஒரு பகுதியினர் முதலில் திரும்பி வந்தனர். திரும்பிவந்த இஸ்ரயேலர் எருசலேமில் அழிந்திருந்த கோவிலைத் திரும்பவும், 515இல் கட்டியெழுப்பிப் புனிதப்படுத்தினர்; மீண்டும் அங்கு வழிபாடுகள் நடத்தினர். சில ஆண்டுகளுக்குப் பின் எஸ்ராவின் தலைமையில் இஸ்ரயேல் மக்களுள் மற்றொரு பகுதியினர் திரும்பி வந்தனர்.</p><p>எஸ்ரா ஒரு குரு; திருச்சட்ட வல்லுநர். உடன்படிக்கையின் மக்களாகவும், இறைவனின் புனித மக்களாகவும் தேர்ந்துகொள்ளப்பட்ட இஸ்ரயேல் மக்களின் சிறப்பு நிலையைக் காக்குமாறு அவர்களின் மறைவாழ்விலும், சமூக வாழ்விலும் மறுமலர்ச்சி ஏற்படுத்தினார். மேலும் அவர் ‘இறையாட்சி’ இஸ்ரயேல் மக்களிடையே நிலவுமாறு அரசியல், மறை ஆகியவற்றின் பொறுப்பைக் குருக்களிடமே ஒப்படைத்து, அவற்றிற்கான சட்டதிட்டங்களை வகுத்துத் தந்தார்.</p><p>இந்நூலின் பெரும் பகுதி எபிரேயத்திலும், சிறு பகுதி (4:8 -6:18, 7:12-16) அரமேயத்திலும் எழுதப்பட்டுள்ளன.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>சிறையிருப்பினின்று திரும்பி வருதலின் முதல் பகுதி 1:1 - 2:70</li><li>கோவில் மீண்டும் கட்டியெழுப்பப்பட்டு அர்ப்பணிக்கப்படல் 3:1 - 6:22</li><li>சிறையிருப்பினின்று எஸ்ராவின் தலைமையில் திரும்பி வருதலின் இரண்டாம் பகுதி 7:1 - 10:44</li></ol>'),
(16, 'Nehemiah', 'நெகேமியா', 'நெகேமியா', 'நெகே', '', '<p>‘நெகேமியா’ என்னும் இந்நூல், ‘எஸ்ரா’ நூலைப் போன்று ‘குறிப்பேட்டின்’ தொடர்ச்சியாகும். நெகேமியா, பாரசீகத் தலைநகரான சூசாவில் மன்னர் அர்த்தக்சஸ்தாவுக்குப் பானப் பணிவிடைக்காரராக இருந்தார். சொந்த நாடு திரும்பிய இஸ்ரயேல் மக்களின் இழிநிலையைக் கண்டு வருத்தமுற்றார். பாரசீக மன்னரால் யூதா நாட்டின் ஆளுநராக நியமனம் பெற்றார். பாழடைந்து கிடந்த எருசலேம் நகரின் மதிலைப் பல எதிர்ப்புகளுக்கிடையே மனம் தளராது கட்டியெழுப்பினார். சமய, சமூகச் சீர்திருத்தங்களை இஸ்ரயேல் மக்களிடையே செய்தார்.</p><p>இவரது காலத்தில் சட்டவல்லுநரான எஸ்ரா திருச்சட்டத்தை மக்கள் முன் வாசிக்க, மக்கள் தங்கள் பாவங்களை அறிக்கையிட்டு, உடன்படிக்கையின்படி வாழ உறுதி பூண்டனர். இறைவனின் உதவியின்றித் தம்மால் ஒன்றும் செய்ய இயலாது என்பதை நெகேமியா உணர்ந்திருந்தார். எனவே, அவர் பலமுறை இறைவனிடம் மன்றாடினார். அவர் உள்ளத்தினின்று எழுந்த மன்றாட்டுகள் பல இந்நூலில் இடம்பெற்றுள்ளன.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>நெகேமியா எருசலேமுக்கு வருதல் 1:1 - 2:20</li><li>எருசலேம் நகரின் மதில்கள் திரும்பக் கட்டப்படுதல் 3:1 - 7:73</li><li>திருச்சட்டம் வாசிக்கப்பட்டு உடன்படிக்கை புதுப்பிக்கப்படுதல் 8:1 - 10:39</li><li>நெகேமியாவின் பிற செயற்பாடுகள் 11:1 - 13:31</li></ol>'),
(17, 'Esther', 'எஸ்தர்', 'எஸ்தர்', 'எஸ்', '', '<p>‘எஸ்தர்’ என்னும் இந்நூலில் இடம்பெற்றுள்ள நிகழ்ச்சிகள் பாரசீகப் பேரரசர் அகஸ்வேரின் குளிர்கால அரண்மனையில் நடைபெற்றவை. எஸ்தர் என்னும் யூதப் பெண், தன் மக்கள்பால் கொண்டிருந்த பேரன்பினால், அவர்கள் தங்கள் எதிரிகளால் அழிக்கப்படாதவாறு, மிகுந்த துணிவுடன் செயல்பட்டதை இந்நூல் விளக்குகிறது. ‘பூரிம்’ என்ற யூதப் பெருவிழாவின் பொருளும் அதன் பின்னணியும் இதில் விளக்கப்படுகின்றன. கிரேக்க மொழிபெயர்ப்பில் இந்நூல் இன்னும் விரிவாகக் காணப்படுகிறது.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>எஸ்தர் அரசி ஆதல் 1:1 - 2:23</li><li>ஆமானின் சதிகள் 3:1 - 5:14</li><li>ஆமான் கொல்லப்படல் 6:1 - 7:10</li><li>யூதர் தங்கள் எதிரிகளை முறியடித்தல் 8:1 - 10:3</li></ol>'),
(18, 'Job', 'யோபு', 'யோபு', 'யோபு', '', '<p>விவிலியத்தின் ஞான இலக்கியங்களுள் ‘யோபு’ என்னும் இந்நூல் தலைசிறந்தது. ஒரு காலத்தில் கடவுளுக்கு ஏற்ற நீதிமானாக ஒருவர் வாழ்ந்து வந்தார்; அவர் பெயர் யோபு. அவர் செல்வர்; கடவுளுக்கு அஞ்சி நடந்தவர். கடவுளின் அனுமதியுடன் சாத்தான் யோபைச் சோதித்தான். இதனால் யோபு மக்களை இழந்தார். சொத்து சுகத்தை இழந்தார்; உடல் நலத்தையும் இழந்தார். இருப்பினும், அவர் கடவுளைத் தூற்றினாரில்லை. அவருக்கு ஏற்புடையவராகவே வாழ்ந்து வந்தார். அவர் மனைவியும் நண்பர்களும் அவருடன் வாக்குவாதம் செய்து, இறைவனின் நீதியை விளக்க முயன்றனர்.</p><p>பழைய ஏற்பாட்டுப் பின்னணியின்படி, துன்பத்திற்குக் காரணம் ஒருவர் செய்யும் பாவமே. ஆகவே, யோபு படும் துன்பத்திற்குக் காரணம் அவர் செய்த பாவமே என்பது நண்பர்களின் கூற்று. தாம் அத்தகைய குற்றம் ஏதும் செய்யவில்லை என்பது யோபு கூறும் மறுப்பு. இவ்வாறு ‘நீதிமான் ஏன் துன்பப்பட வேண்டும்?’ என்ற வினாவிற்கு விடை காணும் போக்கில், நாடகம்போல் அமைந்துள்ளது இந்நூல்.</p><p>இந்நூலின் காலம் திட்டவட்டமாகத் தெரியவில்லை. பல அறிஞர்கள் இது பாபிலோனிய அடிமை வாழ்வுக்குப் பிற்பட்டது என்பர்.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>முகவுரை 1:1 - 2:13</li><li>யோபும் அவர்தம் நண்பர்களும் 3:1 - 31:40<ul>அ) யோபின் முறையீடு 3:1 - 26</ul><ul>ஆ) முதல் உரையாடல் 4:1 - 14:22</ul><ul>இ) இரண்டாம் உரையாடல் 15:1 - 21:34</ul><ul>ஈ) மூன்றாம் உரையாடல் 22:1 - 27:23</ul><ul>உ) ஞானத்தின் மேன்மை 28:1 - 28</ul><ul>ஊ) யோபின் இறுதிப் பதிலுரை 29:1 - 31:40</ul></li><li>எலிகூவின் உரைகள் 32:1 - 37:24</li><li>யோபுக்கு ஆண்டவரின் பதில் 38:1 - 42:6</li><li>முடிவுரை 42:7 -17</li></ol>'),
(19, 'Psalms', 'திருப்பாடல்கள்', 'திருப்பாடல்கள்', 'திபா', 'சங்கீதங்கள்', '<p>‘திருப்பாடல்கள்’ என்னும் இந்நூல் விவிலியப் பக்திப் பாடல்கள், இறை மன்றாட்டுகள் ஆகியவற்றின் தொகுப்பு ஆகும். பல்வேறு காலக் கட்டங்களில் பல்வேறு கவிஞர்களால் இயற்றப்பட்ட இப்பாடல்களை இஸ்ரயேல் மக்கள் தங்கள் வழிபாட்டில் பயன்படுத்தினர். காலப்போக்கில் இவற்றின் தொகுப்பு அவர்களது திருமறைநூலின் முக்கியப் பகுதியாக ஏற்றுக்கொள்ளப்பட்டது.</p><p>இப்பக்திப் பாடல்கள் பலவகைப்படும்; அவையாவன:</p><ol><li>கடவுளைப் புகழ்ந்து வழிபடுவதற்கானவை.</li><li>கடவுளிடம் உதவி, பாதுகாப்பு, மீட்பு வேண்டிப் பாடியவை.</li><li>மன்னிப்பு வேண்டும் மன்றாட்டுகள்.</li><li>கடவுள் வழங்கிய ஆசிகளுக்கு நன்றி செலுத்தும் பாடல்கள்.</li><li>அரசர் பற்றிய பாடல்கள்.</li><li>அறிவுரை அளிக்கும் பாடல்கள்.</li></ol><p>இவை தனி மனிதரின் வேண்டுதலாகவோ, நாட்டு மக்களின் மன்றாட்டாகவோ அமைந்துள்ளன. இவற்றுள் பல, தனிப்பட்ட இறையடியாரின் உள்ளக்கிடக்கையை வெளிப்படுத்துகின்றன; ஏனையவை இஸ்ரயேல் இனத்தின் நாட்டங்களையும் உணர்ச்சிகளையும் படம்பிடித்துக் காட்டுகின்றன.</p><p>கிறிஸ்து இயேசுவின் திருவாழ்விலும் திருப்பணியிலும், திருப்பாடல்கள் சிறப்பிடம் பெற்றிருந்தன. புதிய ஏற்பாட்டிலும் இப்பாடல்கள் மேற்கோள்களாக எடுத்தாளப்பட்டுள்ளன. எனவே, இப்பாடல்கள் கிறிஸ்துவத் திருமறையிலும் திருச்சபையின் இறைவழிபாட்டிலும் தொடக்கத்திலிருந்தே சிறப்பிடம் பெற்றுள்ளமை குறிப்பிடத்தக்கது.</p><h2>நூலின் பிரிவுகள்</h2><p>முதல் பகுதி 1 - 41</p><p>இரண்டாம் பகுதி 42 - 72</p><p>மூன்றாம் பகுதி 73 - 89</p><p>நான்காம் பகுதி 90 - 106</p><p>ஐந்தாம் பகுதி 107 - 150</p>'),
(20, 'Proverbs', 'நீதிமொழிகள்', 'நீதிமொழிகள்', 'நீமொ', 'பழமொழி ஆகமம்', '<p>‘நீதிமொழிகள்’ என்னும் இந்நூல் ஒழுக்கத்தையும் சமயத்தையும் சார்ந்த போதனைகளின் தொகுப்பாகும். இவை சொற்கோவை, பழமொழி ஆகிய வடிவங்களில் காணப்படுகின்றன. இவற்றுள் பெரும்பாலானவை அன்றாட வாழ்வையும் நடைமுறை வழக்கையும் அடிப்படையாகக் கொண்டுள்ளன.</p><p>இந்நூல் ‘ஆண்டவரிடம் கொள்ளும் அச்சமே ஞானத்தின் தொடக்கம்’ எனத் தொடங்கி, சமய ஒழுக்கம் பற்றியும் நல்லறிவு, நன்னடத்தை ஆகியவை பற்றியும் விளக்கிக் கூறுகின்றது. இங்குக் காணப்படும் சொற்கோவைகள் பண்டைய இஸ்ரயேலின் ஞானிகளுடைய அனுபவமிக்க அறிவுரைகளாக அமைந்துள்ளன. மேலும் குடும்ப உறவுகள், பொருளீட்டு முயற்சிகள், சமூக உறவுகள், நன்னடத்தை, தற்கட்டுப்பாடு ஆகிய முறைமைகள் பற்றியும், மனத்தாழ்வு, பொறுமை, ஏழையர்பால் அன்பு, மாறாத நட்பு ஆகிய பண்புகள் பற்றியும் இந்நூல் விரித்துரைக்கின்றது.</p><h2>நூலின் பகுதிகள்</h2><ol><li>ஞானம் பற்றிய புகழுரை 1:1 - 9:18</li><li>சாலமோனின் நீதிமொழிகள் 10:1 - 29:27</li><li>ஆகூரின் மொழிகள் 30:1 - 33</li><li>பல்வேறு சொற்கோவைகள் 31:1 - 31</li></ol>'),
(21, 'Ecclesiastes', 'சபை உரையாளர்', 'சபை உரையாளர்', 'சஉ', 'சங்கத் திருவுரை ஆகமம்', '<p>‘சபை உரையாளர்’ என்னும் இந்நூல் ஒரு ஞானியின் சிந்தனைகளைக் கொண்டது. அவர் மானிட வாழ்வு எவ்வளவு குறுகியது, முரண்பாடானது எனக் கண்டுணர்கிறார். மனித வாழ்க்கையில் காணப்படும் அநீதிகளும் அவநம்பிக்கைகளும் அவருக்குப் பெரும் புதிர்களாகத் தோன்றுகின்றன. எனவே, ‘வாழ்க்கையே வீண்’ என்ற முடிவுக்கு வருகிறார். மனித வாழ்வின் போக்கை ஆண்டு நடத்தும் இறைவனின் வழிகளை அவரால் புரிந்துகொள்ள முடியவில்லை. ஆயினும் மக்கள் கடுமையாக உழைத்து இறைவன் அருளும் கொடைகளை வேண்டுமளவு துய்த்து மகிழுமாறு அறிவுரை கூறுகிறார்.</p><p>இந்நூலில் மனித வாழ்க்கையின் நிலையாமை, இயலாமை முதலியன விரித்துரைக்கப்படுகின்றன. ஆயினும் இந்நூல் விவிலியத்தில் இடம்பெற்றுள்ளமை மனிதர் தோல்வி மனப்பான்மையையும் மனத்தளர்வையும் இறைவனது துணையால் வெல்லலாம் என்பதையே காட்டுகின்றது. இந்நூல் தங்கள் சொந்த வாழ்க்கையைப் பிரதிபலிப்பதாகப் பலர் கருதலாம். ஆயினும், இக்கருத்துகளை எதிரொலிக்கும் இதே விவிலியம், கடவுள்மீது வைக்கும் நம்பிக்கைதான் மானிட வாழ்வுக்கு நிறைபொருளை அளிக்கும் என்று வற்புறுத்திக் கூறுவதையும் அவர்கள் உணர வேண்டும்.</p>'),
(22, 'Song of Solomon', 'இனிமைமிகு பாடல்', 'இனிமைமிகு பாடல்', 'இபா', 'உன்னத சங்கீதம்/பாட்டு', '<p>‘இனிமைமிகு பாடல்’ என்னும் இந்நூல் உண்மையில் ஒரு தொகைநூல்; காதற் கவிதைகளின் ஒரு தொகுப்பு. யூதப் பரம்பரையின்படி, இந்நூலில் அடங்கியுள்ள பாடல்களின் ஆசிரியர் மாமன்னர் சாலமோன்; ஆனால் சாலமோனின் காலத்திற்குப் பிற்பட்ட பாடல்களும் இந்தத் தொகைநூலில் இடம்பெற்றுள்ளன என்பதை மறுத்தல் இயலாது.</p><p>இந்நூலின் பாடல்களுக்குப் பல்வேறு பொருள்பொருத்தம் தருகின்றனர் விரிவுரையாளர்கள். கடவுளுக்கும் இஸ்ரயேல் மக்களினத்திற்கும் இடையே நிலவும் அன்புறவை (காதலுறவை) வருணிப்பவை இவை என்பர் சிலர்; பாலஸ்தீன நாட்டு மக்கள் நடுவில் திருமணத்தின்போது பாடப்பட்டவை என்பர் மற்றும் சிலர். ஆனால் ஆண்-பெண் இருவருக்கும் நடுவில் முகிழ்க்கும் இயல்பான காதலுணர்வை இருவரும் ஒருவர்க்கொருவர் வெளிப்படுத்தும் கவிதைகள் அல்லது கூற்றுகள் இவை என்பதே ஏற்புடையது.</p><p>இந்தத் தொகைநூலில் காணும் பாடல்களின் எண்ணிக்கை பற்றிக் கருத்து வேறுபாடுகள் உள்ளன. மொத்தம் இருபத்தெட்டுப் பாடல்களாகக் காண்பதே இங்குப் பின்பற்றியுள்ள கொள்கை.</p><p>காதலுணர்வு புனிதமானது; இயற்கையின் அன்பளிப்பு; கடவுளின் கொடை; இதனை விவிலியம் ஏற்கிறது. இந்நூலில் கடவுளின் பெயர் ஒருமுறைகூட வரவில்லை. எனினும், இறை ஏவுதல் பெற்ற திருமறை நூல்களில் இந்நூலும் இடம்பெறுதல் வியப்பன்று. ஆண்-பெண் காதலுணர்வும் காதலுறவும் கடவுளின் அன்புக்கு ஓர் உயர்ந்த அடையாளம் என்னும் அளவில் இப்பாடல்கள் கடவுள் - இஸ்ரயேல், கிறிஸ்து - திருச்சபை, கடவுள் - ஆன்மா ஆகியோர்க்கிடையில் நிலவும் அன்புணர்வையும் அன்புறவையும் குறிக்கப் பயன்படுதல் மிகவும் பொருத்தமே என்க.</p>'),
(23, 'Isaiah', 'எசாயா', 'எசாயா', 'எசா', '', '<p>கி.மு. எட்டாம் நூற்றாண்டின் பிற்பகுதியில் எருசலேமில் வாழ்ந்த எசாயா இறைவனால் சிறப்பான முறையில் தெரிந்தெடுக்கப்பட்டு, கற்பிக்கப்பட்டு யூதா மக்களிடம் அனுப்பப்பட்டவர். இந்த மாபெரும் இறைவாக்கினரின் பெயரால் வழங்கப்பெறும் இந்நூலை மூன்று பகுதிகளாகப் பிரிக்கலாம்.</p><ol><li><strong>அதிகாரங்கள் 1 - 39:</strong> இக்காலத்தில் தென் நாடாம் யூதா, வலிமை மிக்க அண்டை நாடான அசீரியாவின் அச்சுறுத்தலுக்கு உள்ளாகியிருந்தது. ஆனால் யூதாவுக்கு வரவிருந்த அழிவுக்கு, அசீரியாவின் ஆற்றலைவிட, அந்த நாடு கடவுள் மீது நம்பிக்கை இழந்து அவருக்குப் பணியாது செய்த பாவங்களே உண்மையான காரணம் என்று எசாயா உணர்ந்தார். எனவே அவர் எழுச்சியூட்டும் சொற்களாலும் செயல்களாலும், அம்மக்களையும் அவர்கள் தலைவர்களையும் நேர்மையோடும் நீதியோடும் வாழுமாறு அழைத்தார். கடவுளுக்கு அவர்கள் செவிகொடாவிடில் இருளும் அழிவுமே காத்திருக்கின்றன என்று எச்சரித்தார். ஆயினும் தாவீதின் வழிமரபில் தோன்றவிருக்கும் ஓர் ஒப்பற்ற அரசர் மூலம், அனைத்துலகும் அமைதி பெறும் பொற்காலம் வரவிருந்ததையும் எசாயா முன்னறிவித்தார்.</li><li><strong> அதிகாரங்கள் 40 - 55:</strong> இப்பகுதி யூதாவின் மக்களுள் பலர் பாபிலோனில் அடிமைகளாக நசுக்கப்பட்டு நம்பிக்கை இழந்து இருந்த காலத்தைச் சார்ந்தது. கடவுள் தம் மக்களை விடுவித்து அவர்களது சொந்த வீடான எருசலேமில் புதுவாழ்வு வாழுமாறு அழைத்துச் செல்வார் என்று இறைவாக்கினர் முழக்கம் செய்தார். வரலாற்றின் ஆண்டவர் கடவுளே என்பதும் இஸ்ரயேலர் மூலமாக மாந்தர் அனைவரும் நற்செய்தி பெற்று, இறையாசி பெறுவர் என்பதும் இங்கு வலியுறுத்தப் பெறுகின்றன. இப்பகுதியில் காணப்பெறும் ‘ஆண்டவரின் ஊழியன்’ பற்றிய பாடல்கள் பழைய ஏற்பாட்டிலேயே மிகச்சிறந்த பகுதிகளாக ஏற்றுக்கொள்ளப்பட்டுள்ளன.</li><li><strong>அதிகாரங்கள் 56 - 66:</strong> இவற்றுள் பெரும்பாலானவை எருசலேமுக்குத் திரும்பி வந்திருந்த மக்களுக்கு உரைக்கப்பட்டவை. கடவுள் இஸ்ரயேலருக்குத் தாம் அளித்த வாக்குறுதியை நிறைவேற்றுவார் என்று இப்பகுதியில் வலியுறுத்தப்படுகின்றது; நேர்மை, நீதி, ஓய்வுநாள், பலி, மன்றாட்டு ஆகியவற்றின் சிறப்பும் எடுத்துரைக்கப்படுகின்றது. இயேசு பெருமான் தம் பணியின் தொடக்கத்தில் இந்நூலின் (61:1-2) சொற்களைக் கையாள்வது குறிப்பிடத்தக்கது.</li></ol><h2>நூலின் பகுதிகள்</h2><ol><li>எச்சரிக்கைகளும் வாக்குறுதிகளும் 1:1 - 12:6</li><li>வேற்றினத்தார்க்குரிய தண்டனைகள் 13:1 - 23:18</li><li>உலகின்மீது ஆண்டவரின் தண்டனைத் தீர்ப்பு 24:1 - 27:13</li><li>எச்சரிக்கைகளும் வாக்குறுதிகளும் 28:1 - 35:10</li><li>யூதாவின் அரசன் எசேக்கியாவும் அசீரியரும் 36:1 - 39:8</li><li>வாக்குறுதி, நம்பிக்கை உரைகள் 40:1 - 55:13</li><li>எச்சரிக்கைகளும் வாக்குறுதிகளும் 56:1 - 66:24</li></ol>'),
(24, 'Jeremiah', 'எரேமியா', 'எரேமியா', 'எரே', '', '<p>எரேமியா என்ற இறைவாக்கினர் கி.மு. 7-ஆம் நூற்றாண்டின் பிற்பகுதியிலும் 6-ஆம் நூற்றாண்டின் முற்பகுதியிலும் வாழ்ந்தார். இந்நெடிய பணிக்காலத்தில் இஸ்ரயேல் மக்களுக்கு நிகழவிருந்த தண்டனை பற்றி முன்னெச்சரிக்கை விடுத்தார். பாபிலோனிய மன்னனால் எருசலேமும் திருக்கோவிலும் அழிவுற்றதையும் யூதா அரசனும் நாட்டினரும் நாடுகடத்தப்பட்டதையும் தம் கண்ணால் கண்டார். ஆயினும் அம்மக்கள் பாபிலோனிய அடிமைத்தனத்தினின்று மீளவிருப்பதையும் நாடு புத்துயிர் பெறவிருப்பதையும் முன்னறிவித்தார்.</p><p>எரேமியா மென்மையான அன்புள்ளம் படைத்தவர். ஆயினும் மக்களுக்கெதிராகத் தண்டனைத் தீர்ப்பு உரைக்குமாறு இறைவனால் பணிக்கப்பட்டார். கடவுள் தந்த இவ்வழைப்பிற்காகத் ‘துன்புறும் மனிதன்’ ஆன இவரைப்பற்றிய உணர்ச்சிகரமான பல பகுதிகள் இந்நூலில் காணக்கிடக்கின்றன. இந்நூலின் சில சிறப்பான பகுதிகளில், ‘இதயத்தில் எழுதப்பட்ட சட்டத்தைக் கொண்ட புதியதோர் இறைக்குலம் தோன்றவிருக்கிறது’ என்னும் நம்பிக்கைப் பேரொளி சுடர்விடுகின்றது.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>எரேமியாவின் அழைப்பு 1:1 - 19</li><li>யூதா, எருசலேமுக்கு எதிரான இறைவாக்குகள் 2:1 - 25:38</li><li>நல்வாழ்வுபற்றிய இறைவாக்குகள் 26:1 - 35:19</li><li>எரேமியாவின் துன்பங்கள் 36:1 - 45:5</li><li>வேற்றினத்தார்க்கு எதிரான இறைவாக்குகள் 46:1 - 51:64</li><li>பிற்சேர்க்கை: எருசலேமின் வீழ்ச்சி 52:1 - 34</li></ol>'),
(25, 'Lamentations', 'புலம்பல்', 'புலம்பல்', 'புல', '', '<p>‘புலம்பல்’ என்னும் இத்திருநூல் ஐந்து எபிரேய அகர வரிசைக் கவிதைகளால் ஆனது. கி.மு. 586இல் எருசலேமுக்கு நேரிட்ட பேரழிவையும், அதன் தொடர் நிகழ்ச்சியான நாடுகடத்தப்படுதலையும் பற்றிய புலம்பலாக இந்நூல் அமைந்துள்ளது.</p><p>எரேமியாவின் காலச் சூழலைப் பின்னணியாகக் கொண்ட இந்நூலில், அவலச்சுவையே மேலோங்கி நிற்கின்றது. ஆயினும், கடவுளின் அருளினால் கிடைக்கவிருக்கும் ஒளிமயமான எதிர்காலம் பற்றிய நம்பிக்கையும் ஆங்காங்கே வெளிப்படுகின்றது. இக்கவிதைகள், மேற்குறிப்பிட்ட பேரழிவின் நினைவு நாளுக்கான நோன்பு வழிபாட்டில், யூதர்களால் ஆண்டுதோறும் பயன்படுத்தப்படுகின்றன.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>எருசலேமின் துன்பங்கள் 1:1 - 22</li><li>எருசலேமுக்குரிய தண்டனை 2:1 - 22</li><li>தண்டனைத் தீர்ப்பும் நம்பிக்கையும் 3:1 - 66</li><li>வீழ்ச்சியுற்ற எருசலேம் 4:1 - 22</li><li>இறைவனின் இரக்கத்திற்காக வேண்டல் 5:1 - 22</li></ol>'),
(26, 'Ezekiel', 'எசேக்கியேல்', 'எசேக்கியேல்', 'எசே', '', '<p>எசேக்கியேல் என்னும் பெயருக்கு ‘ஆண்டவர் ஆற்றல் அளிப்பார்’ என்பது பொருள். எருசலேமின் வீழ்ச்சிக்கு முன்பும் பாபிலோனியச் சிறையிருப்பின் போதும் இறைவாக்கினர் எசேக்கியேல் வாழ்ந்தார். பாபிலோனுக்கு நாடுகடத்தப்பட்டோருக்கு மட்டுமன்றி, எருசலேமில் எஞ்சியிருந்தோருக்கும் அவர் இறைவாக்கு உரைத்தார்.</p><p>எசேக்கியேல் ஆழ்ந்த இறைப்பற்றும் கற்பனை வளமும் கொண்டிருந்தார். எழுச்சிமிகு தம் எண்ணங்கள் பலவற்றைக் காட்சிகளின் வடிவில் எடுத்துரைத்தார். இவர் தம் அறிக்கைகள் பலவற்றை அடையாளச் செயல்கள் வழியாக விளக்கினார். ஒவ்வொருவரும் தம் தீவினைகளுக்குப் பொறுப்பு ஏற்க வேண்டும் என்றும், அவர்தம் நெஞ்சமும் எண்ணமும் உள்ளார்ந்த புதுப்பொலிவு பெறவேண்டும் என்றும் எசேக்கியேல் வலியுறுத்தினார்; நாடும் புதுப்பொலிவு பெற்று வாழ்ந்திட வேண்டும் என அறைகூவல் விடுத்தார். இவர் குருவாகவும் இறைவாக்கினராகவும் இருந்தமையால், கோவிலைக் குறித்தும் உள்ளத் தூய்மையைக் குறித்தும் மிகுந்த அக்கறை எடுத்துக் கொண்டார்.</p><h2>நூலின் பகுதிகள்</h2><ol><li>எசேக்கியேலின் அழைப்பு 1:1 - 3:27</li><li>எருசலேம் பற்றிய அழிவுச் செய்திகள் 4:1 - 24:27</li><li>மக்களினங்களுக்கு எதிரான கடவுளின் நீதித் தீர்ப்புகள் 25:1 - 32:32</li><li>கடவுள் தம் மக்களுக்கு அளித்த வாக்குறுதி 33:1 - 37:28</li><li>கோகுக்கு எதிரான இறைவாக்கு 38:1 - 39:29</li><li>வருங்காலக் கோவில் மற்றும் நாடு பற்றிய காட்சிகள் 40:1 - 48:35</li></ol>'),
(27, 'Daniel', 'தானியேல்', 'தானியேல்', 'தானி', '', '<p>‘தானியேல்’ என்னும் இந்நூல் யூதர்கள் வேற்றினத்து மன்னனால் ஒடுக்கப்பட்டுத் துன்புறுத்தப்பட்டபோது எழுதப்பெற்றது. கொடுங்கோல் மன்னனை வீழ்த்தி இறைவன் தம் மக்களை மீண்டும் முன்னிருந்தவாறே சிறப்புறச் செய்வார் என்பதை வற்புறுத்துமாறு இந்நூலில் எடுத்துக் காட்டுகளும் காட்சிகளும் கையாளப்பட்டுள்ளன.</p><p>இந்நூல் இரு பெரும் பிரிவுகளைக் கொண்டுள்ளது:</p><ol><li>தானியேலும் அவருடைய தோழர்களும் கடவுள்மீது அசையாத நம்பிக்கை கொண்டு, அவர்தம் கட்டளைகளுக்குப் பணிந்து நடந்ததால் தங்கள் எதிரிகளை மேற்கொண்டனர். இப்பகுதியில் காணப்படுபவை பாபிலோனிய, பாரசீகப் பேரரசுகளின் வரலாற்றுப் பின்னணியில் எழுதப்பட்டுள்ளன.</li><li>தானியேல் கண்ட காட்சிகள் பாபிலோனியப் பேரரசு தொடங்கி அடுத்துவரும் எல்லாப் பேரரசுகளின் எழுச்சியையும் வீழ்ச்சியையும் சுட்டிக்காட்டும் உருவகங்களாக அமைந்து, வேற்றினத்துக் கொடுங்கோலனின் வீழ்ச்சியையும் இறைமக்களின் வெற்றியையும் முன்னுரைக்கின்றன.</li></ol><p>இந்நூலின் ஒரு பகுதி (1:1-2:3; 8:1-12:13) எபிரேய மொழியிலும், மறு பகுதி (2:4-7:28) அரமேய மொழியிலும் எழுதப்பட்டுள்ளன. ‘அசரியாவின் மன்றாட்டு’, ‘மூவர் பாடல்’, ‘சூசன்னா’, ‘பேலும் பறவைநாகமும்’ ஆகிய நான்கு பகுதிகள் கிரேக்க மொழியில் மட்டும் காணப்படுகின்றன. அவை ‘தானியேல்: இணைப்புகள்’ என்னும் இணைத்திருமுறை நூலில் இடம்பெற்றுள்ளன.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>தானியேலும் தோழர்களும் 1:1 - 6:28</li><li>தானியேலின் காட்சிகள் 7:1 - 11:45<ul>அ) நான்கு விலங்குகள் 7:1 - 28</ul><ul>ஆ) செம்மறியும் வெள்ளாடும் 8:1 - 9:27</ul><ul>இ) வான தூதர் 10:1 - 11:45</ul></li><li>முடிவின் காலம் 12:1 - 13</li></ol>'),
(28, 'Hosea', 'ஒசேயா', 'ஒசேயா', 'ஓசே', '', '<p>இறைவாக்கினர் ஓசேயா வடநாடான இஸ்ரயேலில் ஆமோசுக்குச் சற்றுப் பின்னர் வாழ்ந்தவர்; சமாரியா வீழ்ச்சியுற்ற கி.மு. 722-க்கு முன் இறைவாக்கு உரைத்தவர்; இஸ்ரயேலரின் சிலைவழிபாட்டைக் கடிந்து கொண்டவர்; அவர்களது கீழ்ப்படியாமையைக் கண்டித்தவர். இவர் கோமேர் என்ற பெண்ணை மணந்து கொண்டார். அவள் அவருக்கு நம்பிக்கைத் துரோகம் செய்து, அவரை விட்டு விலகிச் சென்றாள். அத்தகையவளோடு அவர் கொண்டிருந்த மணஉறவைப் பின்னணியாகக் கொண்டு ஓசேயா இறைமக்களின் உண்மையற்ற தன்மையை, கீழ்ப்படியாமையை, நம்பிக்கைத் துரோகத்தை எடுத்தியம்பினார். கடவுளை விட்டு விலகிச் சென்ற அவர்களுக்கு இறைவன் தண்டனை வழங்குவார்; ஆயினும் இறைவனின் பேரன்பு இறுதிவரை நிலைத்திருக்கும்; அம்மக்களை அவர் பக்கம் ஈர்த்துக்கொள்ளும்; அதன்மூலம், முறிந்த உறவு மலரும். இதுவே இவரது நூலின் செய்தியாகும்.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>ஓசேயாவின் திருமணமும் இல்வாழ்வும் 1:1 - 3:5</li><li>இஸ்ரயேலின் குற்றங்களும் அவற்றுக்குரிய தண்டனைத் தீர்ப்பும் 4:1 - 13:16</li><li>மனமாற்றத்திற்கு அழைப்பும் வாக்குறுதியும் 14:1 - 9</li></ol>'),
(29, 'Joel', 'யோவேல்', 'யோவேல்', 'யோவே', '', '<p>யோவேல் இறைவாக்கினரைப் பற்றியும் அவரது பணி பற்றியும் மிகச் சிறிதே நமக்குத் தெரிய வருகின்றது. இந்நூல் கி.மு. ஐந்தாம் அல்லது நான்காம் நூற்றாண்டில் பாரசீகரின் ஆட்சிக் காலத்தில் எழுதப்பட்டிருக்க வேண்டும். பாலஸ்தீனத்தில் ஏற்பட்ட கொடும் வறட்சி, வெட்டுக்கிளிகள் விளைத்த அழிவு ஆகியவற்றைக் கடவுளின் நாளுக்கும் கடவுளின் நீதியை எதிர்ப்பவர்கள்மீது வரவிருந்த தண்டனைக்கும் முன்னடையாளங்களாக இறைவாக்கினர் கருதுகின்றார். மனமாற்றத்திற்குக் கடவுளின் அழைப்பு, நல்வாழ்வு அளிப்பதாக ஆண்டவர் கூறும் உறுதிமொழி, கடவுளின் ஆசி, ஆண் பெண் இளைஞர் முதியோர் என்ற வேறுபாடு இன்றி அனைவர்மீதும் கடவுள் ஆவியைப் பொழிந்தருளுவார் என்ற வாக்குறுதி ஆகியவை பற்றி இந்நூல் கூறுகிறது.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>வெட்டுக்கிளிகள் விளைத்த அழிவு 1:1 - 2:17</li><li>மீட்பைப்பற்றிய வாக்குறுதி 2:18 - 2:27</li><li>ஆண்டவரின் நாள் 2:28 - 3:21</li></ol>'),
(30, 'Amos', 'ஆமோஸ்', 'ஆமோஸ்', 'ஆமோ', '', '<p>விவிலியத்தில் இடம்பெறும் இறைவாக்குகளுள், ஆமோஸ் உரைத்த தூதுரையே முதலில் எழுத்து வடிவம் பெற்றதாகும். ஆமோஸ் தென்னாடான யூதாவில் பிறந்தவராயினும் வடநாடான இஸ்ரயேலுக்குச் சென்று கி.மு. 8-ஆம் நூற்றாண்டின் மையப்பகுதியில் இறைவாக்கு உரைத்தார். அந்நாளில் அந்நாடு சீரும் சிறப்புமாய் இருந்தது. ஆனால் வளமும் வாழ்வும் செல்வருக்கும் வலியோருக்கும் மட்டுமே; ஏழை எளியவர்கள் நசுக்கப்பட்டுத் தாழ்வுற்றுக் கிடந்தனர்.</p><p>வலியோரை வாழ்த்தி எளியோரை வாட்டும் அநீதியும் பொய்ம்மையும் நிறைந்த சமுதாயத்தைக் கண்டு சீறுகிறார் ஆமோஸ். இனம் இனத்தையும் மனிதர் மனிதரையும் கசக்கிப் பிழியும் கொடுமையைக் கடுமையாய்க் கண்டிக்கிறார். நீதியையும் நேர்மையையும் வளர்த்துக் கொண்டாலன்றி, எந்த இனமும் கடவுளின் கடும் தண்டனைக்கு உள்ளாகும் என்று எச்சரிக்கை விடுக்கின்றார்.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>வேற்றினத்தார்மீது இறைவனின் தீர்ப்பு 1:1 - 2:5</li><li>இஸ்ரயேல் மீது இறைவனின் தீர்ப்பு 2:6 - 6:14</li><li>ஐந்து காட்சிகள் 7:1 - 9:15</li></ol>'),
(31, 'Obadiah', 'ஒபதியா', 'ஒபதியா', 'ஒப', '', '<p>இறைவாக்கு நூல்களிலேயே மிகச் சிறியதான இந்நூல் கி.மு.586இல் எருசலேம் நகர் வீழ்ச்சியடைந்ததற்குச் சற்றுப் பின்னர் தோன்றியதாகும். எருசலேமின் வீழ்ச்சியைக் கண்டு யூதாவின் பழம்பெரும் எதிரியான ஏதோம் நாடு அக்களித்தது. அத்தோடு நில்லாமல் அது யூதாவில் புகுந்து கொள்ளையடித்து, பிற எதிரிகளும் அதனுள் நுழையத் துணை நின்றது. எனவே இஸ்ரயேலின் எதிரிகளான மற்றெல்லா இனத்தோடும் ஏதோம் நாடும் தண்டிக்கப்பட்டுத் தோற்கடிக்கப்படும் என்று ஒபதியா முன்னுரைக்கிறார்.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>ஏதோமிற்கு வரும் தண்டனைத் தீர்ப்பு 1 - 14</li><li>ஆண்டவரின் நாள் 15 - 21</li></ol>'),
(32, 'Jonah', 'யோனா', 'யோனா', 'யோனா', '', '<p>நினிவே மாநகர் மக்கள் நெறிகெட்டவராய் வாழ்ந்து, பாவச் சேற்றில் உழன்று கொண்டிருந்தனர். ஆகவே, அந்நகருக்கு அழிவு வரப்போகிறது என்று அறிவிக்க, கடவுள் யோனாவை அனுப்பினார்.</p><p>கடவுளின் கருணைக்கு அளவும் இல்லை; எல்லையும் இல்லை. கனிவு காட்டுவதில் நல்லார் பொல்லார் என்று அவர் வேற்றுமை பாராட்டுவதும் இல்லை. அவரது வாக்கைக் கேட்டு மனம்மாறும் அனைவர்க்கும் அவர் மன்னிப்பு அருள்கின்றார். அடித்தலைவிட அணைத்தலே அவரது இயல்பாகும். பாவிகளின் அழிவையல்ல, அவர்களது மனமாற்றத்தையே அவர் விரும்புகின்றார்.</p><p>நினிவே மக்களும் யோனா அறிவித்ததைக் கேட்டு மனம் மாறிக் கடவுளின் மன்னிப்பைப் பெற்றனர். ஆனால் அதைக் கண்டு மனம்பொறாத யோனா சினங்கொண்டார். கடவுளோ அவருக்கும் கருணை காட்டித் தம் இயல்பை வெளிப்படுத்தினார். இக்கருத்துகளை இந்நூல் நயம்பட எடுத்துக் காட்டுகின்றது.</p><p>இயேசு கிறிஸ்து தம்முடைய பணியையும் உயிர்ப்பையும் பற்றிப் பேசுகையில், யோனாவை அடையாளமாகச் சுட்டிக் காட்டியது இங்கே குறிப்பிடத்தக்கது.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>யோனாவின் அழைப்பும் கீழ்ப்படியாமையும் 1:1 - 2:1</li><li>யோனாவின் மன்றாட்டு 2:2 - 11</li><li>நினிவேயில் யோனா 3:1 - 10</li><li>யோனாவின் சினமும் கடவுளின் இரக்கமும் 4:1 - 11</li></ol>'),
(33, 'Micah', 'மீக்கா', 'மீக்கா', 'மீக்', '', '<p>மீக்கா இறைவாக்கினர் யூதாவில் உள்ள நாட்டுப்புற நகர் ஒன்றில் தோன்றியவர். ஓசேயா, எசாயா ஆகியோரின் காலத்தவர்.</p><p>இஸ்ரயேல் மக்கள் நேர்மையற்று நடந்தனர்; அநீதிக்குத் தலை வணங்கினர்; தீச்செயல்கள் பல புரிந்தனர்; ஏழைகளை ஏமாற்றினர்; அனாதைகளை நசுக்கினர். தென்னாட்டினரான யூதா மக்களும் இதுபோன்றே வாழ்ந்து வந்தனர். எனவே இஸ்ரயேல் மக்களுக்கு ஆமோஸ் இறைவாக்கினர் முன்னறிவித்த தண்டனைத் தீர்ப்பு தம் நாட்டினர் மீதும் வரும் என்று மீக்காவும் முன்னறிவித்தார். அதே நேரத்தில் மீட்புப் பற்றியும் முன்னறிவித்தார்.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>இஸ்ரயேல்மீது தண்டனைத் தீர்ப்பு 1:1 - 3:12</li><li>மீட்பும் அமைதியும் 4:1 - 5:15</li><li>எச்சரிக்கையும் வருங்கால நம்பிக்கையும் 6:1 - 7:20</li></ol>'),
(34, 'Nahum', 'நாகூம்', 'நாகூம்', 'நாகூ', '', '<p>இஸ்ரயேலின் மிகப் பழைய, கொடிய எதிரியான அசீரியருடைய தலைநகராம் நினிவே பெருநகரின் வீழ்ச்சியைக் குறித்து மகிழ்ந்து பாடும் கவிதையாக “நாகூம்” என்னும் இந்நூல் அமைந்துள்ளது. கி.மு. ஏழாம் நூற்றாண்டின் இறுதியில் நினிவே அழிவுற்றது. ஆணவம் கொண்டு மற்ற மக்களைக் கொடுமைப்படுத்தும் எந்த நாட்டையும் ஆண்டவர் தண்டிக்காமல் விடமாட்டார் என்பதை இந்நூல் விளக்குகிறது.</p><h2>நூலின் பிரிவுகள் </h2><ol><li>நினிவேயின் மீது ஆண்டவரின் தீர்ப்பு 1:1 - 15 </li><li>நினிவேயின் வீழ்ச்சி 2:1 - 3:19</ol>'),
(35, 'Habakkuk', 'அபக்கூக்கு', 'அபக்கூக்கு', 'அப', '', '<p>இறைவாக்கினர் அபக்கூக்கு கி.மு. ஏழாம் நூற்றாண்டின் இறுதியில், கல்தேயர் இனத்தாரான பாபிலோனியரின் ஆட்சிக் காலத்தில் வாழ்ந்திருக்கலாம்.</p><p>பாபிலோனியர் கொடுமை செய்வதையும் கொள்ளையடிப்பதையும் கண்டு மனம் வெதும்பி ஆண்டவரை நோக்கி “பொல்லாதவர்கள் நேர்மையானவர்களை விழுங்கும்போது நீர் ஏன் மௌனமாய் இருக்கிறீர்?” என்று வினவிய அபக்கூக்கிற்கு, தாம் குறித்த காலத்தில் தண்டனைத் தீர்ப்பை நிறைவேற்றுவதாகவும், அதுவரை நேர்மையுடையோர் கடவுள் மீது கொண்ட நம்பிக்கையினால் வாழ்வார்கள் என்றும் ஆண்டவர் மறுமொழி கூறினார்.</p><p>இந்நூலின் பிற்பகுதி நேர்மையற்றோர், கொடியோர் ஆகியவர்களுக்கு ஆண்டவர் வழங்கும் தண்டனைத் தீர்ப்பைப்பற்றிக் கூறுகிறது. இறுதியில் அமைந்துள்ள பாடல் இறைவனின் மாட்சியையும் புகழையும் எடுத்துரைக்கிறது.</p><h2>நூலின் பிரிவுகள் </h2><ol><li>அபக்கூக்கின் குற்றச்சாட்டுகளும் ஆண்டவரின் மறுமொழியும் 1:1 - 2:4</li><li>நேர்மையற்றோர் மேல் வரும் தண்டனைத் தீர்ப்பு 2:5 - 20</li><li>அபக்கூக்கின் மன்றாட்டு 3:1 - 19</ol>'),
(36, 'Zephaniah', 'செப்பனியா', 'செப்பனியா', 'செப்', '', '<p>கி.மு. ஏழாம் நூற்றாண்டின் பிற்பகுதியில் செப்பனியா இறைவாக்கு உரைத்தார். அவர் இறைவாக்கு உரைத்த காலம் யோசியா அரசன் கி.மு. 621இல் செயல்படுத்திய சமயச் சீர்திருத்தத்திற்கு முன்னைய பத்தாண்டாக இருக்கலாம்.</p><p>ஏனைய இறைவாக்கு நூல்களில் மிகுதியாகக் காணப்படும் கருத்துகளையே இந்நூலும் கொண்டுள்ளது: அழிவின் நாள் நெருங்கிவிட்டது; அப்பொழுது யூதா வேற்றுத் தெய்வங்களை வழிபட்டதற்காகத் தண்டிக்கப்படும்; எருசலேம் அழிவுற்றாலும் மீண்டும் ஒரு நாள் முன்னைய நன்னிலைக்கு உயர்த்தப்படும்; பணிவும் நேர்மையும் மிக்க மக்கள் அங்கு மீண்டும் வாழ்வார்கள்.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>ஆண்டவர் தீர்ப்பு வழங்கும் நாள் 1:1 - 2:3</li><li>வேற்றினத்தாரின் அழிவு 2:4 - 15</li><li>எருசலேமின் அழிவும் மீட்பும் 3:1 - 20</li></ol>'),
(37, 'Haggai', 'ஆகாய்', 'ஆகாய்', 'ஆகா', '', '<p>கி.மு. 520-இல் ஆகாய் இறைவாக்கினர் மூலம் ஆண்டவர் அருளிய இறைவாக்குகளின் தொகுப்பாக இத்திருநூல் அமைந்துள்ளது. பாபிலோனிய அடிமைத்தனத்தினின்று இஸ்ரயேலர் எருசலேமுக்கு திரும்பி, சில ஆண்டுகள் ஆகிவிட்டன. ஆயினும் ஆண்டவரது கோவில் இன்னும் கட்டியெழுப்பப்படவில்லை. அதை விரைவில் மீண்டும் கட்டியெழுப்புமாறு இந்த இறைவாக்குகள் தூண்டுகின்றன. தூய்மையாக்கப்பட்டுப் புத்துயிர் பெற்றுள்ள மக்களுக்குச் செழுமையையும் அமைதியையும் அருள்வதாக ஆண்டவர் வாக்களிக்கிறார்.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>கோவிலை மீண்டும் கட்டியெழுப்புமாறு ஆண்டவரின் கட்டளை 1:1 - 15</li><li>ஆறுதலும் நம்பிக்கையும் அளிக்கும் இறைவாக்குகள் 2:1 - 23</li></ol>'),
(38, 'Zechariah', 'செக்கரியா', 'செக்கரியா', 'செக்', '', '<p>செக்கரியா நூலை இருபெரும் பகுதிகளாகப் பிரிக்கலாம். முதற் பகுதி: 1-8 அதிகாரங்கள். இப்பகுதி கி.மு. 520 முதல் 518 வரையுள்ள காலத்தைச் சார்ந்தது. இதில் எட்டுக் காட்சிகள் அடங்கியுள்ளன. எருசலேமின் மீட்பு, கோவில் மீண்டும் கட்டியெழுப்பப்படும் என்ற வாக்குறுதி, இறைமக்கள் தூய்மைப்படுத்தப்படுவர் என்ற அறிவிப்பு, மெசியாவின் வருங்கால ஆட்சி ஆகியன சிறப்பாக எடுத்துரைக்கப்படுகின்றன. இரண்டாம் பகுதி: 9-14 அதிகாரங்கள். இப்பகுதியில் அடங்கியுள்ள குறிப்புகள் அனைத்தும் பிற்காலத்தைச் சேர்ந்தவை. இப்பகுதி மெசியாவைப் பற்றியும் இறுதித் தீர்ப்பைப் பற்றியும் எடுத்துரைக்கின்றது (9:9).</p><h2>நூலின் பிரிவுகள் </h2><ol><li>எச்சரிப்பும் நல்லன் குறித்த அறிவிப்பும் 1:1 - 8:23</li><li>வேற்றினத்தாருக்கு வரும் தண்டனைத் தீர்ப்பு 9:1 - 8</li><li>வருங்கால வாழ்வும் செழுமையும் 9:9 - 14:21</ol>'),
(39, 'Malachi', 'மலாக்கி', 'மலாக்கி', 'மலா', '', '<p>மலாக்கி என்னும் இறைவாக்கு நூல் கி.மு. ஐந்தாம் நூற்றாண்டில் எருசலேம் கோவில் மீண்டும் கட்டியெழுப்பப்பட்டதற்குப் பின் தோன்றியது. குருக்களும் மக்களும் சமயக் கடமைகளில் தவறினர்; அவர்கள் ஆண்டவருக்குச் சேர வேண்டிய காணிக்கையை முறைப்படி செலுத்தவில்லை; அவர்தம் கட்டளைகளைக் கடைப்பிடிக்காது, அவரை அவமதித்தனர்; அவரது திருப்பெயரைக் களங்கப்படுத்தினர். எனவே ஆண்டவர் தம் மக்களுக்குத் தண்டனை வழங்கவும் அவர்களைத் தூய்மைப்படுத்தவும் வருவார்; அவரது வருகைக்கு முன் அவரது வழியை ஆயத்தம் செய்யவும் அவரது உடன்படிக்கை பற்றி எடுத்துரைக்கவும் தம் தூதரை அனுப்புவார் என்பதே இந்நூலின் செய்தியாகும்.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>இஸ்ரயேலரின் குற்றங்கள் 1:1 - 2:16</li><li>கடவுளின் தண்டனைத் தீர்ப்பும் இரக்கமும் 2:17 - 3:23</li></ol>'),
(40, 'Tobit', 'தோபித்து', 'தோபித்து', 'தோபி', 'தொபியாசு ஆகமம்', '<p>யூதர்கள் கி.மு. 721-இல் அசீரியாவுக்கு நாடுகடத்தப்பட்டு அடிமைகளாக வாழ்ந்ததைப் பின்னணியாகக் கொண்டு யூதக் குடும்ப வாழ்வை விளக்கும் இக்கதை கி.மு. 2-ஆம் நூற்றாண்டில் எழுதப்பட்டிருக்கலாம். இந்நூலின் ஆசிரியர் யார் என்பது தெரியவில்லை. இது, யூத போதகர்கள் கையாண்டுவந்த விவிலிய விளக்கமுறையான ‘மித்ராஷ்’ என்னும் இலக்கிய வகையைச் சேர்ந்தது என்பர் அறிஞர்; ஞான இலக்கியத்தைச் சேர்ந்ததாகக் கருதுவாரும் உளர்.</p><p>இந்நூல் முதலில் அரமேய மொழியில் எழுதப்பட்டிருக்கவேண்டும். அது முழுமையாக நமக்குக் கிடைக்காததால், அதன் கிரேக்க மொழிபெயர்ப்பே மூலபாடமாக இன்று பயன்படுகிறது. அண்மைக் காலம்வரை வத்திக்கான், அலக்சாந்திரியத் தோற்சுவடிகளினின்றே ஏறத்தாழ எல்லா மொழிபெயர்ப்புகளும் செய்யப்பட்டு வந்தன. இந்தச் சுவடிகளைவிடச் சீனாய்ச் சுவடி தொன்மை வாய்ந்ததால், இதுவே இம்மொழிபெயர்ப்புக்கு மூலபாடமாக அமைகிறது. சீனாய்ச் சுவடி முன்னையவற்றைவிடச் சற்று விரிவானது.</p><p>பல்வேறு துன்பங்களிடையிலும் தம்மீது பற்றுறுதி கொண்டு வாழ்வோர்க்குக் கடவுள் கைம்மாறு அளித்து அவர்களைக் காப்பார் என்பது இந்நூலின் மையச் செய்தியாகும்.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>முகவுரை 1:1 - 2</li><li>தோபித்துக்கு நேர்ந்த சோதனைகள் 1:3 - 3:15</li><li>தோபித்து பெற்ற கைம்மாறு 4:1 - 11:18</li><li>தோபித்தின் புகழ்ப்பாவும் அறிவுரையும் 12:1 - 14:11</li><li>முடிவுரை 14:12 - 15</li></ol>'),
(41, 'Judith', 'யூதித்து', 'யூதித்து', 'யூதி', '', '<p>செலூக்கியர் ஆட்சியின்போது யூதர்கள் அனுபவித்த துயரத்தின் வரலாற்றையும், மக்கபேயர் வழியாகக் கடவுள் அவர்களுக்கு அளித்த முழுவிடுதலையையும் பின்னணியாகக் கொண்ட இந்நூல் ஒரு புதினம். இது கி.மு. இரண்டாம் நூற்றாண்டின் இறுதியில் அல்லது முதல் நூற்றாண்டின் இடைப்பகுதியில் இயற்றப்பட்டிருக்கலாம்.</p><p>இந்நூலின் ஆசிரியர் யார் என்பது தெளிவாகத் தெரியவில்லை. ஆனால், அவர், பாலஸ்தீனாவில் பரிசேயரின் வழிமரபில் தோன்றிய ஒரு யூதர் என்பதில் ஐயமில்லை. இது எபிரேய மொழியில் எழுதப்பட்டிருக்கவேண்டும்; மூல நூல் கிடைக்காமையால், அதன் கிரேக்க மொழிபெயர்ப்பே இன்று நமக்கு மூலபாடமாக இருந்து வருகிறது.</p><p>ஒருவர் கடவுள் மீது பற்றுறுதி கொண்டு செயல்பட்டால், எத்துணை வலிமை படைத்த உலக ஆற்றல்களையும் வென்றுவிடலாம் என்பது இந்நூலின் மையக் கருத்தாகும். இக்கருத்தை யூதர்கள், என்றும் தங்கள் நினைவில் நிறுத்தும் பொருட்டு, கோவில் அர்ப்பணிப்பின் ஆண்டு விழாவின்போது இந்நூல் பொதுவில் படிக்கப்பட்டது.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>யூதர்களுக்கு நேரிட்ட பேரிடர் 1:1 - 7:32</li><li>யூதித்து வழியாகக் கிடைத்த வெற்றி 8:1 - 16:25</li></ol>'),
(42, 'Esther (Gr)', 'எஸ்தர் (கிரேக்கம்)', 'எஸ்தர் (கி)', 'எஸ் (கி)', '', '<p>பாரசீகர்களின் ஆட்சியில் (கி.மு. 538-333) யூதர்கள் ஓரளவு உரிமையுடன் வாழ்ந்து, சில சலுகைகள் பெற்றிருந்தார்கள். இதைப் பின்னணியாகக் கொண்டு இயற்றப்பட்டதே ‘எஸ்தர்’ என்னும் இந்நூல்.</p><p>இது எபிரேய மொழியில் கி.மு. ஐந்தாம் நூற்றாண்டில் எழுதப்பட்டிருக்கலாம். சிறு சிறு நீக்கங்கள், சுருக்கங்களைத் தவிர, இதன் கிரேக்க பாடம் ஆறு பெரும் இணைப்புகளைக் கொண்டுள்ளது:</p><ol><li>மொர்தெக்காயின் கனவும் மன்னருக்கு எதிரான சூழ்ச்சி வெளிப்படுதலும் (1:1a-1r);</li><li>யூதர்களைக் கொன்றொழிப்பதற்கான அரசாணை (3:13a-13g);</li><li>மொர்தெக்காய், எஸ்தர் ஆகியோரின் மன்றாட்டு (4:17a-17z);</li><li>எஸ்தர் மன்னரிடம் வேண்டுகோள் விடுத்தல் (5:1-2b);</li><li>யூதர்களுக்குச் சலுகைகள் வழங்கும் அரசாணை (8:12a-12x);</li><li>மொர்தெக்காயின் கனவு நனவாதல் (10:3a-3b).</li></ol><p>இவை கி.மு. இரண்டாம் நூற்றாண்டில் எபிரேயப் பாடத்துடன் சேர்க்கப்பட்டிருக்க வேண்டும். இந்தப் பதிப்பில் சிறப்புப் பெயர்களும் வசன எண் வரிசையும் கிரேக்க பாடத்தையொட்டி அமைந்துள்ளன.</p><p>எபிரேய பாடம் விளக்கும் நிகழ்ச்சிகளின் போக்கில் எவ்வகை மாற்றத்தையும் கிரேக்க இணைப்புகள் தோற்றுவிக்கவில்லை; எனினும் கடவுளைப் பற்றிய வெளிப்படையான குறிப்புகளை நூல் முழுவதும் புகுத்துவதன்மூலம், கடவுளின் பாதுகாப்பு எப்போதும் அவருடைய மக்களான இஸ்ரயேலருக்கு உண்டு என்னும் உண்மையைக் கோடிட்டுக் காட்டுகின்றன.</p><p>பாரசீகர்களிடமிருந்து யூதர் விடுதலை பெற்றதன் நினைவாகக் கொண்டாடப்பட்ட ‘பூரிம்’ திருவிழாவின் போது இந்நூல் பொதுவில் படிக்கப்பட்டது.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>முகவுரை 1:1a - 1r</li><li>எஸ்தரின் உயர்வு 1:1s - 2:18</li><li>யூதர்களுக்கு எதிரான சூழ்ச்சி 2:19 - 5:14</li><li>யூதர்களின் வெற்றி 6:1 - 9:32</li><li>முடிவுரை 10:1 - 3l</li></ol>');
INSERT INTO `t_bookkey` (`bn`, `en`, `tn_f`, `tn_s`, `tn_a`, `tn_o`, `intro`) VALUES
(43, 'Wisdom', 'சாலமோனின் ஞானம்', 'சாலமோனின் ஞானம்', 'சாஞா', 'ஞானாகமம்', '<p>‘சாலமோனின் ஞானம்’ என்னும் இந்நூல் சாலமோனைப் பற்றிய சில மறைமுகக் குறிப்புகளைக் கொண்டிருந்தாலும் (காண் 8:9-15; 9:7-8,12), காலத்தாலும் கருத்தாலும் பிந்தியது என்பது உறுதி. பாலஸ்தீனத்துக்கு வெளியே, எகிப்து நாட்டு அலக்சாந்திரியாவில் வாழ்ந்துவந்த ஒரு யூதரால் கி.மு. முதல் நூற்றாண்டின் நடுவில் இது கிரேக்க மொழியில் எழுதப்பட்டிருக்க வேண்டும்; இதுவே பழைய ஏற்பாட்டு நூல்களுள் இறுதியாக எழுத்து வடிவம் பெற்றிருக்க வேண்டும் என அறிஞர்கள் கருதுகிறார்கள்.</p><p>பாலஸ்தீனத்துக்கு வெளியே வாழ்ந்துவந்த யூதர்களுள் சிலர், கிரேக்க மொழி, மெய்யியல், பண்பாடு, வழிபாட்டு முறை, வாழ்க்கை முறை முதலியவற்றின் மீது அளவற்ற நாட்டம் கொண்டதோடு, யூத மறையைவிடக் கிரேக்கர்களின் மறைவான சமயச் சடங்குகள் உயர்ந்தவை என்னும் தவறான எண்ணத்தால் தூண்டப்பெற்று யூத மறையைக் கைவிட்டனர். இவர்கள் யூத மறைக்கு மனம் மாறி வர அழைப்பு விடுப்பதே இந்நூலாசிரியரின் முதன்மை நோக்கம். அதே நேரத்தில், யூதக் கோட்பாடுகளில் பிடிப்போடு இருந்தவர்களை ஊக்கப்படுத்தி உறுதிப்படுத்துகிறார் ஆசிரியர்; விசுவாசத்தில் தளர்ந்து தடுமாற்ற நிலையில் இருந்தவர்களை எச்சரிக்கிறார்; கிரேக்கருடைய சிலைவழிபாட்டின் மூடத்தனத்தை அடையாளம் காட்டுவதோடு, ஆண்டவர்மீது அச்சம் கொள்வதே - அவரது திருச்சட்டத்தின்படி ஒழுகுவதே - உண்மையான, உயரிய ஞானம் என்று கோடிட்டுக் காட்டி, யூத மறையைத் தழுவுமாறு வேற்றினத்தாரைத் தூண்டுகிறார். கிரேக்கருக்கும் கிரேக்கச் சூழலில் வாழ்ந்துவந்த யூதருக்கும் யூதநெறிக் கோட்பாடுகளை விளக்க எழுந்த இந்நூலில் கிரேக்க மெய்யியல், நடை, சொல்லாட்சி முதலியன மிகுதியாகக் காணப்படுகின்றன.</p><p>முழு நூலும் இனிய கவிதை நடையில் அமைந்துள்ளது. நூலின் இறுதிப் பகுதி (அதி 10-19) யூதப் போதகர்கள் கையாண்டுவந்த விவிலிய விளக்க முறையான ‘மித்ராஷ்’ என்னும் இலக்கியவகைக்குச் சிறந்த எடுத்துக்காட்டு.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>ஞானமும் மனிதரின் முடிவும் 1 - 5</li><li>ஞானத்தின் தோற்றம், இயல்பு, அதை அடையும் வழி 6 - 9</li><li>மீட்பு வரலாற்றில் ஞானம் 10 - 19</li></ol>'),
(44, 'Sirach', 'சீராக்கின் ஞானம்', 'சீராக்', 'சீஞா', 'சீராக் ஆகமம்', '<p>செலூக்கியர் ஆட்சியின்போது கிரேக்க மொழி, பண்பாடு, வழிபாட்டுமுறை முதலியன யூதர்கள்மீது திணிக்கப்பட்டன. யூதர் பலரும் இவற்றை விரும்பி ஏற்கத் தொடங்கினர். இக்கட்டத்தில் (ஏறத்தாழ கி.மு. 180) சீராக்கின் மகனும் எருசலேமில் வாழ்ந்த மறைநூலறிஞருமான ஏசு, தம்மவரை யூத மறையில் உறுதிப்படுத்தி ஊக்குவிக்க எண்ணினார். உண்மையான ஞானம் இஸ்ரயேலில்தான் உள்ளது; திருச்சட்டத்தைக் கடைப்பிடிப்பதில்தான் அது அடங்கும் என்பதை வலியுறுத்தி இந்நூலை எழுதினார். எனவே இந்நூல் எபிரேயத்தில் ‘சீராவின் மகனான ஏசுவின் ஞானம்’ அல்லது ‘பென் சீரா’ என வழங்குகிறது.</p><p>எபிரேய மொழியில் எழுதப்பெற்ற இந்நூலை, பாலஸ்தீனத்துக்கு வெளியே கிரேக்கச் சூழலில் வாழ்ந்த யூதர்களின் நலன் கருதி, ஏசுவின் பேரன் (ஏறத்தாழ கி.மு. 132) கிரேக்கத்தில் மொழிபெயர்த்து, அதற்கு ஒரு முன்னுரையும் வரைந்தார். தொடக்கத் திருச்சபையில் ‘திருப்பாடல்கள்’ நூலுக்கு அடுத்தபடி இந்நூல் திருவழிபாட்டிலும் மறைக்கல்வியிலும் மிகுதியாகப் பயன்பட்ட காரணத்தால், இது ‘சபை நூல்’ என்றும் பெயர் பெற்றது.</p><p>இந்நூலின் எபிரேய பாடம் முழுதும் தொலைந்துவிட, இதன் மொழிபெயர்ப்பான கிரேக்க பாடமே நமக்கு மூலபாடமாகப் பயன்பட்டுவருகிறது. எனினும் எபிரேய பாடத்தின் பெரும் பகுதி தொல்லியல் ஆராய்ச்சியின் பயனாக இன்று நமக்குக் கிடைத்துள்ளதால், கிரேக்க பாடத்தை நன்குபுரிந்து கொள்ள இது பெரிதும் துணை புரிகிறது.</p><p>ஞானம்பற்றிய கருத்துக் குவியலைக் கொண்ட முதல் பகுதி, அன்றாட வாழ்வில் ஞானத்தைக் கடைப்பிடிக்கும் முறைபற்றிப் பேசுகிறது. இரண்டாவது பகுதி இஸ்ரயேலின் மீட்பு வரலாற்றில் இடம்பெற்ற தலைவர்களைப் புகழ்வதோடு, அவர்களைப் பின்பற்ற அழைப்பு விடுக்கிறது.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>முகவுரை 1 - 35 வரிகள்</li><li>ஞானம் வழங்கும் நன்னெறி 1:1 - 43:33</li><li>மூதாதையர் புகழ்ச்சி 44:1 - 50:29</li><li>பிற்சேர்க்கை 51:1 - 30</li></ol>'),
(45, 'Baruch', 'பாரூக்கு (எரேமியாவின் மடல்)', 'பாரூக்கு', 'பாரூ', '', '<p>இறைவாக்கினர் எரேமியாவின் செயலரான பாரூக்கு இந்நூலை எழுதினார் என்பது மரபுவழிச் செய்தி. வெவ்வேறு காலக்கட்டங்களில் எழுதப்பட்ட ஐந்து சிறிய தனித்தனிப் பகுதிகள் முறையாகத் தொகுக்கப்பட்டு, கி.மு. முதல் நூற்றாண்டில் தனி நூலாகப் பாரூக்கின் பெயரில் வெளியிடப்பட்டன என்பர் அறிஞர். நூலின் மையப் பகுதி (3:9-5:9) கவிதை நடையில் அமைந்துள்ளது.</p><p>கடவுளையும் அவரது திருச்சட்டத்தையும் கைவிட்டமையே இஸ்ரயேலர் பாபிலோனுக்கு நாடுகடத்தப்பட்டதற்குரிய காரணம்; எனவே அவர்கள் தங்கள் பாவங்களை அறிக்கையிட்டுக் கடவுள்பால் மனந்திரும்பி (1:1-3:8), உண்மை ஞானமாகிய திருச்சட்டத்தைக் கடைப்பிடித்து நடந்தால் (3:9-4:4), கடவுள் அவர்களது அடிமைத்தனத்தை விரைவில் முடிவுக்குக் கொண்டுவந்து, அவர்களுக்கு மீட்பை அருள்வார் (4:5-5:9) என்னும் செய்தியை இந்நூல் வலியுறுத்துகிறது.</p><p>எரேமியாவின் மடல் என அழைக்கப்படும் இறுதிப் பகுதி (6:1-72; காண் 2 மக் 2:1-3) பிற இனத்தார் நடுவே பழக்கத்தில் இருந்த சிலைவழிபாட்டை வன்மையாகக் கண்டிப்பதன்மூலம், முழு முதற் கடவுள்மீது பற்றுறுதி கொள்ளுமாறு இஸ்ரயேலரைத் தூண்டுகிறது (காண் எரே 10:1-16; எசா 44:6-20). கிரேக்க மூலத்தில் பாரூக்கு 5ஆம் அதிகாரத்தைத் தொடர்ந்து ‘புலம்பல்’ நூல் இடம்பெற, அதன் பின்னரே எரேமியாவின் மடல் காணப்படுகிறது. இருப்பினும் ‘உல்காத்தா’ எனப்படும் இலத்தின் பாடத்தைப் பின்பற்றிக் கத்தோலிக்க மரபு இம்மடலைப் பாரூக்கு 6ஆம் அதிகாரமாகப் பார்க்கிறது.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>முகவுரை 1:1 - 9</li><li>எருசலேமுக்கு விடுக்கப்பட்ட மடல் 1:10 - 3:8</li><li>ஞானத்தின் புகழ்ச்சி 3:9 - 4:4</li><li>எருசலேமின் புலம்பலும் நம்பிக்கையும் 4:5 - 5:9</li><li>எரேமியாவின் மடல் 6:1 - 72</li></ol>'),
(46, 'Daniel (Appendices)', 'தானியேல்: இணைப்புகள்', 'தானியேல் (இ)', 'தானி (இ)', '', '<p>தானியேல் என்னும் நூல் விவிலியத்தின் கிரேக்கத் திருமுறையில் மூன்று பெரும் இணைப்புகளைக் கொண்டுள்ளது:</p><ol><li><strong>இளைஞர் மூவரின் பாடல்: </strong>பாபிலோனிய மன்னன் நெபுகத்னேசர் நிறுவிய பொற்சிலையை வணங்க மறுத்ததற்காக அனனியா, மிசாவேல், அசரியா என்ற மூன்று இளைஞர்கள் சூளையில் எறியப்பட்டார்கள். இத்தகைய தீங்குகளினின்று தங்களையும் தங்கள் மக்கள் இஸ்ரயேலரையும் விடுவிக்குமாறு ஆண்டவரிடம் அசரியா மன்றாட (1 - 22), அவரும் அவர்களைப் பாதுகாத்தார் (23 -27). பின் அம்மூவரும் சேர்ந்து ஆண்டவருக்குப் புகழ்ச்சிப் பண் இசைத்தனர் (28-67).<br /><br /> இப்பகுதி ‘உல்காத்தா’ எனப்படும் இலத்தீன் மொழிபெயர்ப்பில் தானி 3:24-90 ஆகக் காணப்பாடுகிறது.<br /><br /></li><li><strong>சூசன்னா: </strong>யூத ஒழுக்கத்தின்படி அப்பழுக்கற்றவராய் வாழ்ந்துவந்த சூசன்னாவின் பேரழகில் மயங்கிய முதியோர் இருவர் காமுற்று அவரை அடைய முயன்றனர் (1-27). அது நிறைவேறாததால் அவர்மீது பொய்க்குற்றம் சுமத்தி, அவருக்குச் சாவுத் தண்டனை விதித்தனர் (28-41). ஆண்டவரோ தானியேல் வழியாக அவருக்கு முறையான தீர்ப்பு வழங்கி, அவரைச் சாவின் பிடியிலிருந்து விடுவித்தார் (42-64).<br /><br /> இலத்தீன் பாடத்தில் இப்பகுதி, தானியேல் நூலின் 13 ஆம் அதிகாரமாக இடம்பெறுகிறது.<br /><br /></li><li><strong>பேல் தெய்வமும் அரக்கப்பாம்பும்: </strong>இப்பகுதி எரேமியா இறைவாக்கினரின் சொற்களை (51:34,35,41) அடிப்படையாகக் கொண்டது. பேல் என்னும் தெய்வம் முழுமுதற் கடவுள் அல்ல என்பது இதன்மூலம் தெளிவாகிறது (1-22). இதே போன்று, பாபிலோனியர் வணங்கிவந்த அரக்கப்பாம்பும் கடவுள் அல்ல என்பது வெளிப்படுகிறது (23-30). இவற்றுடன் இறைவாக்கினர் அபகூக்குப் பற்றிய நிகழ்ச்சி ஒன்றும் இணைக்கப்படுகிறது (33-39). இறுதியில் ஆண்டவர் தானியேலைச் சிங்கக் குகையினின்று வியத்தகு முறையில் விடுவிப்பது விளக்கப்படுகிறது (31-32, 40-42).<br /><br /> இலத்தீன் பாடத்தில் இப்பகுதி, தானியேல் நூலின் 14-ஆம் அதிகாரமாக இடம்பெறுகிறது.<br /><br /></li></ol><p>இப்பகுதிகள் மூன்றும் கிரேக்க மொழியில் கி.மு. இரண்டாம் நூற்றாண்டில் எழுதப்பட்டிருக்கலாம். ‘செப்துவாசிந்தா’ பாடத்தை விடத் தெயொதோசியோன் மொழிபெயர்ப்பு தொன்மை வாய்ந்தது; ஆதலால் இதுவே இங்கு மூலபாடமாக அமைகிறது.</p><p>‘இஸ்ரயேலின் கடவுள் அனைத்துலகிற்கும் ஆண்டவர் ஆவார்; அவர் வரலாற்றில் குறுக்கிட்டுத் தம்மில் நம்பிக்கை கொள்வோரை எவ்வகைத் துன்பத்திலிருந்தும் காப்பாற்றுவார்’ என்ற தானியேல் நூலினது எபிரேய மொழி வடிவத்தில் நாம் காணும் மையக் கருத்தையே இம்மூன்று பகுதிகளும் வலியுறுத்துகின்றன.</p>'),
(47, '1 Maccabees', 'மக்கபேயர் - முதல் நூல்', '1 மக்கபேயர்', '1 மக்', '1 மக்கபே ஆகமம்', '<p>மத்தத்தியாவின் மூன்றாம் மகன் யூதா, கிரேக்கமயமாக்கல்மூலம் யூதர்களைப் பலவாறு துன்புறுத்திவந்த செலூக்கிய ஆட்சியை எதிர்த்துக் கிளர்ச்சியைத் தூண்டிவிட்டு யூதர்களை வழிநடத்தியதால், ‘மக்கபே’ என்று அழைக்கப்பெற்றார் (‘மக்கபே’ என்னும் சொல்லுக்குச் ‘சம்மட்டி’ எனச் சிலர் பொருள் கொள்வர்). காலப்போக்கில் அவருடைய சகோதரர்கள், ஆதரவாளர்கள், பிற யூதத் தலைவர்கள் ஆகிய அனைவருமே ‘மக்கபேயர்’ என்று குறிப்பிடப்பெற்றனர்.</p><p>அந்தியோக்கு எப்பிபானின் ஆட்சி தொடங்கி யோவான் இர்க்கான் ஆட்சிப் பொறுப்பு ஏற்றதுவரை (கி.மு. 175-134) யூத வரலாற்றில் இடம் பெற்ற குறிப்பிடத்தக்க சில நிகழ்ச்சிகளை இந்நூல் விளக்குகிறது. ஏறத்தாழ கி.மு. 100இல் பாலஸ்தீனத்தைச் சேர்ந்த யூதர் ஒருவரால் இந்நூல் எபிரேயத்தில் எழுதப்பெற்றிருக்க வேண்டும். அது தொலைந்துவிட, அதன் கிரேக்க மொழிபெயர்ப்பு இன்று மூலபாடமாக விளங்குகிறது.</p><p>இஸ்ரயேலைக் காப்பதற்காகக் கடவுள் மக்கபேயரைத் தேர்ந்தெடுத்து, வரலாற்றில் அவர்களோடு இருந்து செயல்படுகிறார், அவர்மீது பற்றுறுதி கொள்வோருக்கு வெற்றி அருள்கிறார் என்னும் செய்தியை இந்நூல் வலியுறுத்துகிறது.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>முகவுரை 1:1 - 9</li><li>யூதர்களின் துன்பமும் மக்கபேயரின் கிளர்ச்சியும் 1:10 - 2:70</li><li>யூதா மக்கபேயின் தலைமை 3:1 - 9:22</li><li>யோனத்தானின் தலைமை 9:23 - 12:53</li><li>சீமோனின் தலைமை 13:1 - 16:24</li></ol>'),
(48, '2 Maccabees', 'மக்கபேயர் - இரண்டாம் நூல்', '2 மக்கபேயர்', '2 மக்', '2 மக்கபே ஆகமம்', '<p>இந்நூல் மக்கபேயர் முதல் நூலின் தொடர்ச்சியன்று; ஒரு வகையில் அதற்கு இணையானது. அந்தியோக்கு எப்பிபானின் தந்தை நான்காம் செலூக்குவின் ஆட்சி தொடங்கி நிக்கானோரை யூதா மக்கபே வெற்றி பெற்றது வரையிலான காலக்கட்டத்தில் (ஏறத்தாழ கி.மு. 180-161) நடந்த நிகழ்ச்சிகள் இங்கு இடம்பெறுகின்றன. இவை ஏற்கெனவே மக்கபேயர் முதல் நூலின் முதல் ஏழு அதிகாரங்களில் காணப்படுகின்றன.</p><p>சீரேனைச் சேர்ந்த யாசோன் கிரேக்க மொழியில் ஐந்து தொகுதிகளில் விரிவாக எழுதிய வரலாற்றின் இரத்தினச் சுருக்கம் இந்நூல் (2:19-32). எருசலேம் யூதர்கள் எகிப்துவாழ் யூதர்களுக்கு விடுத்த இரண்டு மடல்கள் இதற்கு முன்னுரையாக (1:1 - 2:18) அமைகின்றன. “படிக்க விரும்புவோருக்கு மகிழ்ச்சி அளிக்கவும், மனப்பாடம் செய்ய விரும்புவோருக்கு எளிதாக அமையவும், அனைவருக்கும் பயன் தரவும்” (2:25) ஏறத்தாழ கி.மு. 124இல் இது எழுதப்பெற்றதால், வரலாற்றுக் கண்ணோட்டத்தைவிடத் திருவுரைப் பாணியில் அமைந்த இறையியல் கண்ணோட்டமே இதில் முன்னிடம் பெறுகிறது. இறைத் தலையீட்டை விளக்கும்பொருட்டு வெளிப்பாட்டு இலக்கிய நடைக்குரிய காட்சிகள் ஆங்காங்கே (2:21; 3:24-34; 5:2-4; 10:29-30; 15:11-16) புகுத்தப்பட்டுள்ளன.</p><p>திருச்சட்டத்திற்குக் கீழ்ப்படிந்து நடப்போர்க்குக் கடவுள் கைம்மாறு அளிப்பார் என்னும் மையக் கருத்தை இந்நூல் விளக்குகிறது. நீதிமான்களின் உயிர்ப்பு (7:9, 11, 14, 23; 14:46), இறந்தோருக்காக வேண்டுதல் (12:39-46), மறைசாட்சி இறப்பின் சிறப்பு (6:18-7:42; 14:37-46), மண்ணக மனிதருக்காக விண்ணகப் புனிதர்களின் மன்றாட்டு (15:12-16) போன்ற பழைய ஏற்பாட்டின் பிற நூல்களில் இடம்பெற்றிராத புதிய கருத்துகள் இதில் காணக்கிடக்கின்றன.</p><h2>நூலின் பிரிவுகள்</h2><ol><li>எகிப்திய யூதர்களுக்கு விடுத்த மடல்கள் 1:1 - 2:18</li><li>நூலாசிரியரின் முன்னுரை 2:19-32</li><li>கோவிலைத் தீட்டுப்படுத்த எலியதோரின் முயற்சி 3:1 - 40</li><li>அந்தியோக்கு எப்பிபானும் யூதர்களின் துன்பமும் 4:1 - 7:42</li><li>யூதாவின் வெற்றிகள் 8:1 - 10:8</li><li>மீண்டும் யூதர்களின் துன்பம் 10:9 - 15:36</li><li>முடிவுரை 15:37 - 39</li></ol>'),
(49, 'Matthew', 'மத்தேயு நற்செய்தி', 'மத்தேயு', 'மத்', '', '<h2>ஆசிரியர்</h2><p>இயேசு கிறிஸ்து நிறுவிய இறையாட்சி பற்றிய நற்செய்தியைத் திருத்தூதர் மத்தேயு முதன்முதலில் எழுதினார் என்றும் அதனை அரமேய மொழியில் எழுதினார் என்றும் திருச்சபை மரபு கருதுகிறது. எனினும் இன்று நம்மிடையே இருக்கும் கிரேக்க மத்தேயு நற்செய்தி நூல் ஒரு மொழிபெயர்ப்பு நூலாகத் தோன்றவில்லை. இயேசுவைப் பின்பற்றிய ஒரு திருத்தூதர் தாமே நேரில் கண்ட, கேட்ட நிகழ்ச்சிகளை நூலாக வடித்திருக்கிறார் என்பதை விட, அவரது வழிமரபில் வந்த சீடரோ, குழுவினரோ இதனைத் தொகுத்து எழுதியிருக்கவேண்டும் எனக் கொள்வதே சிறப்பு.</p><h2>சூழல்</h2><p>எருசலேம் கோவிலின் அழிவுக்குப் பின்னர் யூதச்சங்கங்கள் கிறிஸ்தவர்களைத் துன்புறுத்திய ஒரு காலக்கட்டத்தில் இந்நூல் எழுதப்பட்டிருக்க வேண்டும். இயேசுவின் சீடர்கள் யூதத் தொழுகைக் கூடங்களை விட்டுவிட்டுத் திருச்சபையாகக் கூடிவரத் தொடங்கிவிட்ட காலத்தில் இந்நூல் தோன்றியிருக்கிறது. அத்தகைய தொடக்கக் காலத் திருச்சபைக்குள்ளும் அறம், மன்னிப்பு, நல்லுறவு ஆகியவை இன்றியமையாதவை எனக் கற்பிக்க வேண்டிய சூழல் காணப்படுவதையும் இதைப் படிப்பவர் உய்த்துணரலாம்.</p><h2>உள்ளடக்கம்</h2><p>கிரேக்க மொழி பேசும் யூதர் நிறைந்த அந்தியோக்கியா போன்ற நகரங்களில் யூதக் கிறிஸ்தவர்களும் பிற இனத்துக் கிறிஸ்தவர்களும் திருச்சபையில் உறுப்பினர்களாக இருந்தனர். இவர்களுக்குள் பல சிக்கல்கள் இருந்தன. இது தவிர யூதக் கிறிஸ்தவர்கள் பலர் மற்ற யூதர்களால் துன்புறுத்தப்பட்ட நிலையில் மனத் தளர்ச்சியடைந்து இருந்தனர். இயேசுதான் உண்மையான மெசியாவா என்ற ஐயப்பாடு அவர்கள் உள்ளத்தில் எழுந்தது. இச்சிக்கல்களுக்குத் தீர்வுகாண இந்நூல் எழுதப்பட்டிருப்பதாகத் தெரிகிறது. யூதர்கள் எதிர்பார்த்திருந்த மெசியா இயேசுதாம் என யூதக் கிறிஸ்தவர்களுக்கு அழுத்தமாக இந்நூல் கூறுகிறது. அவர் இறைமகன் என்பது வலியுறுத்தப்படுகிறது. அவருடைய வருகையில் இறையாட்சி இலங்குகிறது எனும் கருத்தும் சுட்டிக்காட்டப்படுகிறது.</p><p>யூதக் கிறிஸ்தவர்கள் பிற இனத்தாரையும் சீடராக்கும் பணியைச் செய்ய இந்நூல் அறைகூவல் விடுக்கிறது. பிற இனத்தார் திருச்சட்டம் பெறாதவர்கள். இப்போது அவர்கள் கிறிஸ்தவர்களாக மாறிடினும் திருச்சட்டத்தின் உயர்வு பற்றி அவர்களுக்குச் சொல்லப்படுகிறது. கிறிஸ்து திருச்சட்டத்தின் நிறைவு எனவும் வலியுறுத்தப்படுகிறது.</p><p>ஆனால் அதே நேரத்தில் மத்தேயு, இறையாட்சியின் நெறிகள் யூதச் சமய நெறிகளைவிட மேலானவை எனக் கூறிக் கிறிஸ்தவ மதிப்பீடுகளைத் தொகுத்துப் புதிய சட்டநூலாகத் திருச்சபைக்கு வழங்குகிறார்; யாவரும் இப்புதிய சட்டத் தொகுப்பைக் கடைப்பிடிக்க அறைகூவல் விடுக்கிறார் (மத் 28:20). இதற்கு இயேசுவின் வாழ்க்கை நிகழ்வுகள், முக்கியமாக அவரின் கலிலேயப் பணிகள் எவ்வாறு அடிப்படையாக அமைகின்றன எனவும் இந்நூல் சுட்டிக்காட்டுகிறது.</p><p>இந்நூலில் கிறிஸ்தியல், திருச்சபையியல், நிறைவுகால இயல் ஆகியவற்றிற்கான அடிப்படைகள் பிணைந்து கிடக்கின்றன.</p><h2>அமைப்பு</h2><p>திருச்சட்ட நூலில் ஐந்து நூல்கள் அமைந்திருப்பதுபோல் இந்நூலிலும் முகவுரை, முடிவுரை நீங்கலாக ஐம்பெரும் பகுதிகள் அமைந்திருக்கக் காணலாம். ஒவ்வொரு பகுதியிலும் ஒரு நிகழ்ச்சிப் பகுதியும் ஓர் அறிவுரைப் பகுதியும் காணப்படுகின்றன.</p><h2>முன்னுரை</h2><p>இயேசுவின் பிறப்பும் குழந்தைப் பருவமும் 1 - 2</p><h2>பகுதி 1. விண்ணரசு பறைசாற்றப்படல் 3 - 7</h2><ol><li>நிகழ்ச்சி (3,4)</li><li>அறிவுரை (5-7) (மலைப்பொழிவு)</li></ol><h2>பகுதி 2. விண்ணரசுப் பணி 8 - 10</h2><ol><li>நிகழ்ச்சி (8,9)</li><li>அறிவுரை (10) (திருத்தூதுப் பொழிவு)</li></ol><h2>பகுதி 3. விண்ணரசின் தன்மை 11:1 - 13:52</h2><ol><li>நிகழ்ச்சி (11,12)</li><li>அறிவுரை (13:1-52) (உவமைப் பொழிவு)</li></ol><h2>பகுதி 4 விண்ணரசின் அமைப்பு 13:53 - 18:35</h2><ol><li>நிகழ்ச்சி (13:53 -17)</li><li>அறிவுரை (18) (திருச்சபைப் பொழிவு)</li></ol><h2>பகுதி 5. விண்ணரசின் வருகை 19 -25</h2><ol><li>நிகழ்ச்சி (19-23)</li><li>அறிவுரை (24,25) (நிறைவுகாலப் பொழிவு)</li></ol><h2>முடிவுரை</h2><ul>இயேசு துன்புற்று இறத்தலும் உயிர்த்தெழுதலும் 26 - 28</ul>'),
(50, 'Mark', 'மாற்கு நற்செய்தி', 'மாற்கு', 'மாற்', '', '<h2>ஆசிரியர்</h2><p>மாற்கு நற்செய்தி நூலின் ஆசிரியர் யோவான் மாற்கு என்பது திருச்சபை மரபு. மாற்குவின் தாய் பெயர் மரியா. இவர்களுடைய வீட்டில் தொடக்கக் காலக் கிறிஸ்தவர்கள் கூடி வழிபட்டு வந்தனர் (திப 12:12). மாற்கு 14:51, 52-இல் குறிப்பிடப்பட்டிருக்கும் இளைஞர் இவராக இருக்கலாம். இவர் பர்னபாவின் உறவினர். தொடக்கத்தில் பவுலோடு பயணம் செய்தவர். பவுலுடைய வாழ்வின் இறுதிக் கட்டத்தில் அவருக்கு உறுதுணையாக இருந்தவர் (2 திமொ 4:11). இவர் திருத்தூதர் பேதுருவுக்கும் துணையாகத் திருத்தொண்டில் ஈடுபட்டிருந்தார். பேதுருவின் போதனைகளின் அடிப்படையில் தான் இந்நற்செய்தி எழுதப்பட்டிருக்க வேண்டும் என்பது பலரது கருத்து.</p><h2>சூழல்</h2><p>நற்செய்தி நூல்களுள் மாற்கு நற்செய்தி நூல்தான் முதலாவதாக எழுதப்பட்டது என்பது பெரும்பாலான அறிஞர்களின் கருத்து. கி.பி.64- ஆம் ஆண்டில் இருந்து 70-ஆம் ஆண்டுக்குள் இந்நூல் எழுதப்பட்டிருக்க வேண்டும். எருசலேம் அழிக்கப்படவிருந்த சூழலில், உரோமையரால் கிறிஸ்தவர்கள் துன்புறுத்தப்பட்டக் காலத்தில், உரோமை நகரிலிருந்த மாற்கு இதனை எழுதியிருக்க வேண்டும். பேதுரு, பவுல் போன்ற பெருந்தலைவர்கள் கொல்லப்பட்டுவிட்ட காலக் கட்டத்தில், இயேசுவின் நற்செய்தியைத் தொகுத்து அதற்கு எழுத்து வடிவம் கொடுக்க வேண்டியது இன்றியமையாத தேவையாகத் தெரிந்திருக்க வேண்டும்.</p><p>இந்நற்செய்தி நூல் பிற இனத்துக் கிறிஸ்தவர்களைக் குறிப்பாகக் கண்முன் கொண்டு எழுதப்பட்டதாகக் தெரிகிறது. இந்நூலின் ஆசிரியர், இயேசு கிறிஸ்துவைப்பற்றிய மரபுகளை, சிறப்பாக அவருடைய வல்ல செயல்கள், உவமைகள், கூற்றுகள் ஆகியவற்றைத் தமக்கே உரிய பாணியில் விறுவிறுப்பாகத் தொகுத்து எழுதியுள்ளார். இவ்வாறு இயேசுவே மெசியா, இறைமகன் என்னும் உறுதியில் பிற இனத்துக் கிறிஸ்தவர்கள் வளர இந்நூல் பெரிதும் உதவியாக அமைகிறது.</p><h2>உள்ளடக்கம்</h2><p>“கடவுளின் மகனாகிய இயேசு கிறிஸ்துவைப்பற்றிய நற்செய்தி” என்னும் தொடக்கச் சொற்களே நற்செய்தி நூலுக்கு நல்ல தலைப்பாக அமைகின்றன.</p><p>இந்நூலின் முதல் பகுதியில் இயேசு கிறிஸ்து மாட்சிமை மிக்க இறைமகனாகிய மெசியா (1:1) என்பதையும் மனம்மாறும் மக்கள் பாவமன்னிப்புப் பெற்று, இறையாட்சியில் உரிமைக் குடிமக்களாகும் தகுதி பெறுகின்றனர் (1:15) என்பதையும் இவ்வாசிரியர் எடுத்துக்காட்டுகிறார். இவ்வாறு கலிலேயாவில் மக்கள் திரளுக்குப் பணிபுரியும் இயேசு, சீடர்களோடு இணைந்து செயல்பட்டு, பேய்கள் மற்றும் சமயத் தலைவர்களின் அதிகாரத்தை அடக்குகின்றார் என்பதை, மற்ற நற்செய்தி ஆசிரியர்களைவிட விளக்கமாக இவர் எடுத்துரைக்கிறார்.</p><p>இயேசு கிறிஸ்து மக்கள் அனைவருக்கும் மீட்பு வழங்கும் துன்புறும் மானிட மகன் (10:45) என்ற கருத்து, இரண்டாம் பகுதியில் வலியுறுத்தப்படுகின்றது. “இயேசு கிறிஸ்து துன்பங்கள் பட்டு இறந்து உயிர்பெற்றெழுந்து மீட்பரானார்” என்னும் தொடக்கக் காலத் திருச்சபையின் மையப் போதனையை, இயேசுவின் வாழ்க்கை நிகழ்ச்சிகளின் அடிப்படையில் இந்நூல் ஆசிரியர் விரித்துக் கூறுகின்றார்.</p><h2>அமைப்பு</h2><h2>பகுதி 1 முன்னுரை 1:1 - 13</h2><h2>பகுதி 2. இயேசுவே மெசியா (1:14 - 8:30)</h2><ol><li>இயேசுவும் மக்கள் கூட்டமும் 1:14 - 3:6</li><li>இயேசுவும் சீடர்களும் 3:7 - 6:6அ</li><li>இயேசு தம்மைச் சீடருக்கு வெளிப்படுத்தல் 6:6ஆ - 8:26</li><li>இயேசு மெசியா என்னும் அறிக்கை 8:27 - 30</li></ol><h2>பகுதி; 3. இயேசுவே மானிடமகன் (8:31 - 16:8)</h2><ol><li>பயணம் செய்யும் மானிடமகன் 8:31 - 10:52</li><li>எருசலேமில் மானிடமகன் 11:1 - 13:37</li><li>மானிடமகன் முழுமையாய் வெளிப்படுத்தப்படல் 14:1 -16:8</li></ol><h2>பகுதி; 4. முடிவுரை 16:9 - 20</h2>'),
(51, 'Luke', 'லூக்கா நற்செய்தி', 'லூக்கா', 'லூக்', '', '<h2>ஆசிரியர்</h2><p>மூன்றாம் நற்செய்தி நூலின் ஆசிரியர் சிரியநாட்டு அந்தியோக்கியாவைச் சேர்ந்த லூக்கா என்பது திருச்சபை மரபு. இவர் மருத்துவம் தெரிந்தவர். திருத்தூதர் பவுலோடு சேர்ந்து நற்செய்திப் பணி ஆற்றியவர். பிற இனக் கிறிஸ்தவச் சபைகளோடு மிகநெருங்கிய தொடர்பு கொண்டிருந்த இவரும் பிற இனக் கிறிஸ்தவர்தான் என்பது பெரும்பாலோர் கருத்து (பில 24; 2 திமொ 4:11; கொலோ 4:11-14).</p><h2>சூழல்</h2><p>‘மாண்புமிகு’ தியோபிலுக்கு இந்நூல் அர்ப்பணிக்கப்படுகிறது; நல்ல கிரேக்க மொழிநடையில் அமைந்துள்ளது. தம் காலத்துத் திருச்சபையின் போதனையையும் பணியையும் பற்றி அறிவிக்கும் நோக்கத்தோடு ஆசிரியர் இந்நூலைப் படைத்துள்ளார். இயேசுவைப் பற்றிப் பிற நூல்கள் இதற்குமுன் எழுதப்பட்டிருந்தாலும் முறையாகவும் முழுமையாகவும் வரலாற்றுப் பின்னணியோடும் யாவற்றையும் உறுதிப்படுத்தும் வகையில் இந்நூலை இவர் எழுதுகிறார் (லூக் 1:1-4). பிற இனத்தவருக்கென்றே எழுதுவதால் எபிரேயச் சொல்லாட்சி இந்நூலில் தவிர்க்கப்படுகிறது.</p><p>எருசலேம் நகரம் தீத்துவால் அழிக்கப்பட்டபோது நிகழ்ந்தவை இந்நூலில் சித்தரிக்கப்பட்டுள்ளன. மாற்கு நற்செய்தி நூலில் காணப்படும் எருசலேம் கோவில்பற்றிய தானியேல் மறைபொருள் வெளிப்பாட்டு இலக்கியச் செய்தி எருசலேம் நகர அழிவைப்பற்றிய செய்தியாக மாற்றப்படுகிறது (லூக் 21:5, 20; 13:35). எனவே இந்நூல் கி.பி. 70-க்கு பின்தான் எழுதப்பட்டிருக்கவேண்டும். அந்தியோக்கியா, உரோமை போன்ற ஏதேனும் ஒரு நகரிலிருந்து இந்நூல் எழுதப்பட்டிருக்கலாம்.</p><h2>உள்ளடக்கம்</h2><p>லூக்கா நற்செய்தியாளர் மீட்பு வரலாற்றை, யோவான் வரை உள்ள இஸ்ரயேல் மக்களின் காலம், இயேசுவின் காலம், திருச்சபையின் காலம் என்னும் முப்பெரும் கட்டங்களாகப் பிரிக்கின்றார் (16:16). ‘உங்கள் மீட்பு நெருங்கி வருகிறது’ என மீட்பளிக்கும் வருங்காலத்தைக் குறிப்பிட்டாலும் (21:28) இறையாட்சி மக்களிடையே மலர்ந்துவிட்டது (17:21), இப்பொழுதே இயேசுவின் வாழ்வில் நிறைவேறிவிட்டது (1:1; 4:21) என நிகழ்கால மீட்பையே வலியுறுத்திக் கூறுகிறார். இந்நூலில் எருசலேம் உலக மீட்பின் திட்டத்துக்கு மையமாகத் திகழ்கிறது. இந்நற்செய்தி நூல் எருசலேமில் தொடங்கி எருசலேமில் முடிகிறது. இழந்து போனதைத் தேடி மீட்கவே மானிடமகன் வந்தார் என்னும் கருத்து முதன்மைச் செய்தியாக விளங்குகிறது (19:10). இகழ்ந்து ஒதுக்கப்பட்ட பாவிகள், பெண்கள், ஏழைகள், சமாரியர் ஆகியோர் சிறப்பிடம் பெறுகின்றனர். தூய ஆவியின் செயல்பாடு, இயேசுவின் இரக்கம், திருப்பணி, மனம் மாற்றம், பாவ மன்னிப்பு, இவற்றால் ஏற்படும் மகிழ்ச்சி ஆகியவை வலியுறுத்தப் பெறுகின்றன. இறைவேண்டல், சான்றுபகருதல், பொறுப்புடன் செல்வத்தைப் பயன்படுத்தல், அமைதி பெறுதல், சிலுவை சுமத்தல் ஆகியன சீடத்துவத்தின் சிறப்புத் தகுதிகளாகச் சுட்டிக் காட்டப்படுகின்றன.</p><h2>அமைப்பு</h2><ol><li>அர்ப்பணம் 1:1 - 4</li><li>குழந்தைப் பருவ நிகழ்ச்சிகள் 1:5 - 2:52</li><li>திருப்பணிக்குத் தயார் செய்தல் 3:1 - 4:13</li><li>கலிலேயப் பணி 4:14 - 9:50</li><li>எருசலேம் நோக்கிப் பயணம் 9:51 - 19:27</li><li>எருசலேம் பணி 19:28 - 21:38</li><li>இயேசு துன்புற்று இறந்து உயிர்த்தெழுதலும் விண்ணேற்றமடைதலும் 22 - 24</li></ol>'),
(52, 'John', 'யோவான் நற்செய்தி', 'யோவான்', 'யோவா', 'அருளப்பர் நற்செய்தி', '<h2>ஆசிரியர்</h2><p>நான்காம் நற்செய்தியின் ஆசிரியர் செபதேயுவின் மகனாகிய யோவான் என்பது மிகத் தொன்மையான கிறிஸ்தவ மரபு. இதனை எழுதியதாக நற்செய்தியே கூறும் அன்புச்சீடர் (21:24) இவராகத்தான் இருக்க வேண்டும். ஆனால் யோவானே அனைத்தையும் எழுதியிருக்கவேண்டும் என்று கூற முடியாது. யோவான் இயேசுவைப்பற்றி எடுத்துரைத்த செய்திகள் அவரது சமூகத்தில் தனிவடிவம் பெற்று, பின்னர் எழுத்து வடிவம் ஏற்றது. காலப்போக்கில் கிறிஸ்தவச் சமூகத்தின் தேவைகளுக்கேற்பச் சில மாற்றங்கள் பெற்று, முன்னுரை, பிற்சேர்க்கை ஆகியவற்றுடன் இணைக்கப் பெற்று இன்றைய வடிவம் பெற்றிருக்கவேண்டும்.</p><p>இயேசுவின் அன்புச் சீடரான யோவான் முதலில் திருமுழுக்கு யோவானின் சீடராக இருந்து பின் இயேசுவின் சீடராகிறார் (1:35-39). இயேசுவோடு மிகுந்த நெருக்கம் கொண்டிருந்த மூன்று திருத்தூதருள் இவரும் ஒருவர் (மாற் 5:37; 9:2; 13:3; 14:33). இறுதி இரவுணவின்போது இயேசுவின் மார்புப் பக்கமாய் சாய்ந்திருந்த இவர் (13:22-26) மற்றத் திருத்தூதரெல்லாம் ஓடிவிட்ட நேரத்திலும் இயேசுவின் சிலுவையடியில் நின்றார். இவரிடமே இயேசு தம் அன்பு அன்னையை ஒப்படைத்தார் (19:25-27).</p><h2>நூலின் நோக்கம்</h2><p>“இயேசுவே இறைமகனாகிய மெசியா என்று நீங்கள் நம்புவதற்காகவும், நம்பி அவர் பெயரால் வாழ்வு பெறுவதற்காகவுமே இந்நூலில் உள்ளவை எழுதப் பெற்றுள்ளன” (20:31) என்று ஆசிரியரே நூலின் நோக்கத்தைத் தெளிவாக எடுத்துக் கூறுகின்றார். கிறிஸ்தவ நம்பிக்கையை ஆழப்படுத்திக் கிறிஸ்தவ வாழ்வை வலுப்படுத்துவது நற்செய்தியின் முக்கிய நோக்கமாய் இருந்தது. மேலும் கிறிஸ்துவுக்கு முந்திய பழையன கழிந்து, கிறிஸ்து வழியாகப் புதியன புகுந்துவிட்டன என்று காட்டுவதும், கிறிஸ்துவை நேரடியாகக் கண்டிராத இரண்டாம் தலைமுறையினருக்கு அவரைப் பற்றிய நம்பத்தக்க சான்று அளிப்பதும் (20:29), முதல் நூற்றாண்டின் இறுதிக் கட்டத்தில் தோன்றிய சில தவறான கொள்கைகளை (திருமுழுக்கு யோவானே மெசியா, தோற்றக் கொள்கை போன்றவை) திருத்துவதும் நூலாசிரியரின் நோக்கங்களாய் இருந்தன. இந்நோக்கங்களை நிறைவேற்றும் வண்ணம் ஏறத்தாழ கி.பி. 90ஆம் ஆண்டில் கிரேக்க மொழியில் இந்நற்செய்தி எழுதப்பட்டது. இது எபேசு நகரில் எழுதப்பட்டது என்பது கிறிஸ்தவ மரபு.</p><h2>உள்ளடக்கம்</h2><p>மனிதரான வாக்கே இயேசு எனத் தொடக்கத்திலேயே எடுத்துரைக்கிறார் நூலாசிரியர். அவரது இயல்பை ஒளி, வாழ்வு, வழி, உண்மை, உணவு போன்ற உருவகங்களால் விளக்கி, இயேசு யார் என்பதை எல்லாக் கால, இட, சமய, பண்பாட்டு மக்களுக்கும் எளிதில் புரிய வைக்கிறார். அழைத்தல், கோவில், வழிபாடு, திருமுழுக்கு, நற்கருணை ஆகிய கிறிஸ்தவக் கருத்துக்களின் ஆழ்ந்த பொருளை விரித்துரைக்கிறார். அரும் அடையாளங்களாலும் அவற்றைத் தொடரும் உரைகளாலும் இயேசுவை வெளிப்படுத்தி, அவர் பெற்ற ஏற்பையும் எதிர்ப்பையும் எடுத்துக் காட்டுகிறார். மகனுக்கும் தந்தைக்கும், மகனுக்கும் சீடருக்கும் உள்ள நெருங்கிய உறவு பற்றிச் சிறப்பாகப் பேசுகிறார். இயேசு துன்பங்கள் பட்டபோது அவரை நொறுக்கப்பட்ட துன்புறும் ஊழியனாக அல்ல, மாறாக அனைத்தையும் திட்டமிட்டுச் செயல்படுத்தும் வெற்றி வீரராகப் படம் பிடித்துக் காட்டுகிறார். இவ்வாறு இயேசுவின் உரைகள், அவருடைய செயல்கள், அவரைப் பற்றிய செய்திகள், அவருடைய ஆளுமை, அவரது பணித்தளம் ஆகிய அனைத்துமே இந்நற்செய்தியில் ஒத்தமை நற்செய்திகளிலிருந்து வேறுபட்டு இருக்கின்றன.</p><h2>அமைப்பு</h2><p>யூதத் திருவிழாக்களை அடிப்படையாகக் கொண்டு இயேசுவின் உரைகளும் அவரைப் பற்றிய பிற செய்திகளும் தொகுக்கப்பட்டிருப்பனவாகத் தெரிகிறது:</p><ol><li>முன்னுரைப் பாடல் 1:1 - 18</li><li>முதல் பாஸ்கா விழா 1:19 - 4:54</li><li>யூதர்களின் திருவிழா 5:1 - 47</li><li>இரண்டாம் பாஸ்கா விழா 6:1 - 71</li><li>கூடார விழா 7:1 - 10:21</li><li>கோவில் அர்ப்பண விழா 10:22 - 11:54</li><li>இறுதிப் பாஸ்கா விழா 11:55 - 20:31</li><li>பிற்சேர்க்கை 21:1 - 25</li></ol>'),
(53, 'Acts', 'திருத்தூதர் பணிகள்', 'திருத்தூதர் பணிகள்', 'திப', 'அப்போஸ்தலர் பணி', '<h2>ஆசிரியர்</h2><p>திருத்தூதர் பணிகள் என்னும் நூல் லூக்கா நற்செய்தி நூலின் தொடர்ச்சியான இரண்டாவது பகுதி (1:1). ஆகவே மூன்றாவது நற்செய்தி நூலின் ஆசிரியரே இந்நூலின் ஆசிரியர் என்னும் மரபு எல்லோராலும் ஏற்றுக் கொள்ளப்படுகிறது. இந்நூலின் பிந்திய பகுதியில் ஆசிரியர் தாமே கண்ட நிகழ்ச்சிகளைத் தொகுத்து வழங்குவதாக எழுதுகிறார். ‘நாங்கள் பயணம்செய்தோம்’, ‘நாங்கள் தங்கியிருந்தோம்’, ‘நாங்கள் போதித்தோம்’ போன்ற பகுதிகள் இந்நூலின் ஆசிரியர் பவுலின் உடன்பணியாளர் என்பதைச் சுட்டிக் காட்டுகின்றன (16:10-17; 20:5-15; 21:1-18; 27:1-28:16). (ஆசிரியரைப்பற்றிய பிற குறிப்புகளை லூக்கா நற்செய்தி நூல் முன்னுரையில் காண்க).</p><h2>சூழல்</h2><p>கிறிஸ்துவோ அவர் வழியைப் பின்பற்றுபவர்களோ உரோமை அரசுக்கு எதிராகக் குற்றம் ஏதும் செய்யாதவர்கள் என விளக்கமளிக்கவும், பிற இனத்தாருக்குத் திருத்தூதராகத் தேர்ந்துகொள்ளப்பட்ட பவுல் யூதருக்கு எதிராகப் பெருந்தவறு ஒன்றும் செய்யவில்லை என்று எடுத்துரைக்கவும் இந்நூலை ஆசிரியர் எழுதுகிறார். இச்சூழலில் நற்செய்திப் பணியும் இறைவார்த்தைப் போதனையும் சிறப்பிடம் பெறுகின்றன. ஆவியார் துணையுடன் கடவுளது மீட்புத் திட்டத்துக்குச் சான்று பகர்வது திருச்சபையின் கடமை என்பது தெளிவாகிறது. திருத்தூதர்கள் - குறிப்பாகப் பேதுருவும் பவுலும் - எவ்வாறு திருத்தொண்டாற்றினர் என்பது விளக்கமாக எடுத்துரைக்கப்படுகிறது. பவுல் உரோமையில் சான்று பகர்ந்து கொண்டிருப்பதே நூலின் முடிவுரையாக அமைகின்றது.</p><h2>உள்ளடக்கம்</h2><p>“தூய ஆவி உங்களிடம் வரும்போது நீங்கள் கடவுளது வல்லமையைப் பெற்று எருசலேமிலும் யூதேயா, சமாரியா முழுவதிலும் உலகின் கடையெல்லை வரைக்கும் எனக்குச் சாட்சிகளாய் இருப்பீர்கள்” (1:8) என்னும் உயிர்த்தெழுந்த ஆண்டவரின் கூற்றே இந்நூலுக்கு மையச் செய்தியாக அமைகின்றது. யூதரும் சமாரியரும் கிரேக்கரும் பிற இனத்தவரும் தூய ஆவியால் ஆட்கொள்ளப்பட்டு ஆண்டவரின் சாட்சிகளாகின்றனர். இறைவார்த்தைப் பணி வளர்ந்து பெருக, எங்கும் திருச்சபைகள் நிறுவப்படுகின்றன. எனவே இந்நூலைத் “தூய ஆவியின் பணிகள்” எனவும் அழைக்கலாம். பேதுரு, ஸ்தேவான், பவுல் ஆகியோரின் அருளுரைகள் இயேசு கிறிஸ்து பற்றிய கிறிஸ்தியல் விளக்கங்களை அளிக்கின்றன. பேதுரு, பவுல் ஆகியோரின் மனமாற்ற அனுபவங்களும், எருசலேம் சங்கமும் உலகெங்கும் உருவாகும் பொதுவான திருச்சபைக்கு வித்திடுகின்றன. கிறிஸ்தவர்களைப் பற்றித் தொகுத்துக் கூறுமிடங்களில் நட்புறவு, அப்பம்பிடுதல், இறைவேண்டல், சான்றுபகர்தல், தொண்டாற்றுதல், அன்புப் பகிர்வு (11:27-30; 2:42-47; 4:32-37) போன்றவற்றைச் சீடர்களின் தனித்தன்மைகளாக இந்நூல் எடுத்துக்காட்டுகிறது.</p><h2>அமைப்பு</h2><ol><li>முன்னுரை (விண்ணேற்றம்) 1:1 - 11</li><li>எருசலேமில் சான்று பகர்தல் 1:12 - 8:3</li><li>யூதேயா, சமாரியாவில் சான்று பகர்தல் 8:4 - 12:25</li><li>உலகின் கடையெல்லைவரை சான்று பகர்தல் 13:1 - 28:31</li></ol>'),
(54, 'Romans', 'உரோமையருக்கு எழுதிய திருமுகம்', 'உரோமையர்', 'உரோ', '', '<p>விவிலியத்தில் உள்ள ஆழமான இறையியல் பகுதியாக விளங்குவது உரோமையர் திருமுகமாகும். தூய பவுல் எழுதிய போதனையின் சுருக்கம் இந்நூலில் அடங்கியுள்ளது எனலாம். இத்திருமுகக் கருத்துக்களின் அடிப்படையில் திருச்சபையின் இறையியல் கோட்பாடுகள் பல வடிவங்களில் வளர்ச்சியடைந்துள்ளன.</p><p>இதனைத் தூய பவுல் கைப்பட எழுதவில்லை; மாறாக 16:22இல் காண்கிறபடி தெர்த்தியு என்பவரை எழுத்தாளராகக் கொண்டு எழுதியுள்ளார்.</p><h2>சூழலும் நோக்கமும்</h2><p>தூய பவுல் இத்திருமுகத்தை எழுதும்போது உரோமைக்குச் சென்றிருக்கவில்லை. எனினும் அக்காலத்தில் திருச்சபை அங்கே வேரூன்றியிருந்தது. வேறு பல நற்செய்தியாளர்கள் அங்குச் சென்று மறைப்பணி புரிந்திருந்தனர். அது உரோமைப் பேரரசின் தலைநகராக இருந்ததால், பல நாடுகளிலிருந்து கிறிஸ்தவர்கள் அங்குப் போய் வாழ்ந்து வந்தனர். இந்த உரோமைச் சபையைச் சந்திக்க விழைந்தார் பவுல். ஸ்பெயின் நாடு போகும் வழியில் உரோமைக் கிறிஸ்தவர்களைச் சந்திக்கத் திட்டமிட்டார் (15:28). மாசிதோனியா, அக்காயா ஆகிய நாடுகளிலிருந்து இறைமக்கள் கொடுத்த காணிக்கையை எருசலேம் கொண்டு போகுமுன் கொரிந்து நகரிலிருந்து இத்திருமுகத்தை அவர் கி.பி. 57-58 காலக் கட்டத்தில் வரைந்திருக்க வேண்டும்.</p><p>பவுல் இத்திருமுகத்தை எழுதுமுன் கலாத்தியருக்கு ஒரு திருமுகத்தை எழுதியிருந்தார். நம்பிக்கையினால் இறைவனுக்கு ஏற்புடைமை ஆதல் குறித்து அத்திருமுகத்தில் சொல்லப்பட்ட கருத்துக்கள் பல சர்ச்சைகளை உருவாக்கியிருக்க வேண்டும். அத்துடன் செயல்கள், சட்டங்கள் ஆகியவற்றை விட நம்பிக்கையே மேலானது என்னும் பவுலின் போதனையும் கடும் எதிர்ப்புக்குள்ளாகியது. சிலர் தவறான கருத்துக்களை உரோமையிலும் பரப்பி, செயல்கள் மற்றும் சட்டத்திற்கு மிகுந்த முக்கியத்துவம் கொடுக்க முனைந்தனர்; யூத மறைக்கெதிரான பல தவறான கருத்துக்களைப் பவுல் பரப்பிக் குழப்பம் ஏற்படுத்துவதாகக் கூறினர். எனவே பவுல் இத்திருமுகத்தை எழுதுகிறார். தம்முடைய போதனையைக் குறித்தும் தம் திருத்தூதுப் பணியைக் குறித்தும் தெளிவான கண்ணோட்டத்தை உரோமைக் கிறிஸ்தவர்கள் பெறவேண்டும்; தாம் அவர்களைச் சந்திக்குமுன் அவர்கள் தம்மைப் பற்றிய தவறான கருத்துக்களைக் கைவிட வேண்டும் என்றெல்லாம் எண்ணி அவர் இத்திருமுகத்தை எழுதியதாகத் தெரிகிறது.</p><p>எருசலேம் மக்களுக்கான நன்கொடையை நேரில் சென்று கொடுக்குமுன் தம்மையும் தாம் திரட்டிய கொடையையும் அவர்கள் ஏற்றுக்கொள்ளும்படி (15:31), அவர்களைத் தயாரிப்பதும் இத்திருமுகத்தின் சில பகுதிகளின் (குறிப்பாக அதி 9-11) நோக்கமாக இருக்கலாம் எனக் கருதப்படுகிறது.</p><h2>உள்ளடக்கம்</h2><p>இத்திருமுகத்தின் அதி 1-11 வரையிலான பகுதியில் ஆழமான இறையியல் கொள்கைகள் காணப்படுகின்றன.</p><p>1:17இல் பவுல் திருமுகத்தின் மையக் கருத்தை முன் வைக்கிறார்; நம்பிக்கையினால் இறைவனுக்கு ஏற்புடையவரே வாழ்வு பெறுவர் என்கிறார்.</p><p>தொடர்ந்து இக்கருத்து விளக்கம் பெறுகிறது. யூதர்கள் என்றாலும் பிற இனத்தவர்கள் என்றாலும் அவர்கள் கடவுளுக்குக் கீழ்ப்படியாதவர்களே. மனித குலம் முழுவதுமே பாவத்திற்கு உட்படுத்தப்பட்டுள்ளது. எனவே எல்லாருக்கும் மீட்பு தேவை. இந்த மீட்பு இயேசு மீது நம்பிக்கை கொள்வதால் வருகிறது. திருச்சட்டத்தினாலோ, விருத்தசேதனத்தினாலோ இது வருவதில்லை.</p><p>தொடர்ந்து, புதுவாழ்வு பற்றிப் பேசும் பவுல் அதை ஆவிக்குரிய வாழ்வு என்கிறார். ஏனெனில் தூய ஆவியால் நம்பிக்கை கொள்வோர் பாவம், சாவு ஆகியவற்றின் பிடியிலிருந்து விடுதலை பெறுகின்றனர்.</p><p>9-11 அதிகாரங்களில் யூதர்களைப் பற்றிப் பேசுகிறார் பவுல். யூதர்கள் கிறிஸ்துவைப் புறக்கணித்தது கடவுளின் திட்டப்படி மனிதகுலம் முழுவதும் கிறிஸ்து இயேசு வழியாகக் கடவுளின் அருளைப் பெறவே என்றும், பிற இனத்தார் இப்போது மனம் மாறியிருப்பது யூதர்களிடையே போட்டி மனப்பான்மையை வளர்க்கவே என்றும், கடவுள் யூதர்களுக்குக் கொடுத்த வாக்கு மாறுவதில்லை என்பதால் யூதர்கள் ஒருநாள் மனம் மாறுவர் என்றும் அவர் குறிப்பிடுகிறார்.</p><p>12 முதல் 15 வரையுள்ள அதிகாரங்களில் நடைமுறை ஒழுங்குகள் தரப்பட்டுள்ளன. உரோமைத் திருச்சபையில் யூதக் கிறிஸ்தவர்களும் பிற இனத்துக் கிறிஸ்தவர்களும் இருந்ததை மனத்தில் கொண்டு, அவர்கள் அன்புடன் கிறிஸ்தவ நெறிப்படி வாழும் முறைகுறித்துப் பவுல் பேசுகிறார்.</p><p><strong>16ஆம் அதிகாரம்:</strong> சில கையெழுத்துப்படிகளில் 15:1-16:24 வரையுள்ள பகுதி நீக்கப்பட்டு, 16:25-27இல் உள்ள இறுதி வாழ்த்து 14ஆம் அதிகாரத்துடன் சேர்க்கப்பட்டுள்ளது. எனவே 15 மற்றும் 16ஆம் அதிகாரம் வேறு ஒருவரால் எழுதப்பட்டது என்பர் சிலர். இருப்பினும் 15ஆம் அதிகாரம் கருத்தின் அடிப்படையில் முன்னைய அதிகாரங்களுடன் ஒத்துப்போகிறது.</p><p>16ஆம் அதிகாரத்தில் 26 பேரைப் பவுல் வாழ்த்துகிறார். இவர்கள் பவுல் சென்றிராத உரோமைச் சபையின் உறுப்பினர்களாக இருக்க முடியாது; எபேசில் பணிபுரிந்தவர்களாக இருக்க வேண்டும். இவ்வதிகாரம் இத்திருமுகத்தை எடுத்துச்சென்ற பெய்பா, செல்லும் வழியில், எபேசிலிருந்த பணியாளர்களிடம் கொடுக்க வேண்டிய வாழ்த்து மடலாக இருக்கலாம். அது காலப்போக்கில் உரோமையர் திருமுகத்துடனே இணைக்கப் பெற்றிருக்க வேண்டும்.</p><h2>அமைப்பு</h2><ol><li>முன்னுரை (வாழ்த்து, நன்றியும் மன்றாட்டும்) 1:1 - 17</li><li>மனிதருக்கு மீட்பு தேவை 1:18 - 3:20</li><li>கடவுள் மனிதரைத் தமக்கு ஏற்புடையவராக்கும் முறை 3:21 - 4:25</li><li>கிறிஸ்துவோடு இணைந்த நிலையில் புதுவாழ்வு 5:1 - 8:39</li><li>கடவுளின் மீட்புத் திட்டத்தில் இஸ்ரயேலர் 9:1 - 11:36</li><li>கிறிஸ்தவ வாழ்வு 12:1 - 15:13</li><li>முடிவுரையும் வாழ்த்தும் 15:14 - 16:27</li></ol>'),
(55, '1 Corinthians', 'கொரிந்தியருக்கு எழுதிய முதல் திருமுகம்', '1 கொரிந்தியர்', '1 கொரி', '', '<p>திருத்தூதர் பவுல் இத்திருமுகத்தை எழுதியுள்ளார். அவரது பெரிய மடல்களில் ஒன்றான இத்திருமுகம் இன்றைய சூழலுக்கு மிகவும் பொருத்தமுடைய அறிவுரைகளை வழங்குகிறது. அன்பு பற்றிய சிறந்த ஒரு கவிதை இம்மடலில் உள்ளது.</p><h2>சூழலும் நோக்கமும்</h2><p>பவுல் காலத்தில் கொரிந்து ஒரு பெரிய வணிக நகரமாக விளங்கியது; உரோமையரின் குடியேற்ற நகரமாகவும் திகழ்ந்தது. இங்குப் பல தெய்வங்களுக்கான கோவில்கள் இருந்தன. வழிபாடு சார்ந்த வாணிகமும் தழைத்தோங்கியது. கொரிந்தியரைப் போல் இருத்தல் என்னும் கூற்று ஒழுக்கக்கேடாய் வாழ்தலைக் குறித்தது.</p><p>பவுல் காலத்தில் இந்நகரில் ஏறக்குறைய 700,000 மக்கள் வாழ்ந்தனர். அவர்களில் மூன்றில் இரு பங்கினர் அடிமை மக்கள்.</p><p>பவுல் தம் இரண்டாம் பயணத்தின்போது இங்குத் திருச்சபையை ஏற்படுத்தினார் (3:6, 10; 4:5; திப 18:1-7); அக்கிலா, பிரிஸ்கில்லா தம்பதியருடன் நட்பு கொண்டார்; யூதர்களுடன் தொழுகைக் கூடத்தில் விவாதித்தார்.</p><p>பின்னர் பவுல் எபேசு நகரத்தில் நற்செய்திப்பணி ஆற்றியபோது குலோயி வீட்டினர் மூலம் கொரிந்துத் திருச்சபையில் இருந்த பிளவுகள் பற்றிக் கேள்விப்பட்டார் (1:11). மற்றும் கொரிந்திய திருச்சபை பவுலுக்கு ஒரு கடிதம் எழுதிக் கொரிந்தில் காணப்பட்ட சில சிக்கல்களுக்கு விடை கேட்டு இருந்தது (7:1). இக்கடிதம் வழியாகவும், கடிதத்தைக் கொண்டுவந்த மூவர் வாய்மொழி வழியாகவும் (16:17) அறிந்துகொண்ட சிக்கல்களுக்குத் தீர்வு காணும் முறையில் பவுல் திருமுகத்தை வரைந்துள்ளார். இது கி.பி. 54-55ஆம் ஆண்டுகளில் எழுதப்பட்டிருக்கலாம்.</p><h2>உள்ளடக்கம்</h2><p>மூன்று பகுதிகளாக இத்திருமுகத்தைப் பிரிக்கலாம். பவுல், அப்பொல்லோ, பேதுரு ஆகியோர் மீதிருந்த மிகைப்படுத்தப்பட்ட பற்று, கொரிந்தில் பிளவுகளுக்கும் தற்பெருமை பாராட்டுதலுக்கும் வழியமைத்தது (1). இத்தகைய பிளவுகள் கிறிஸ்துவையே பிளவுபட்டவராகக் காட்டுகின்றன என்கிறார் பவுல். கொரிந்தில் காணப்படும் பிளவுகள் அவர்களின் ஆன்மிக முதிர்ச்சியின்மையைக் காட்டுகின்றன என்று கூறும் அவர், கொரிந்தியக் கிறிஸ்தவர்கள் மனிதத் தலைவர்களிடமல்ல, மாறாகக் கிறிஸ்துவிடம் கொள்ளும் உறவைப் பற்றியே பெருமை பாராட்ட வேண்டும் என்கிறார் (2-4).</p><p>ஒருவன் தன் தந்தையின் மனைவியையே வைத்துக் கொண்டிருக்கும் அளவுக்கு ஒழுக்கக்கேடு மலிந்திருந்த நிலையில் கிறிஸ்தவர்கள் அதைக் கண்டிக்கவில்லை. அத்தகைய செயலைச் செய்தவனைச் சபையிலிருந்து நீக்குமாறு கட்டளையிடுகிறார் பவுல் (5); மேலும் கிறிஸ்தவர்கள் பொது நீதிமன்றங்களுக்குச் செல்லாமல் தங்களுக்குள்ளே பிணக்குகளைத் தீர்த்துக் கொள்ள வேண்டும் என்கிறார் (6). திருமணம், கன்னிமை, மணமுறிவு, மறுமணம் ஆகியவற்றைப் பற்றித் தொடர்ந்து பேசுகிறார் (7). சிலைகளுக்குப் படைத்த உணவை உண்ணலாமா (8-11) என்னும் கேள்விக்குப் பதில்அளிக்கும் நேரத்தில், கிறிஸ்தவ உரிமையும் அன்பின் விதியும் முரண்பட்டு நிற்பதாகத் தோன்றும்போது, மனவலிமையற்ற சகோதரரை முன்னிட்டு உரிமையை விட்டுக்கொடுக்க வேண்டும் என்கிறார் பவுல்; தொடர்ந்து, ஆண்டவரின் திருவிருந்தில் முறையாகப் பங்கெடுத்தல், ஆவிக்குரிய கொடைகளை முறையாகப் பயன்படுத்தல் பற்றிப் பேசுகிறார் (11-14).</p><p>உயிர் பெற்றெழுதலைப்பற்றிய கொரிந்தியரின் தவறான கண்ணோட்டத்தைப் பவுல் களையப் பார்க்கிறார் (15); உயிர்பெற்றெழுந்த உடல் எவ்வாறிருக்கும் என விளக்குகிறார்; இறுதியாக எருசலேம் கிறிஸ்தவர்களுக்காகத் தாம் திரட்டப்போகும் நன்கொடை பற்றிக் கூறி (16:1-4) பல்வேறு அறிவுரைகள் மற்றும் வாழ்த்துகளுடன் முடிக்கிறார் (16:5-24).</p><p>5:9இல் ஏற்கெனவே ஒரு மடல் எழுதியிருந்ததாகக் கூறப்படுகிறது. அது நமக்குக் கிடைக்காத நிலையில் இம்மடலையே கொரிந்தியருக்கு எழுதிய முதல் திருமுகம் என்கிறோம்.</p><h2>அமைப்பு</h2><ol><li>முன்னுரை (வாழ்த்தும் நன்றியும்) 1:1 - 9</li><li>திருச்சபையில் பிளவுகள் 1:10 - 4:21</li><li>கூடா ஒழுக்கமும் குடும்ப ஒழுக்கமும் 5:1 - 7:40</li><li>சிலைகளுக்குப் படைக்கப்பட்டவற்றை உண்ணுதல் 8:1 - 11:1</li><li>வழிபாடு 11:2 - 14:40<ul><li>பெண்கள் தலையை மூடிக் கொள்ளுதல்</li><li>ஆண்டவரின் திருவிருந்து</li><li>தூய ஆவியார் அருளும் கொடைகள்</li></ul></li><li>உயிர்பெற்றெழுதல் 15:1 - 58</li><li>இறைமக்களுக்காக நன்கொடை திரட்டல் 16:1 - 4</li><li>முடிவுரை 16:5 - 24</li></ol>'),
(56, '2 Corinthians', 'கொரிந்தியருக்கு எழுதிய இரண்டாம் திருமுகம்', '2 கொரிந்தியர்', '2 கொரி', '', '<p>கொரிந்தியருக்கு எழுதிய இரண்டாம் திருமுகம் தன்னிலை விளக்க மடலாக அமைந்துள்ளது. இது பவுலின் உள்ளத்தையும் உணர்வுகளையும் மிகத் தெளிவாகப் பிரதிபலிக்கிறது; தம் திருத்தூதுப் பணி முறையானது என நிலைநாட்டுவதையும், தம் பணியை இகழ்ந்து பேசியவர்கள்மேல் சினங்கொண்டு அவர்களைத் தாக்குவதையும், தாம் இகழ்ச்சிக்கு உட்படுத்தப்பட்ட போது உள்ளம் வேதனையடைந்து கண்ணீர் விடுவதையும், கொரிந்தியர் மனம் மாறியபோது ஆறுதலால் நிறைந்து மனம் மகிழ்ச்சியடைவதையும் நாம் கண்டு அவரோடு ஒத்துணர முடிகிறது.</p><h2>சூழலும் நோக்கமும்</h2><p>பவுல் கொரிந்தியருக்கு முதலாம் திருமுகத்தை எழுதிய பின் கொரிந்திலிருந்து போலிப் போதகர்கள் அவருக்கு எதிராகக் கலகமூட்டினர். அவர் கொரிந்துக்கு வரும் திட்டத்தை மாற்றியதால் அவர் உறுதியற்ற மனமுடையவர் என்றனர்; நன்கொடை திரட்டி வந்ததால் நேர்மையற்றவர் என்றனர்; அவர் தற்பெருமைமிக்கவர், நல்ல தோற்றமோ பேச்சுவன்மையோ இல்லாதவர், இயேசு கிறிஸ்துவின் அப்போஸ்தலராய் இருக்கத் தகுதியற்றவர் என்றனர்.</p><p>பவுல் தீத்துவைக் கொரிந்துக்கு அனுப்பி இச்சிக்கல்களுக்குத் தீர்வு காணப் பணித்தார்; தீத்து திரும்பி வந்தபின் கொரிந்தியர் மனம்மாற்றம் பெற்றதை அவரிடமிருந்து அறிந்து மகிழ்ந்தார்; குறிப்பாக 1 கொரி 5இல் சொல்லப்பட்ட ஒழுக்கக்கேடான ஒருவன் மனம்மாறி மீண்டும் திருச்சபையில் சேர்த்துக் கொள்ளப்பட்டது குறித்து மகிழ்ந்தார். எனவே மனம் மாறிய கொரிந்தியருக்கு நன்றி கூறும் நோக்குடனும் தம் திருத்தூதுப் பணியின் அதிகாரத்தை ஏற்காதோரை அதை ஏற்கச் செய்யும் நோக்குடனும் அவர் இம்மடலை எழுதினார். கி.பி. 55-56ஆம் ஆண்டுகளில் இதனை எழுதியதாகத் தெரிகிறது.</p><h2>ஒரே கடிதமா, கடிதங்களின் தொகுப்பா?</h2><p>இத்திருமுகம் ஒரே மடலா அல்லது பல மடல்களின் தொகுப்பா என்பது பற்றி அறிஞரிடையே கருத்து வேறுபாடுகள் காணப்படுகின்றன. சிலர் இதனை இன்றும் ஒரே மடலாகவே பார்க்கின்றனர். மற்றும் சிலர் 1-9 அதிகாரங்களை ஒரு மடலாகவும், 10-13 அதிகாரங்களை இன்னொரு மடலாகவும் பார்க்கின்றனர். ஆனால் வேறு சில அறிஞர்கள் இத்திருமுகம் ஐந்து மடல்களின் தொகுப்பு என்கின்றனர்:</p><ol><li>தம் பணிபற்றிய விளக்க மடல் (2:14-7:4);</li><li>கண்ணீர்மடல் (10-13);</li><li>ஒப்புரவுமடல் (1:1-2:13; 7:5-16);</li><li>நன்கொடை பற்றிக் கொரிந்தியருக்கு மடல் (8:1-24);</li><li>நன்கொடை பற்றி அக்காயாவினருக்கு மடல் (9:1-15);</li></ol><p>இவ்வாறு பகுத்து இந்த வரிசையில் வாசிக்கும்போது மிகுந்த கருத்துத் தொடர்பும் பொருள் தெளிவும் கிடைக்கிறது.</p><h2>உள்ளடக்கம்</h2><p>இத்திருமுகத்தின் முதற்பகுதியில் பவுல் கொரிந்து திருச்சபையுடன் தமக்கிருந்த உறவை விவரிக்கிறார்; புதிய உடன்படிக்கையே தம் பணிக்கும் கிறிஸ்துவின் பணிக்கும் அடிப்படை என்கிறார். பணியில் வரும் துன்பங்கள் குறித்தும் அப்பணிக்கான நோக்கம் குறித்தும் பேசுகிறார்; அந்நோக்கம் கிறிஸ்துவுடன் ஒப்புரவு ஆதல் என்கிறார்; தாம் வரும்போது நன்கொடைகளைத் தயாராக வைத்திருக்க வேண்டுமென அறிவுரை கூறுகிறார்.</p><p>இறுதியில், தாம் நிச்சயமாகக் கொரிந்துக்கு வரப்போவதாக வலியுறுத்தித் தாம் உண்மையான திருத்தூதர் என்றும் ஒரு திருத்தூதருக்குரிய தன்மையுடன் கண்டிப்பான நடவடிக்கைகள் எடுக்கப் போவதாகவும் கூறுகிறார்.</p><h2>அமைப்பு</h2><ol><li>முன்னுரை (வாழ்த்தும், கடவுளுக்கு நன்றி கூறுதலும்) 1:1 - 11</li><li>பவுலின் பயணத் திட்டத்தை மாற்றியது குறித்து விளக்கம் 1:12 - 2:13</li><li>தம் பணிபற்றிய விளக்கம் 2:14 - 6:13</li><li>தூய வாழ்விற்கான அழைப்பு 6:14 - 7:2</li><li>பவுலின் ஆறுதலும் மகிழ்ச்சியும் 7:3 - 16</li><li>யூதேயாவிலுள்ள கிறிஸ்தவர்களுக்கு நன்கொடை திரட்டுதல் 8:1 - 9:15</li><li>பணிபற்றிய குற்றச்சாட்டுக்கு மறுமொழி 10:1 - 13:10</li><li>முடிவுரை 13:11 - 14</li></ol>'),
(57, 'Galatians', 'கலாத்தியருக்கு எழுதிய திருமுகம்', 'கலாத்தியர்', 'கலா', '', '<p>உரோமையருக்கு எழுதிய திருமுகம் போல் நம்பிக்கையினால் ஏற்புடைமை ஆதல் பற்றிச் சுருக்கமாகப் பேசும் இன்னொரு திருமுகம் கலாத்தியர் திருமுகமாகும். உரோமையர் திருமுகத்தை இத்திருமுகத்தின் விளக்கவுரையாகப் பலர் பார்க்கின்றனர். குறிப்பாக உரோ 1-8 அதிகாரங்கள் கலா 2:15-21 பகுதியின் விரிவுரையாக அமைகின்றன.</p><h2>சூழலும் நோக்கமும்</h2><p>கலாத்திய சபையில் யூதமயமாக்கும் கிறிஸ்தவர்கள் சிலர் புகுந்து பவுல் எடுத்துரைத்த போதனைக்கு எதிராகப் பேசினர். அவர்கள் திருச்சட்டத்தினால்தான் மீட்புப் பெற இயலும் என வாதிட்டனர்; பிற இனத்துக் கிறிஸ்தவர்கள் மோசேயின் சட்டமாகிய விருத்தசேதனம் போன்றவற்றைக் கடைப்பிடித்தாக வேண்டும் என்றனர். வேற்றினத்துக் கிறிஸ்தவர்களிடம் நல்ல பெயர் வாங்கவேண்டும் என்பதற்காக சில சட்ட எதிர்பார்ப்புகளைப் பவுல் நீக்கிவிட்டதாகக் குற்றம் சாட்டினர்.</p><p>இந்நிலையில் யூதமயமாக்கும் கிறிஸ்தவர்களின் போதனையை முறியடிக்கக் கடுமையான முறையில் பவுல் இத்திருமுகத்தை எழுதுகிறார். இதனைக் கி.பி. 52-53 ஆண்டுகளில் எழுதியிருக்கலாம் என்பது பொதுவான கருத்து. எனினும் ஒரு சிலர் இத்திருமுகம் எருசலேம் சங்கத்துக்கு முன்பே, அதாவது கி.பி. 49க்கு முன்பே எழுதப்பட்டிருக்க வேண்டும் என்றும் கூறுகின்றனர்.</p><h2>உள்ளடக்கம்</h2><p>இத்திருமுகத்தின் முதற்பகுதியில் பவுல் தம்மைக் குறித்துப் பேசுகிறார். தாம் இயேசு கிறிஸ்துவின் திருத்தூதர் என்பதை நிலைநாட்டப் பார்க்கிறார்; தம் திருத்தூதுப் பணிக்கான அழைப்பு எந்த மனித அதிகாரத்திடமிருந்தும் வரவில்லை, மாறாகக் கடவுளிடமிருந்தே வந்தது என்கிறார்; யூதரல்லாதார்க்கும் நற்செய்தி போதிப்பதே தம்முடைய பணி என அடித்துச் சொல்கிறார் (1-2).</p><p>நம்பிக்கையால் மட்டுமே நாம் கடவுளுக்கு ஏற்புடையவர்களாகிறோம் என்கிறார் பவுல்; கிறிஸ்தவர் தூய்மையாக்கப்படுவது சட்டம் சார்பான செயல்களால் அல்ல, மாறாக நம்பிக்கையால் விளையும் கீழ்ப்படிதலால் என்கிறார்; கிறிஸ்துவைச் சார்ந்தோர் உரிமை வாழ்வுக்கு அழைக்கப்பட்டவர்கள் என்பதை அழுத்தந்திருத்தமாகக் கூறுகிறார் (3-4).</p><p>கிறிஸ்தவ வாழ்வு கிறிஸ்துவின் மீது கொண்ட நம்பிக்கையால் விளையும் அன்பின் அடிப்படையில் உள்ளது என அவர் விளக்குகிறார் (5-6).</p><h2>அமைப்பு</h2><ol><li>முன்னுரை<ul>வாழ்த்தும், நற்செய்தியும் 1: 1 - 10</ul></li><li>நிகழ்ச்சிப் பகுதி:<ul>பவுல் திருத்தூதராக அழைப்பு பெறல், எருசலேம் சங்கம், பவுல் அறிவிக்கும் நற்செய்தி 1:11 - 2:21</ul></li><li>கொள்கைப்பகுதி:<ul>நம்பிக்கையால் இறைவனுக்கு ஏற்புடைமை ஆதல் 3:1 - 4:31</ul></li><li>அறிவுரைப்பகுதி:<ul>கிறிஸ்தவரின் உரிமை வாழ்வு, நம்பிக்கை ஆகியவற்றை வாழ்ந்து காட்டுதல் 5:1 - 6:10</ul></li><li>முடிவுரை 6:11 - 18</li></ol>'),
(58, 'Ephesians', 'எபேசியருக்கு எழுதிய திருமுகம்', 'எபேசியர்', 'எபே', '', '<p>எபேசியர் திருமுகம் மிகவும் சிறந்த கருத்துக்களைத் தன்னுள் கொண்டுள்ளது; திருச்சபை பற்றிய அழகான உருவகங்களை நமக்குத் தருகிறது. கடவுளின் திட்டம் என்பது மனித குலத்தை மட்டுமல்ல, படைப்பு அனைத்தையுமே கிறிஸ்துவின் தலைமையில் கடவுளுடன் ஒப்புரவாக்குவது என்னும் உயரிய கருத்தை இத்திருமுகம் எடுத்துரைக்கிறது; குடும்ப வாழ்வு பற்றிச் சிறந்த அறிவுரைகளையும் வழங்குகிறது. இது கொலோசையர் திருமுகத்துடன் நெருங்கிய தொடர்புடையது. பலர் இதனைக் கொலோசையர் திருமுகத்தின் விளக்கமாகக் கருதுகின்றனர்.</p><h2>ஆசிரியர்</h2><p>இத்திருமுகத்தைப் பவுல் எழுதினார் என்பது மரபு நம்பிக்கையாக இருந்தாலும், அண்மைக் காலத்தில் சில மாற்றுக் கருத்துக்களும் தோன்றியுள்ளன. மூன்று ஆண்டுகளுக்கு மேல் எபேசில் தங்கிப் பணிபுரிந்த ஆசிரியர் (திப 18:23-19:4) எபேசு மக்களுடன் கொண்டிருந்த உறவைத் திருமுகம் காட்டவில்லை (1:15; 3:2); வழக்கமான வாழ்த்துக்களும் இதில் இல்லை.</p><p>இதன் கருத்தோட்டமும் நடையும் சொற்களும்கூட பவுல் எழுதிய கடிதங்களினின்று மாறியிருக்கின்றன. திருச்சபையைக் கிறிஸ்துவின் மறையுடலாகச் சித்தரிக்கும் ஆழமான இறையியல் கருத்துக்கள் பிற்காலத்தவை. இத்தகைய பல காரணங்களின் அடிப்படையில் இது கி.பி. 80க்குப் பின் வேறொருவரால் எழுதப்பட்ட மடல் எனப் பலர் கூறுவர். இது லவோதோக்கியருக்கு எழுதப்பட்ட மடல் என்பர் வேறு சிலர்.</p><p>எனினும் இதனை ஒரு மடல் என்பதைவிட ஆழமான இறையியல் கட்டுரை எனக் கொள்வதே சிறப்பு. பல சபைகளுக்கு எழுதப்பட்ட திருமுகத்தின் எபேசு நகரப் பிரதி இது எனக் கருதலாம். எனவே தனிப்பட்ட செய்திகள் இதில் இடம் பெறவில்லை. இதனைப் பவுலின் சீடர் ஒருவர் பவுலினது கண்ணோட்டத்தில், அவரது பெயரில் எழுதியிருக்க வேண்டும். இவ்வாறு எழுதுவது அக்காலத்தில் முறையாகக் கருதப்பட்டது.</p><h2>உள்ளடக்கம்</h2><p>இத்திருமுகத்தின் முதற்பகுதியில் ஒற்றுமை பற்றிய மையக் கருத்தை ஆசிரியர் விளக்குகின்றார்; தந்தையாம் கடவுள் தம் மக்களை எப்படி அழைத்துள்ளார் என்றும், அவர்கள் எவ்வாறு தம் மகன் இயேசு கிறிஸ்துவினால் மன்னிக்கப்பெற்று, பாவத்திலிருந்து விடுதலை பெற்றார்கள் என்றும், கடவுளின் தூய ஆவியால் எவ்வாறு உறுதிப்படுத்தப்படுகின்றார்கள் என்றும் கூறுகிறார் (1-3).</p><p>இரண்டாம் பகுதியில் வாசகர்கள் கிறிஸ்துவுடன் கொண்டுள்ள உறவைத் தங்கள் வாழ்வில் காட்ட வேண்டும் என்கிறார் ஆசிரியர். பொதுவான சமூக அன்பு வாழ்வும், குடும்ப அன்பு வாழ்வும் இந்த ஒற்றுமையை எடுத்துக் காட்ட வேண்டும் என விருப்பம் தெரிவிக்கின்றார்.</p><p>இத்திருமுகத்தில் பல உருவகங்கள் இடம் பெறுகின்றன. திருச்சபை உடலாகவும் கட்டடமாகவும் மணமகளாகவும், கிறிஸ்து தலையாகவும் மூலைக்கல்லாகவும் மணமகனாகவும் உருவகிக்கப்படுவதைக் காண்கிறோம்.</p><p>கிறிஸ்துவுடன் இணைந்து வாழ்தல் பற்றிப் பலமுறை சொல்லப்படுகிறது; கடவுளின் அருள் வலியுறுத்தப்படுகின்றது. அனைத்தும் கிறிஸ்துவின் அன்பு, தியாகம், மன்னிப்பு, அருள், தூய்மை என்னும் அடிப்படையில் பார்க்கப்படுகின்றன.</p><h2>அமைப்பு</h2><ol><li>முன்னுரையும் வாழ்த்தும் 1:1 - 2</li><li>கிறிஸ்துவும் திருச்சபையும் 1:3 - 3:21</li><li>கிறிஸ்தவப் புது வாழ்வு 4:1 - 6:20</li><li>இறுதி வாழ்த்தும் முடிவுரையும் 6:21 - 24</li></ol>'),
(59, 'Philippians', 'பிலிப்பியருக்கு எழுதிய திருமுகம்', 'பிலிப்பியர்', 'பிலி', '', '<p>இத்திருமுகம் ஒற்றுமை பற்றியும் மகிழ்ச்சி பற்றியும் அருமையான கருத்துகளை எடுத்துக்கூறுகிறது; கிறிஸ்து இயேசுவின் வழியாய் அவற்றைப் பெற இயலும் என அழுத்தமாக எடுத்துரைக்கிறது.</p><h2>ஆசிரியர்</h2><p>பவுலது மிக உயர்ந்த சிந்தனைகள் பலவற்றைக் கொண்டுள்ள இத்திருமுகம் பவுல் எழுதியவற்றுள் மிகச் சிறந்த திருமுகமாகப் பலரால் கருதப்படுகிறது.</p><h2>சூழலும் நோக்கமும்</h2><p>பவுல் தம் இரண்டாம் நற்செய்திப் பயணத்தின்போது பிலிப்பியில் பலரை மனம் மாற்றினார். சிறைப்பிடிக்கப்பட்டு, வியத்தகு முறையில் சிறையிலிருந்து தப்பினார். மூன்றாம் நற்செய்திப் பயணத்தின்போதும் அங்கு வந்தார் (திப 20:1,6).</p><p>பின்னாளில் பவுல் சிறைப்பட்டபோது பிலிப்பியர்கள் எப்பப்பிராதித்திடம் பணம் கொடுத்துப் பவுலுக்குப் பணிவிடை செய்யுமாறு அவரை அனுப்பினர் (4:18). எப்பப்பிராதித்து கடுமையாக நோயுற்றார். குணம் பெற்ற பின் பவுல் பிலிப்பியர் திருமுகத்தை எழுதி அவரிடம் கொடுத்து அனுப்பியிருக்க வேண்டும் (2:25-30). தமக்கு உதவி செய்த பிலிப்பியருக்கு நன்றி செலுத்துவதற்காகவும் அவர்கள் மகிழ்வோடும் மன உறுதியோடும் கிறிஸ்தவ நம்பிக்கையோடு இலங்கவேண்டும் என்பதற்காகவும் இந்நூலை எழுதுகிறார்.</p><p>அவர் எங்குச் சிறைப்பட்டிருந்தபோது இந்தத் திருமுகத்தை எழுதினார் என்பதில் கருத்து ஒற்றுமை இல்லை. உரோமைச் சிறையிலிருந்து எழுதினார் என்பதே மரபுக் கருத்து. எனினும் அவரது மூன்றாம் நற்செய்திப் பயணத்தின்போது அவர் எபேசிலிருந்து இதனை எழுதியிருக்க வேண்டும் என்றே பல அறிஞர்கள் கருதுகின்றனர்.</p><h2>உள்ளடக்கம்</h2><p>இத்திருமுகத்தில் பவுல் தன்னிலை விளக்கம் தருகிறார் (1:12-26; 4:10-19); தாம் சிறைப்பட்டதைப் பற்றி எடுத்துரைக்கிறார்; துன்பங்களில் பிலிப்பியர் மனவுறுதியோடு இருக்குமாறு கேட்டுக் கொள்கிறார் (1:27,30; 4:4); பிலிப்பியர் தாழ்மையுடனும் ஒற்றுமையுடனும் இருக்குமாறு வேண்டுகின்றார்; தம்மை மிகவும் தாழ்த்திப் பின்னர் தந்தையால் உயர்த்தப்பட்ட இயேசுவை முன்மாதிரியாகக் காட்டுகிறார் (2:1-11; 4:2-3).</p><p>திமொத்தேயுவையும் எப்பப்பிராதித்துவையும் பிலிப்பியத் திருச்சபை ஏற்றுக் கொள்ள வேண்டும் எனப் பவுல் விரும்புகிறார் (2:19-30); யூதமயமாக்கலைக் குறித்துப் பிலிப்பியரை எச்சரிக்கிறார் (3:1-21); தமக்கு உதவி செய்த பிலிப்பியருக்கு நன்றி செலுத்துகிறார் (4:10-20).</p><p>பிலிப்பியர் திருமுகத்தில் பழைய ஏற்பாட்டு மேற்கோள்கள் இல்லை. இக்கடிதம் மகிழ்வின் கடிதமாகும். மகிழ்வைக் குறிக்கும் ‘காரா’ எனும் கிரேக்கச் சொல் 16 முறை இத்திருமுகத்தில் வருகிறது. கிறிஸ்துவின் தாழ்மை உயர்வு பற்றிய கிறிஸ்தியல் பாடல் (2:5-11) மிகச் சிறப்பானது.</p><h2>அமைப்பு</h2><ol><li>முன்னுரை (வாழ்த்தும், நன்றியும் இறைவேண்டலும்) 1:1 - 11</li><li>பவுலின் தன்னிலை விளக்கம் 1:12 - 26</li><li>கிறிஸ்தவ வாழ்வு 1:27 - 2:18</li><li>திமொத்தேயு, எப்பப்பிராதித்து குறித்த திட்டம் 2:19 - 30</li><li>எதிரிகள் குறித்து எச்சரிக்கை 3:1 - 4:9</li><li>பவுலும் பிலிப்பிய நண்பர்களும் 4:10 - 20</li><li>முடிவுரை 4:21 - 23</li></ol>'),
(60, 'Colossians', 'கொலோசையருக்கு எழுதிய திருமுகம்', 'கொலோசையர்', 'கொலோ', '', '<p>விவிலியத்தில் கிறிஸ்தியல் விளக்கம் மிகுதியாய்க் கொண்ட நூல் கொலோசையர் திருமுகமாகும். கிறிஸ்து இல்லாமல் கிறிஸ்தவர்களாக வாழ இயலாது என இத்திருமுகம் வலியுறுத்திக் கூறுகிறது.</p><h2>ஆசிரியர்</h2><p>இத்திருமுகத்திலேயே பவுல்தான் இதன் ஆசிரியர் எனக் குறிப்பிடப்பட்டுள்ளது (1:1,23; 4:18). 19ஆம் நூற்றாண்டு வரை இதனை யாரும் மறுக்கவில்லை. ஆனால் அண்மைக் காலத்தில் இத்திருமுகத்தில் பயன்படுத்தப்படும் சொற்கள், மொழிநடை, இறையியல் கருத்துக்கள் அடிப்படையில் இதனைப் பவுலே நேரடியாக எழுதியிருக்க மாட்டார் என்னும் கருத்து வலுப்பெற்று வருகிறது. அவரது கண்ணோட்டத்தில், மாறிவிட்ட காலக் கட்டத்திற்கும் சிக்கல்களுக்கும் ஏற்ப, அவரது பெயரில் பவுலின் சீடர் ஒருவர் இதனை எழுதியிருக்க வேண்டும் எனப் பல அறிஞர்கள் கருதுகின்றனர்.</p><h2>சூழலும் நோக்கமும்</h2><p>சின்ன ஆசியாவிலிருந்த கொலோசை நகரத்தில் பவுல் நேரடியாக நற்செய்திப் பணி ஆற்றவில்லை; ஆனால் எபேசு நகரில் அவர் தங்கியிருந்த போது எப்பப்பிரா மூலமாகக் கொலோசையில் நற்செய்தி அறிவித்தார் (1:7-8).</p><p>இந்நகரில் ஞான உணர்வுக் கொள்கையின் தொடக்க வடிவமும் யூதச் சிந்தனைகளும் இணைந்த சில கொள்கைகள் பரவிக் கிடந்தன. சடங்கு விதிகளுக்கு முக்கியத்துவம் தருதல் (2:16-17; 3:11), உடல் ஒறுத்தல் (2:21,23), வானதூதர் வழிபாடு (2:18; 2:2-3), மனித ஞானத்திலும் மரபிலும் மிகுந்த நம்பிக்கை வைத்தல் (2:4,8) போன்றவை இக்கொள்கையில் விரவிக் கிடந்தன. இந்நிலையைக் கண்டித்துக் கிறிஸ்தவ உண்மையை நிலைநிறுத்த இத்திருமுகம் எழுதப்பட்டிருக்க வேண்டும். சிறையிலிருந்து எழுதப்பட்டதாகச் சொல்லப்பட்டிருப்பதால் இது சிறைக்கூட மடல்களுள் ஒன்றாகக் கருதப்பட்டது. கி.பி. 70-80 ஆண்டுகளில் இது எழுதப்பட்டிருக்கலாம்.</p><h2>உள்ளடக்கம்</h2><p>கொலோசையில் இருந்த போலிப்போதகர்களின் தவறான கொள்கைகளைக் கண்டிப்பதற்காக இத்திருமுக ஆசிரியர் கிறிஸ்துவைக் கடவுளது சாயல் என்கிறார் (1:15); அவர் அனைத்தையும் படைத்தவர் என்கிறார் (1:16); அனைத்துக்கும் முந்திய அவர், அனைத்தையும் நிலைக்கச் செய்பவர் என்கிறார் (1:17). இத்திருமுகத்தின்படி கிறிஸ்து திருச்சபையின் தலைவர் (1:18), இறந்து உயிர்த்தெழுவோருள் தலைப்பேறு (1:18), கடவுளின் தன்மையை மனித உருவில் நிறைவாய்க் கொண்டவர் (1:19; 2:9), கடவுளோடு நம்மை மீண்டும் ஒப்புரவாக்குபவர் (1:20-22).</p><p>முதற் பகுதியில் (1-2) கிறிஸ்துவின் தன்மையை எடுத்துக்காட்டிய திருமுக ஆசிரியர், இரண்டாம் பகுதியில் இப்போதனை கிறிஸ்தவர்களின் வாழ்வில் என்ன தாக்கத்தை ஏற்படுத்துகிறது என விளக்குகிறார் (3-4). கிறிஸ்தவர்கள் பழைய இயல்பைக் களைந்து புதிய இயல்பை அணிந்து கொள்ள வேண்டும்; குடும்ப வாழ்விலும், தொழிலிலும், பொது வாழ்விலும் தூய்மையைக் கடைப்பிடிக்க வேண்டும் என அவர் அறிவுறுத்துகிறார்; இறுதியில் திக்கிக்கு என்பவருக்கு நன்றி கூறி நண்பர்களுக்கு வாழ்த்துக் கூறுகிறார்.</p><h2>அமைப்பு</h2><ol><li>முன்னுரை (வாழ்த்தும் நன்றியும் மன்றாட்டும்) 1:1 - 14</li><li>கிறிஸ்துவின் மேன்மை 1:15 - 2:3</li><li>போலிப் போதகர்களுக்கு எதிரான எச்சரிக்கை 2:4 - 23</li><li>கிறிஸ்தவ வாழ்வு 3:1 - 4:6</li><li>முடிவுரை 4:7 - 18</li></ol>');
INSERT INTO `t_bookkey` (`bn`, `en`, `tn_f`, `tn_s`, `tn_a`, `tn_o`, `intro`) VALUES
(61, '1 Thessalonians', 'தெசலோனிக்கருக்கு எழுதிய முதல் திருமுகம்', '1 தெசலோனிக்கர்', '1 தெச', '', '<p>பவுல் எழுதியவற்றுள் இதுவே முதலாவது திருமுகம். அவர் இதனைக் கி.பி. 51ஆம் ஆண்டில் எழுதினார். தொடக்ககால மடலாக இருப்பதால் இதில் இறையியல் வளர்ச்சி மிகுதியாக இடம் பெறவில்லை. இருப்பினும் உயிர்பெற்றெழுதல், ஆண்டவரின் இறுதி வருகை ஆகியவை பற்றிய இதன் கருத்துக்கள் முக்கியமானவை.</p><h2>சூழலும் நோக்கமும்</h2><p>மாசிதோனியாவிலுள்ள ஒரு துறைமுக நகரம் தெசலோனிக்கா. அங்கு யூதர்கள் மிகுதியாக வாழ்ந்து வந்தார்கள். பவுல் தம் இரண்டாம் நற்செய்திப் பயணத்தின்போது தெசலோனிக்கா வந்தார்; அங்கு எதிர்ப்பு இருந்ததால் பெரேயா வழி ஏதென்சு சென்றார். அங்கிருந்தபோது தெசலோனிக்கர் பற்றிய நினைவே அவர் நெஞ்சில் நிறைந்திருந்தது. தாமே அங்குச் செல்ல முடியாத நிலையில் பவுல் தமக்குப் பதில் திமொத்தேயுவை அனுப்பினார். திமொத்தேயு தெசலோனிக்கா சென்று திரும்பி வந்தபோது நல்ல செய்தி கொண்டு வந்தார். அதாவது அங்குள்ள சீடர்கள் இன்னல்களுக்கிடையே தளரா ஊக்கத்துடன் கிறிஸ்தவராகத் தொடர்ந்து நிலைத்திருக்கின்றனர் என்பதை அவர் பவுலிடம் தெரிவித்தார்; அத்துடன், இறந்துபோனவர்கள் குறித்து அவர்கள் கவலை கொண்டிருக்கிறார்கள் என்பதையும் எடுத்துரைத்தார்.</p><p>நல்ல செய்தி அறிந்து மகிழ்ந்த பவுல் தம் மகிழ்ச்சியை வெளிப்படுத்தவும் தெசலோனிக்கரின் தவறான கண்ணோட்டங்களைக் களையவும் விரும்பிக் கொரிந்திலிருந்து இத்திருமுகத்தை எழுதினார்.</p><h2>உள்ளடக்கம்</h2><p>இத்திருமுகத்தில் பவுல் தெசலோனிக்க மக்களுடன் கொண்டிருந்த உறவு, அவர்கள்மேல் கொண்டிருந்த அக்கறை ஆகியவற்றை வெளிப்படுத்துகிறார்; அங்குள்ள கிறிஸ்தவர்களை ஊக்கப்படுத்துகிறார்; அவர்கள் கிறிஸ்துவிடம் கொண்டிருந்த அன்புக்காகவும் நம்பிக்கைக்காகவும் நன்றி கூறுகிறார்; அவர்களுடன் இருந்தபோது அவர் வாழ்ந்த வாழ்வை நினைவூட்டுகிறார்; கிறிஸ்துவின் வருகை குறித்த ஐயப்பாட்டிற்கு விடையளிக்கிறார்; ‘கிறிஸ்துவின் வருகைக்குமுன் இறந்தவர்கள், கிறிஸ்து வரும்போது நிலைவாழ்வில் பங்குபெறுவார்களா? எப்போது கிறிஸ்து வருவார்?’ போன்ற கேள்விகளுக்குப் பதில் சொல்லும் நேரத்தில் கிறிஸ்துவின் வருகையை நம்பிக்கையுடன் எதிர்பார்த்து அமைதியாக உழைக்கப் பணிக்கிறார். கிறிஸ்து திரும்ப வருதல் எந்த நாளில் நிகழும் எனத் தெரியாததால், அவரது வருகைக்காக எப்போதும் தயாராயிருக்குமாறு வேண்டுகிறார்.</p><h2>அமைப்பு</h2><ol><li>முன்னுரை ( வாழ்த்தும், தெசலோனிக்கரின் நம்பிக்கையும் முன்மாதிரியும்) 1:1 - 10</li><li>தெசலோனிக்காவில் பவுல் ஆற்றிய பணி 2:1 - 16</li><li>திருமுகம் எழுதப்பட்ட சூழ்நிலை 2:17 - 3:13</li><li>கடவுளுக்கு உகந்த வாழ்க்கை 4:1 - 12</li><li>ஆண்டவரின் வருகை 4:13 - 5:11</li><li>பொது அறிவுரைகள் 5:12 - 22</li><li>முடிவுரை 5:23 - 28</li></ol>'),
(62, '2 Thessalonians', 'தெசலோனிக்கருக்கு எழுதிய இரண்டாம் திருமுகம்', '2 தெசலோனிக்கர்', '2 தெச', '', '<h2>ஆசிரியர்</h2><p>இத்திருமுகத்தைப் பவுல் எழுதினாரா, வேறொருவர் எழுதினாரா என்பது பற்றிக் கருத்து வேறுபாடு உள்ளது. பவுல் இதனை எழுதவில்லை என்பதற்குச் சான்றுகள் உள்ளது போலவே, அவரே எழுதினார் என்பதற்கும் சான்றுகள் உள்ளன. எனினும் அகச் சான்றுகளை வைத்துப் பார்க்கும்போது, பவுல் இதனை நேரடியாக எழுதியிருக்க முடியாது என்று சொல்லத் தோன்றுகிறது. பவுலின் உள்ளக் கிடக்கையை அறிந்து கொண்டு, அவருடைய உணர்வுகளைப் புரிந்துகொண்டு, அவர் கருத்துக்களில் ஊன்றி நின்று, திருமுகம் எழுதப்பட்ட காலக்கட்டத்தில் இரண்டாம் வருகைபற்றிப் பவுல் என்ன கூறியிருப்பார் என்பதை அவருடைய சீடர் ஒருவர் பவுல் பெயரால் திருமுகமாக எழுதியுள்ளார் எனக் கருத இடமிருக்கிறது. இவ்வாறு எழுதுவது அந்தக் காலத்தில் முறையாகக் கருதப்பட்டது.</p><h2>சூழலும் நோக்கமும்</h2><p>தெசலோனிக்கருக்கு எழுதிய முதலாம் திருமுகம் பல ஐயப்பாடுகளை உருவாக்கிற்று. அவை குறிப்பாக இயேசு கிறிஸ்து மீண்டும் வருதலைப் பற்றியனவாக இருந்தன. முதல் திருமுகத்தில் ஆறுமுறை கிறிஸ்துவின் வருகை பற்றிப் பேசப்பட்டிருந்தது. அது விரைவில் நிகழும் என அத்திருமுகத்தில் சொல்லப்பட்டிருந்தது. இந்தப் பின்னணியில் இறுதி வருகை ஏற்கெனவே நிகழ்ந்துவிட்டதென்று சிலர் நினைத்தனர்; வேறு சிலர் அது நெருங்கி வந்து விட்டது எனக் கருதிச் சோம்பித் திரிந்தனர். இக்கருத்துக்களை மாற்றிட இரண்டாம் திருமுகம் தெசலோனிக்கருக்கு எழுதப்பட்டது.</p><p>இது எழுதப்பட்ட காலத்தைக் குறிப்பிட்டுச் சொல்வது கடினம். கி.பி. 52ஆம் ஆண்டுக்கும் 100ஆம் ஆண்டுக்கும் இடைப்பட்ட காலத்தில் இது எழுதப்பட்டிருக்க வேண்டும்.</p><h2>உள்ளடக்கம்</h2><p>இறுதி வருகை ஏற்கெனவே நிகழ்ந்துவிட்டதென்ற அவர்கள் எண்ணத்தை மாற்ற, ஆண்டவரின் இறுதி வருகை நிகழுமுன் கிளர்ச்சி ஏற்படும் என்றும், நெறிகெட்ட மனிதன் தோன்றுவான் என்றும், அவன் கிறிஸ்துவுக்கு எதிராய் இருப்பான் என்றும் திருமுக ஆசிரியர் கூறுகிறார்.</p><p>இவ்வருகையை முன்னிட்டுத் தெசலோனிக்காவில் பலர் வேலை செய்யாமல் சோம்பித் திரிந்தனர். அவர்கள் நிலையாய் இருந்து, தாங்கள் கற்றுக்கொண்ட உண்மைகளின்படி வாழப் பணிக்கிறார் ஆசிரியர்; வேலை செய்யாது சோம்பித் திரிவோர் உழைத்து உண்ணுமாறு கட்டளை இடுகின்றார்.</p><h2>அமைப்பு</h2><ol><li>முன்னுரை (வாழ்த்து) 1:1 - 2</li><li>புகழாரம் 1:3 - 12</li><li>கிறிஸ்துவின் வருகை குறித்து அறிவுரை 2:1 - 17</li><li>கிறிஸ்தவ வாழ்வுக்குப் பரிந்துரை 3:1 - 15</li><li>முடிவுரை (இறுதி வாழ்த்து) 3:16 - 18</li></ol>'),
(63, '1 Timothy', 'திமொத்தேயுவுக்கு எழுதிய முதல் திருமுகம்', '1 திமொத்தேயு', '1 திமொ', '', '<p>திமொத்தேயுவுக்கு எழுதப்பட்ட இரண்டு திருமுகங்களும், தீத்துவுக்கு எழுதப்பட்ட திருமுகமும் “ஆயர் பணித் திருமுகங்கள்” என வழங்கப் பெறுகின்றன. ஆயர்களான திமொத்தேயுவுக்கும் தீத்துவுக்கும் ஆயர் பணி பற்றி எழுதப்பட்டுள்ளதால் இவை இவ்வாறு பெயர் பெறுகின்றன.</p><p>இவற்றைப் பவுலே நேரடியாக எழுதினாரா என்பது பற்றி ஐயப்பாடு உள்ளது. இத்திருமுகங்களில் காணப்படும் சொற்கள், மொழி நடை, திருச்சபை அமைப்புமுறை ஆகியவற்றின் அடிப்படையில் பார்க்கும்போது, இவற்றைப் பவுலே எழுதியிருப்பார் எனக் கூறுவது கடினமாய் இருக்கிறது. பவுலின் சிந்தனையில் வளர்ந்த அவருடைய சீடர்கள் திருமுகம் எழுதப்பட்ட காலக்கட்டத்தில் அவர் என்ன கூறியிருப்பார் என்பதை உணர்ந்து, அவர் பெயரால் இத்திருமுகங்களை எழுதியிருப்பார்கள் எனக் கருத இடமிருக்கிறது. இவ்வாறு எழுதுவது அக்காலத்தில் முறையானதாகக் கருதப்பட்டது. முதல் நூற்றாண்டின் இறுதியிலோ இரண்டாம் நூற்றாண்டின் தொடக்கத்திலோ இவை எழுதப்பட்டிருக்கலாம்.</p><h2>சூழலும் நோக்கமும்</h2><p>பவுலுடைய உடன் உழைப்பாளர் திமொத்தேயு லிஸ்திராவைச் சேர்ந்தவர். தந்தை கிரேக்க மொழி பேசும் பிற இனத்தார், தாய் யூதக் கிறிஸ்தவர் (திப 16:1). பவுலின் மூலம் கிறிஸ்தவராகி, இரண்டாம் மூன்றாம் நற்செய்திப் பயணங்களில் உடன் பணியாளராய்த் திமொத்தேயு செயல்பட்டார் (திப 16:3; 19:22). முக்கிய பணிகளுக்குப் பல வேளைகளில் பவுல் அவரைத்தான் அனுப்பினார் (திப 19:2; 1 கொரி 4:17; 1 தெச 3:2). 1 திமொ 1:3-இன்படி திமொத்தேயு எபேசிய திருச்சபையின் கண்காணிப்பாளராய் (ஆயராய்) இருந்தார் எனத் தெரிகிறது.</p><p>பவுல் எபேசு சபையைக் கண்காணிக்குமாறு திமொத்தேயுவை ஏற்படுத்திய பின் (1:3) மாசிதோனியா சென்றார். மீண்டும் தாம் எபேசுக்கு வரப்போவதில்லை என உணர்ந்த அவர், தாம் ஆயராக நியமித்த திமொத்தேயுவுக்கு அவர்தம் கடமைகளை நினைவுறுத்துவதற்காக இத்திருமுகத்தை எழுதியதாகப் பலர் கருதுகின்றனர்.</p><h2>உள்ளடக்கம்</h2><p>தவறான போதனைகளை எதிர்க்குமாறு இத்திருமுக ஆசிரியர் கூறுகிறார் (1:3-7; 4:1-8; 6:3-5, 20-21). ஏனெனில் அக்காலத்தில் ஞான உணர்வுக் கொள்கை பரவத் தொடங்கியிருந்தது. உலகம் கெட்டது என்றும், ஒருவர் மீட்புப்பெற வேண்டுமென்றால் மறைவான அறிவு பெறவேண்டும் என்றும், பலவகை உணவுகளைத் தவிர்க்க வேண்டும் என்றும், திருமணம் புரியலாகாது என்றும் இக்கொள்கை கூறியது.</p><p>எபேசு திருச்சபையின் வழிபாடு, நிர்வாகம் ஆகியவற்றைச் செவ்வனே கவனிக்குமாறு திருமுக ஆசிரியர் பணிக்கிறார் (2:1-15); தகுதியான தலைவர்களை நியமிக்குமாறும் அறிவுரை கூறுகிறார் (3:1-13; 5:17-25).</p><h2>அமைப்பு</h2><ol><li>முன்னுரை 1:1 - 2</li><li>நலமான போதனை 1:3 - 20</li><li>வழிபாடு, தலைமைப்பணி பற்றிய அறிவுரைகள் 2:1 - 3:16</li><li>திமொத்தேயுவின் பணி பற்றிய அறிவுரைகள் 4:1 - 6:19</li><li>இறுதிப் பரிந்துரையும் எச்சரிக்கையும் 6:20 - 21</li></ol>'),
(64, '2 Timothy', 'திமொத்தேயுவுக்கு எழுதிய இரண்டாம் திருமுகம்', '2 திமொத்தேயு', '2 திமொ', '', '<h2>சூழலும் நோக்கமும்</h2><p>பவுல் தம் வாழ்வின் இறுதிக் கட்டத்தில் மீண்டும் ஒருமுறை சிறையில் அடைக்கப்பட்டதாகவும் அப்போது இரண்டாம் முறையாகத் திமொத்தேயுவுக்குத் திருமுகம் எழுதியதாகவும் கூறப்பட்டுள்ளது. முதல்முறை போலல்லாமல் இம்முறை அவர் ஒரு சாதாரண குற்றவாளிபோல் நடத்தப்பட்டார் (1:16; 2:9; 4:13) என்றும் குறிப்புத் தரப்பட்டுள்ளது. இத்திருமுகத்தில் ஆசிரியர், பவுலின் வாழ்வை எடுத்துக்காட்டாகக் கொண்டு பல அறிவுரைகளைத் திமொத்தேயுவுக்கு வழங்குவதைக் காண்கின்றோம்.</p><h2>உள்ளடக்கம்</h2><p>இத்திருமுகத்தில் பவுலைக் குறித்த செய்திகள் பல உள்ளன. மன உறுதியுடன் இருத்தலே இத்திருமுகத்தின் மையக் கருத்தாக அமைகிறது. திமொத்தேயு தொடர்ந்து இயேசுவுக்குச் சான்று பகரவும், நற்செய்தி மற்றும் பழைய ஏற்பாட்டின் உண்மையான போதனைகளை ஏற்றுக்கொள்ளவும், போதகர், நற்செய்தியாளர் என்னும் முறையில் தம் கடமைகளைச் செவ்வனே செய்யவும் திருமுக ஆசிரியர் வலியுறுத்துகிறார்; துன்பங்கள் நடுவிலும் எதிர்ப்புகள் நடுவிலும் முன்மாதிரியாய் வாழ்ந்து காட்டப் பணிக்கிறார். பயனற்ற வீண் விவாதங்களில் திமொத்தேயு ஈடுபடலாகாது என அவர் அறிவுறுத்துகிறார்.</p><h2>அமைப்பு</h2><ol><li>முன்னுரை (வாழ்த்து) 1:1 - 2</li><li>திமொத்தேயுவுக்கு அறிவுரை 1:3 - 2:13</li><li>தவறான போதனை குறித்து அறிவுரை 2:14 - 4:8</li><li>பவுலின் நிலைமை 4:9 - 18</li><li>முடிவுரை 4:19 - 22</li></ol>'),
(65, 'Titus', 'தீத்துக்கு எழுதிய திருமுகம்', 'தீத்து', 'தீத்', 'தீத்துஸ்', '<p>ஆயர்பணித் திருமுகங்களில் மூன்றாவதாக வருவது தீத்துக்கு எழுதிய இந்தத் திருமுகம். விவிலிய வரிசையில் மூன்றாவதாக வந்தாலும் இதுவே முதலில் எழுதப்பட்டிருக்க வேண்டும் எனத் தெரிகிறது. இந்நூல்களைத் தொகுத்தவர்கள் அவை எழுதப்பட்ட காலத்தை அடிப்படையாகக் கொண்டு அல்ல, அவற்றின் அளவை அடிப்படையாகக் கொண்டே வரிசைப்படுத்தினார்கள். தீத்து என்பவர் பிற இனத்துக் கிறிஸ்தவர். அவர் பவுலுடன் அந்தியோக்கியாவிலிருந்து எருசலேம் சென்றார் (கலா 2:1; திப 15:2); பவுலின் மூன்றாம் பயணத்தின்போது உடன் சென்றார் (2 கொரி 1:13; 7:6; 13:4).</p><p>அவர் கிரேத்துத் தீவின் ஆயராக விளங்கும்போது இத்திருமுகம் எழுதப்பட்டதாகச் சொல்லப்பட்டுள்ளது. அங்கே மூப்பர்கள், ஆயர்களை நியமிக்கும் பொறுப்பு இவரிடம் இருந்தது.</p><h2>உள்ளடக்கம்</h2><p>கிரேத்துத் தீவில் திருச்சபை வளர்ச்சி குன்றிய சூழ்நிலையில் தீத்து சரியான உதவியாளர்களைத் தேர்ந்தெடுக்க வேண்டும் எனப் பணிக்கிறார் இத்திருமுக ஆசிரியர் (1:5-16); கிரேத்துச் சபையிலுள்ள முதியவர், இளைஞர், அடிமைகள் ஆகியோருக்கு எப்படிப் போதிப்பது என எடுத்துக் காட்டுகிறார்; இறுதியாகக் கிறிஸ்தவ நல்லொழுக்கத்துடன் அமைதியுடனும் அன்புடனும் இருத்தல், பகைமை, வாக்குவாதம், பிளவுகள் ஆகியவற்றைத் தவிர்த்தல் ஆகியவை குறித்துப் பேசுகிறார்; கிரேத்து மக்கள் தங்கள் வாழ்வை மாற்றிக்கொள்ளக் கிறிஸ்துவின் மீட்பளிக்கும் பலியே முன் மாதிரி என்கிறார்.</p><h2>அமைப்பு</h2><ol><li>முன்னுரை (வாழ்த்து) 1:1 - 4</li><li>கிரேத்துவில் தீத்துவின் பணி 1:5 - 16</li><li>திருச்சபையில் பல்வேறு குழுக்களின் கடமைகள் 2:1 - 15</li><li>அறிவுரைகளும் எச்சரிக்கைகளும் 3:1 - 11</li><li>முடிவுரை 3:12 - 15</li></ol>'),
(66, 'Philemon', 'பிலமோனுக்கு எழுதிய திருமுகம்', 'பிலமோன்', 'பில', '', '<p>புதிய ஏற்பாட்டுத் திருமுகங்களுள் பரிந்துரை மடலாக விளங்குவது பிலமோனுக்கு எழுதப்பட்ட திருமுகமாகும். அழகிய சொல்லாட்சியுடன் நளினமான முறையில் பவுல் இச்சிறு மடலை எழுதியுள்ளார். கிறிஸ்தவ அன்பு மன்னிப்பு மிகுந்ததாய், தீமை செய்தோரையும் அன்புடன் ஏற்றுக் கொள்வதாய் அமைய வேண்டும் எனும் உயரிய கருத்தை இத்திருமுகம் எடுத்துக் கூறுகிறது.</p><p>இத்திருமுகம் நேர்த்தியான முறையில் வரையப்பட்டுள்ளது. பழங்கால உரோமை, கிரேக்க இலக்கிய முறையில் இக்கடிதத்தில் வரும் பரிந்துரை அமைந்துள்ளது.</p><h2>சூழலும் நோக்கமும்</h2><p>கொலோசை நகரிலிருந்த முக்கிய கிறிஸ்தவரும் செல்வருமான பிலமோன் என்பவரிடம் ஒனேசிம் என்பவர் அடிமைத் தொழில் செய்து வந்தார். ஒரு குறிப்பிட்ட சூழ்நிலையில் அவர் தலைவரிடமிருந்து தப்பியோடிவிட்டார் (18). இதற்கு உரோமைச் சட்டப்படி மரணதண்டனை கொடுக்கலாம். ஆனால் ஒனேசிம் தம் தலைவரின் நண்பரான தூய பவுலை நாடி வந்தார்; சிறிது காலம் அவரோடு இருந்து கிறிஸ்தவராகிய பின்னர் தம் தலைவரிடம் திரும்பிச் செல்ல விழைந்தார். அப்போது பவுல், பிலமோன் ஒனேசிமுவை மன்னித்து ஏற்றுக்கொள்ள வேண்டும்; ஓர் அடிமையாக அல்ல, ஒரு சகோதரக் கிறிஸ்தவராகவே ஏற்றுக்கொள்ள வேண்டும் எனக் கோரி இப்பரிந்துரைக் கடிதத்தை எழுதி அனுப்புகிறார்.</p><p>இக்கடிதத்தைப் பவுல் கி.பி. 61ஆம் ஆண்டு உரோமைச் சிறையிலிருந்து எழுதினார் என்பது மரபுக் கருத்து. எனினும் இதனை அவர் தம் மூன்றாம் நற்செய்திப் பயணத்தின்போது எபேசிலிருந்து எழுதியிருக்க வேண்டும் என்றே பெரும்பான்மையான அறிஞர்கள் கருதுகின்றனர்.</p><h2>அமைப்பு</h2><ol><li>முன்னுரை (வாழ்த்து, பிலமோனின் அன்பும் நம்பிக்கையும்) வச.1 - 7</li><li>ஒனேசிமுக்காக வேண்டுகோள் வச. 8 - 21</li><li>முடிவுரை வச. 22 - 25</li></ol>'),
(67, 'Hebrews', 'எபிரேயருக்கு எழுதிய திருமுகம்', 'எபிரேயர்', 'எபி', '', '<p>பவுலுடன் தொடர்புப்படுத்தப்படும் இறுதியான திருமுகம் இந்த எபிரேயர் திருமுகம். இது ஒரு திருமுகம் என வழங்கப்பட்டாலும், இதில் திருமுக அமைப்பு இல்லை; மாறாக ஓர் இறையியல் கட்டுரையாகவே இது அமைந்துள்ளது. நூலின் இறுதியில் மட்டும் வாசகர்களுக்கு அறிவுரைகள் தரப்பட்டுள்ளன.</p><h2>ஆசிரியர்</h2><p>எபிரேயருக்கு எழுதிய திருமுகத்திற்கும் பவுலின் திருமுகங்களுக்கும் இடையே ஒற்றுமைகள் இருப்பினும் வேற்றுமைகள் மிகுதியாக இருப்பதால் இத்திருமுகத்தைப் பவுல் எழுதியிருக்க இயலாது என அனைவரும் இப்போது ஒருமனதாக ஏற்றுக்கொள்கின்றனர். அப்பொல்லோ எழுதியிருக்கலாம் என்பர் சிலர். இருப்பினும் யாரால் எழுதப்பட்டது என்னும் கேள்விக்குத் தெளிவான விடை காண இயலவில்லை.</p><h2>சூழலும் நோக்கமும்</h2><p>இத்திருமுகம் யூதக் கிறிஸ்தவர்களுக்கு எழுதப்பட்டதாகும். அவர்கள் பழைய ஏற்பாட்டில் தோய்ந்தவர்கள்; தங்கள் நம்பிக்கையில் தளர்ச்சி ஏற்பட்டு, நம்பிக்கையை இழந்துவிடக்கூடிய ஆபத்தில் இருந்தவர்கள். அவர்கள் கிறிஸ்தவ நம்பிக்கையில் உறுதியாய் நில்லாமல் மீண்டும் யூதச் சமயத்திற்குத் திரும்ப நினைத்தார்கள்; தாங்கள் பெற்றுக்கொண்ட நற்செய்தியை யூதமயமாக்க விரும்பினார்கள் (கலா 2:14). ஒருவேளை இந்த வாசகர்களில் பலர் கிறிஸ்துவை ஏற்றுக்கொண்ட யூதக் குருக்களாகவும் இருக்கலாம் (திப 6:7).</p><p>இத்திருமுகத்தில் எருசலேம் கோவில் மற்றும் அதன் வழிபாடுகள் நிகழ்காலத்தில் விரிவாகத் தரப்படுவதால் கி.பி. 70இல் நடந்த எருசலேம் கோவில் அழிவுக்கு முன்னர் இது எழுதப்பட்டது என்பர் சிலர்.</p><p>ஆயினும் இக்கருத்தைப் பலர் ஏற்றுக்கொள்வதில்லை. ஏனெனில் கோவிலும் அதன் வழிபாடுகளும் கிறிஸ்துவின் செயல்களுக்கும் தன்மைக்கும் முன் அடையாளமாக மட்டுமே தரப்படுகின்றன. தன்னிலேயே அவற்றை விளக்குவது ஆசிரியரின் நோக்கமல்ல. திருமுகம் கி.பி. 80ஆம் ஆண்டிலிருந்து 85ஆம் ஆண்டுக்குள் எழுதப்பட்டிருக்க வேண்டும் என்றே எல்லாரும் நம்புகின்றனர்.</p><h2>உள்ளடக்கம்</h2><p>கிறிஸ்துவின் மேன்மையே இந்நூலின் மையக் கருத்தாகும். முன்னுரையில் கிறிஸ்து முழுமையான, முடிவான வெளிப்பாட்டைத் தருகிறார் எனக் கூறப்பட்டுள்ளது. இறைவாக்கினருக்கும் வானதூதருக்கும் பழைய உடன்படிக்கையின் இணைப்பாளரான மோசேக்கும் மேலானவராகக் கிறிஸ்து காட்டப்படுகிறார். கிறிஸ்துவின் குருத்துவம் பழைய ஏற்பாட்டுக் குருத்துவத்தினின்று முற்றிலும் மாறுபட்டதும் அதற்கு மேம்பட்டதுமாகும் எனச் சுட்டிக் காட்டப்படுகிறது. ஏனெனில் கிறிஸ்துவின் குருத்துவம் பழைய ஏற்பாட்டின் வாக்குறுதியை நிறைவு செய்கிறது (8:1-13); கிறிஸ்துவின் பலியும் எக்காலத்துக்கும் உரிய ஒரே பலியாய் விளங்கி, பழைய ஏற்பாட்டுப் பலிகளை நிறைவுசெய்கிறது; கிறிஸ்துவின் சாவு, உயிர்பெற்றெழுதல், விண்ணேற்றம் ஆகியன விண்ணகத் தூயகத்தை நமக்குத் திறந்து வைத்துள்ளன என்னும் கருத்துகள் வலியுறுத்தப்படுகின்றன.</p><p>இத்தகைய காரணங்களினால், கிறிஸ்துவின் மீது நம்பிக்கை கொண்டோர் கிறிஸ்துவை விட்டு மீண்டும் யூத முறைக்குத் திரும்பலாகாது என்று கேட்டுக் கொள்கிறார் ஆசிரியர்; அவ்வாறு செய்தால் பாலைவனத்தில் கிளர்ச்சி செய்த இஸ்ரயேலர் போல் இவர்களும் தண்டிக்கப்படுவார்கள் என எச்சரிக்கை விடுக்கிறார்.</p><h2>அமைப்பு</h2><ol><li>முன்னுரை: கடவுள் தம் மகன் மூலமாக நம்மிடம் பேசியுள்ளார். 1:1 - 3</li><li>கடவுளின் மகன் வானதூதரைவிட மேலானவர் 1:4 - 2:18</li><li>இயேசு கிறிஸ்து மோசேக்கும் யோசுவாவுக்கும் மேலானவர் 3:1 - 4:3</li><li>இயேசு கிறிஸ்துவின் குருத்துவத்தின் மேன்மை 4:14 - 7:28</li><li>இயேசு கிறிஸ்துவின் உடன்படிக்கையின் மேன்மை 8:1 - 9:28</li><li>இயேசு கிறிஸ்துவின் பலியின் மேன்மை 10:1 - 39</li><li>நம்பிக்கையின் மேன்மை 11:1 - 12:29</li><li>கடவுளுக்கு உகந்த வாழ்க்கை 13:1 - 19</li><li>இறுதி வாழ்த்துரை 13:20 - 21</li><li>முடிவுரை 13:22 - 25</li></ol>'),
(68, 'James', 'யாக்கோபு எழுதிய திருமுகம்', 'யாக்கோபு', 'யாக்', 'யாகப்பர்', '<p>இத்திருமுகம் உலகெங்கும் சிதறுண்டு வாழும் இஸ்ரயேலின் பன்னிரு குலத்தினருக்கும் எழுதப்பட்டுள்ளது. அவர்கள் யூதக் கிறிஸ்தவர்கள். ஆயினும் திருமுகம் தரும் போதனை யூதக் கிறிஸ்தவர்களுக்கும் பிற இனத்துக் கிறிஸ்தவர்களுக்கும் பொருந்தவே அமைந்துள்ளது.</p><p>இத்திருமுகம் கிறிஸ்தவ நடைமுறை வாழ்வுக்கான அறிவுரை எனினும், பழைய ஏற்பாட்டின் ஞானநூல்களின் தன்மையைக் கொண்டுள்ளது. இது எழுதப்பட்ட காலத்தைக் கணிப்பது கடினம். வெவ்வேறு விவிலிய அறிஞர்கள் கி.பி. 50லிருந்து 100 வரையுள்ள வெவ்வேறு காலக் கணிப்புகளைக் கொடுக்கிறார்கள்.</p><h2>ஆசிரியர்</h2><p>யாக்கோபு திருமுகத்தின் ஆசிரியர் தம்மைப் பற்றிக் “கடவுளுக்கும் ஆண்டவராகிய இயேசு கிறிஸ்துவுக்கும் பணியாளனாகிய யாக்கோபு” என்று குறிப்பிடுகிறார் (1:11). மரபுக் கருத்துப்படி யாக்கோபு ஆண்டவரின் சகோதரர்; எருசலேம் திருச்சபையின் தலைவர்; பவுலுடன் தொடர்பு கொண்டிருந்தவர் (கலா 1:19; 2:9,12; திப 15:13).</p><p>ஆனால் இக்கருத்துக்களைப் பல அறிஞர்கள் ஏற்றுக்கொள்ளத் தயங்குகிறார்கள்; ஏனெனில் கடிதத்தின் நடை, கருத்துக்கள், இயேசுவோடுள்ள நெருக்கமான உறவு பற்றிய குறிப்பின்மை போன்றவற்றின் அடிப்படையில் இத்திருமுகத்தின் ஆசிரியர் வேறு ஒரு யாக்கோபாக இருக்கலாம் என எண்ணுகிறார்கள்.</p><h2>உள்ளடக்கம்</h2><p>இத்திருமுகத்தின் முக்கியக் கருத்து கிறிஸ்தவர் ஒருவர் தம் அன்றாடக் கடமைகளைப் பிழையின்றிச் செய்து முடிக்க வேண்டும் என்பதாகும். உண்மையான கிறிஸ்தவ வாழ்வு தன்னல மறுப்பையும் தூய வாழ்வையும் உள்ளடக்கியது (1:1-27). நம்பிக்கை, பிறரன்பு போன்றவை செயல்களில் காட்டப்பட வேண்டும். செயலற்ற நம்பிக்கை செத்த நம்பிக்கை (2:1-26). நாவை அடக்குதல், அமைதி வாழ்விற்கான ஆர்வம் ஆகியவை ஒருவரை ஞானியாகவும், தூயவராகவும் மாற்றுகின்றன (3:1-18). பாவம் பிளவின் காரணம். திருமுகத்தின் இறுதியில் அனைவருக்கும் எழுச்சியுரை தரப்படுகிறது (4-5).</p><h2>அமைப்பு</h2><ol><li>முன்னுரை (வாழ்த்து) 1:1</li><li>சோதனைகள் 1:2 - 18</li><li>அறிவுரையும் எச்சரிக்கைகளும் 1:19 - 5:20</li></ol>'),
(69, '1 Peter', 'பேதுரு எழுதிய முதல் திருமுகம்', '1 பேதுரு', '1 பேது', 'இராயப்பர் எழுதிய முதல் திருமுகம்', '<p>இத்திருமுகம் சின்ன ஆசியாவிலுள்ள கிறிஸ்தவ சபைகளுக்கு எழுதப்பட்டது என முதல் வசனத்திலிருந்து அறிகிறோம். அங்கு யூதக் கிறிஸ்தவர்களும் பிற இனத்துக் கிறிஸ்தவர்களும் இருந்திருக்க வேண்டும். வாசகர்களுள் பெரும்பாலோர் ஏழைகளாக இருந்திருக்க வேண்டும். அவர்களிடையே அடிமைகளும் இருந்திருக்க வேண்டும்.</p><h2>ஆசிரியர்</h2><p>திருச்சபைத் தந்தையர் காலத்திலிருந்தே இத்திருமுகம் திருத்தூதரான பேதுருவால் எழுதப்பட்டது என ஏற்கப்பட்டு வந்தது. ஆசிரியரும் தம்மைப்பற்றி “இயேசு கிறிஸ்துவின் திருத்தூதனான பேதுரு (1:2)” என எழுதுகிறார். எனினும் இதனைப் பேதுருவே நேரடியாக எழுதியிருப்பாரா என்னும் கேள்வி எழுகிறது. இத்திருமுகம் உயர்ந்த கிரேக்க நடையில் அமைந்துள்ளது; பவுலின் கருத்துகள் பல இதில் பிரதிபலிக்கின்றன. மேலும் திருமுகம் குறிப்பிடுவது போன்ற பெரிய துன்புறுத்தல் பேதுரு வாழ்ந்தபோது இல்லை. இத்திருமுகம் அனுப்பப்பெற்ற இடங்களில் (1:1) சிலவற்றிலாவது பேதுரு இறக்குமுன்னே (கி.பி. 64) திருச்சபை தோன்றியிருந்ததா என்பதே ஒரு கேள்விக் குறியாக உள்ளது. எனவே மாறிவிட்ட ஒரு காலக்கட்டத்தில் பேதுரு என்ன சொல்லியிருப்பார் என்பதை, அவருடைய சீடர் ஒருவர் அவர் பெயரில் திருமுகமாக எழுதியிருக்கலாம் என அறிஞர் பலர் கருதுகின்றனர். இவ்வாறு இத்திருமுகம் கி.பி. 70-90 ஆண்டுகளில் எழுதப்பட்டிருக்கலாம்.</p><h2>சூழலும் நோக்கமும்</h2><p>இத்திருமுகம் எழுத ஏதாவது குறிப்பிட்ட சூழ்நிலை இருந்ததா எனத் தெரியவில்லை. எனினும் துன்புறுத்தப்பட்ட காலத்திலும் சோதனைக் காலத்திலும் கிறிஸ்தவர்களை ஊக்கப்படுத்தவும், தளர்ந்துபோன நம்பிக்கையை மீண்டும் கட்டியெழுப்பவும் இத்திருமுகத்தை எழுதியுள்ளார் எனலாம் (4:12-13).</p><h2>உள்ளடக்கம்</h2><p>இத்திருமுகத்தில் கிறிஸ்தவ வாழ்வின் கடமைகள், உண்மையான வாழ்வு, சகோதர அன்பு ஆகியவை வலியுறுத்தப்படுகின்றன (1:1-2:10). கிறிஸ்தவர்கள் பிற இனத்தாருக்கும் அரசு அதிகாரிகளுக்கும், அடிமைகள் தலைவர்களுக்கும், கணவர்கள் மனைவியருக்கும், மனைவியர் கணவர்களுக்கும் செய்ய வேண்டிய கடமைகளை ஆசிரியர் நினைவூட்டுகிறார் (2:11-4:6); கிறிஸ்துவின் இறுதி வருகைபற்றிப் பேசி, விழிப்புணர்வு தேவை என வலியுறுத்துகிறார் (4:7-5:11).</p><h2>அமைப்பு</h2><ol><li>முன்னுரை (வாழ்த்து) 1:1 - 2</li><li>கிறிஸ்தவ அழைப்பும் பொறுப்பும் 1:3 - 2:10</li><li>குடும்பத்திலும் சமூகத்திலும் கிறிஸ்தவ நடத்தை 2:11 - 4:11</li><li>துன்புறுத்தப்படுவோருக்கு அறிவுரை 4:12 - 5:11</li><li>முடிவுரை (இறுதி வாழ்த்து) 5:12 - 4</li></ol>'),
(70, '2 Peter', 'பேதுரு எழுதிய இரண்டாம் திருமுகம்', '2 பேதுரு', '2 பேது', 'இராயப்பர் எழுதிய இரண்டாம் திருமுகம்', '<p>பேதுருவின் முதல் திருமுகம்போல் இதுவும் திருமுகப்பாணியில் அமைந்த மறையுரையே. யூதா திருமுகத்தோடு இது நெருங்கிய தொடர்புடையது. அதனை அடிப்படையாகக்கொண்டு இத்திருமுகம் வரையப்பட்டிருக்கலாம். பிற்கால மடலாக இருப்பதால் இது கிறிஸ்துவைப் பற்றிய வளர்ச்சியடைந்த கிறிஸ்தியல் கருத்துகளைக் குறிப்பிடுகிறது.</p><h2>ஆசிரியர்</h2><p>இத்திருமுக ஆசிரியர் தம்மை “இயேசு கிறிஸ்துவின் பணியாளனும் திருத்தூதனுமான சீமோன் பேதுரு” என எழுதினாலும் பேதுருதான் இத்திருமுகத்தை எழுதினார் என்பது பற்றி ஐயப்பாடு உள்ளது. ஏனெனில் பேதுரு வாழ்ந்த போது இறுதி வருகை பற்றிப் பெரும் ஐயப்பாடு எதுவும் எழவில்லை. இது பிற்காலச் சூழ்நிலை ஆகும் (3:2-4). எனவே, பேதுருவின் வாரிசுகளில் ஒருவர் அவர் பாணியில் இத்திருமுகத்தை அவர் பெயரில் எழுதியிருக்க வேண்டும்.</p><h2>சூழலும் நோக்கமும்</h2><p>இத்திருமுகத்தின் சூழலும் நோக்கமும் தெளிவாக இல்லை. யாருக்கு எழுதப்பட்டது என்றும் திட்டவட்டமாகக் கூறமுடியவில்லை. அக்காலத்தில் பல போலிப் போதகர்கள் தோன்றி உண்மையைத் திரித்து மக்களைத் தவறான நெறியில் வாழத் தூண்டினர் (2:2). இச்சூழ்நிலை இக்கடிதத்தை எழுத வைத்திருக்கலாம்.</p><h2>உள்ளடக்கம்</h2><p>வாசகர்கள் கடவுளைப் பற்றியும் இயேசு கிறிஸ்துவைப் பற்றியும் உண்மையான அறிவைப் பெறவேண்டும். இந்த அறிவு இயேசுவை நேரில் பார்த்தும் அவர் போதனைகளைக் கேட்டும் இருந்த திருத்தூதர்களால் தரப்பட்டுள்ளது.</p><p>கிறிஸ்து இனி வரமாட்டார் எனச் சிலர் பேசிவந்ததால் அவர் வருவது உறுதி என்பதை மீண்டும் திருமுக ஆசிரியர் வலியுறுத்துகிறார்; எவரும் அழிந்து விடாமல், எல்லாரும் மீட்கப்பட வேண்டும் என்பதற்காகவே கிறிஸ்து வரக் காலம் தாழ்த்துகிறார் என்கிறார்.</p><h2>அமைப்பு</h2><ol><li>முன்னுரை (வாழ்த்து) 1:1 - 2</li><li>கிறிஸ்தவ அழைப்பு 1:3 - 21</li><li>போலி இறைவாக்கினர்களும் போலிப் போதகர்களும் 2:1 - 22</li><li>ஆண்டவருடைய வருகை 3:1 - 18</li></ol>'),
(71, '1 John', 'யோவான் எழுதிய முதல் திருமுகம்', '1 யோவான்', '1 யோவா', 'அருளப்பர் எழுதிய முதல் திருமுகம்', '<p>இத்திருமுகம் பொதுத்திருமுகங்களில் ஒன்றாக இருப்பினும் வழக்கமான வாழ்த்து, வாசகர் பற்றிய குறிப்பு எதுவும் இதில் இல்லை. எனினும் இதன் வாசகர்கள் ஒரு குறிப்பிட்ட குழுவினராக அல்லது சபையினராக இருந்திருக்க வேண்டும். அங்கு முதியோர் இளையோர் அனைவரும் இருந்ததாகக் குறிப்பிடப்பட்டுள்ளது. இவர்களை நம்பிக்கையில் முதியோராகவும், இளையோராகவும்கூடக் கருதலாம்.</p><h2>ஆசிரியர்</h2><p>யோவான் நற்செய்திக்கும் இத்திருமுகத்திற்கும் இடையே மிகுந்த ஒற்றுமை காணப்படுகிறது. மொழி நடை, கலைச் சொற்கள், கருத்துக்கள் ஆகியவற்றில் இந்த ஒற்றுமை குறிப்பாகக் காணப்படுகிறது. இரண்டிற்கும் இடையே உள்ள ஒரு சில வேறுபாடுகள் எளிதில் விளக்கக்கூடியவையாய் இருக்கின்றன. எனவே இரண்டும் யோவான் சமூகத்திலிருந்தே தோன்றியிருக்க வேண்டும். திருத்தூதர் யோவானே நேரடியாக இதனை எழுதினாரா என்பது பற்றி அறுதியிட்டுக் கூறமுடியாது. இத்திருமுகம் யோவான் நற்செய்திக்கு முன்னுரையாக எழுதப்பட்டது என்பர் ஒரு சிலர். இது யோவான் நற்செய்தியில் வரும் சில கருத்துக்களுக்கு விளக்கமாக எழுதப்பட்டது என்பர் வேறு சிலர்.</p><h2>நோக்கம்</h2><p>அக்காலத்தில் எழுந்த சில தவறான கருத்துக்களை, குறிப்பாக இயேசுவைப்பற்றிய தவறான கருத்துக்களைக் களைவது இத்திருமுகத்தின் முக்கிய நோக்கங்களுள் ஒன்றாகத் தெரிகிறது. கிறிஸ்தவச் சமூகத்தின் ஆன்மீகம் மற்றும் சமூகம் பற்றிய விழிப்புணர்வை ஆழப்படுத்துவதும் (3:17) திருமுகத்தின் நோக்கமாக இருந்திருக்க வேண்டும். இச்சமூகத்தின் உறுப்பினர்கள் (2:19) இயேசுவை மெசியாவாக ஏற்றுக்கொள்ள மறுத்தார்கள் (2:22). அத்துடன் அவர் உண்மையில் மனிதராகவும் இருந்தார் என்பதையும் அவர்கள் ஏற்றுக்கொள்ளவில்லை (4:2). மீட்புக்கும் நல்லொழுக்கம், அன்பு போன்றவற்றுக்கும் தொடர்பு இல்லை என்றும் அவர்கள் கூறி வந்தார்கள். திருமுக ஆசிரியர் இப்படிப்பட்டத் தவறுகளைக் களைய முற்படுகிறார்.</p><h2>உள்ளடக்கம்</h2><p>இயேசு உண்மையில் மனிதராகவும் இருந்தார் என்று கூறி, அதை அவர்கள் நம்ப வேண்டும் என்கிறார் ஆசிரியர்; அன்புகூர்பவர்கள் சகோதர சகோதரிகளிடமும் அன்புகூர வேண்டும் என அறுதியிட்டுக் கூறுகிறார். அன்பு பற்றி மிகுதியாகப் பேசுவதால் இதை அன்புக் கடிதம் எனலாம்.</p><h2>அமைப்பு</h2><ol><li>முன்னுரை (வாழ்வு அளிக்கும் வாக்கு) 1:1 - 4</li><li>ஒளியும் இருளும் 1:5 - 2:29</li><li>கடவுளின் பிள்ளைகளும் அலகையின் பிள்ளைகளும் 3:1 - 24</li><li>உண்மையும் பொய்ம்மையும் 4:1 - 6</li><li>அன்பும் நம்பிக்கையும் 4:7 - 5:12</li><li>முடிவுரை 5:13 - 21</li></ol>'),
(72, '2 John', 'யோவான் எழுதிய இரண்டாம் திருமுகம்', '2 யோவான்', '2 யோவா', 'அருளப்பர் எழுதிய இரண்டாம் திருமுகம்', '<p>யோவான் எழுதிய இரண்டாம் திருமுகம் 13 வசனங்களை மட்டுமே கொண்ட ஒரு மிகச் சிறிய திருமுகம். இது ‘தேர்ந்தெடுக்கப்பட்ட பெருமாட்டிக்கு’ எழுதப்பட்டுள்ளது. ‘பெருமாட்டி’ என்பது ஒரு தனிப்பட்ட திருச்சபையைக் குறிப்பதாகும். இப்பெருமாட்டிக்கு ஒரு சகோதரியும் உள்ளார். இச்சகோதரி வேறொரு சபையைக் குறித்து நிற்கிறார்.</p><h2>ஆசிரியர்</h2><p>இத்திருமுகத்தின் ஆசிரியர் தாம் ஒரு மூப்பர் எனச் சொல்லிக் கொள்கிறார். கிறிஸ்தவச் சபைகளில் மூப்பர்கள் தலைமைப்பணி ஆற்றி வந்தார்கள். இம்மூப்பரது பெயர் குறிப்பிடப்படாததால், இவர் அக்காலத்தில் நன்கு அறிமுகமான ஒருவராக இருந்திருக்க வேண்டும்.</p><p>இத்திருமுகத்தை யோவான் எழுதினார் என மரபு கூறினாலும், அவர் சீடர் ஒருவர் இதனை எழுதியிருக்க வேண்டும் என அறிஞர்கள் கருதுகின்றனர். 1 யோவான் எழுதப்பட்ட காலத்திலேயே இத்திருமுகமும் எழுதப்பட்டிருக்க வேண்டும்.</p><h2>நோக்கமும் உள்ளடக்கமும்</h2><p>திருமுக ஆசிரியரான மூப்பர் திருச்சபைக்கு எச்சரிக்கை விடுக்கிறார். தவறான போதனையின் அடிப்படையில் பிரிந்து சென்றோர் கிறிஸ்து மனிதரானதை ஏற்றுக் கொள்வதில்லை; தாங்கள் முன்னேறியவர்கள் என இவர்கள் கூறிக்கொண்டாலும், அந்த முன்னேற்றம் கிறிஸ்தவக் கொள்கையின் எல்லையை மீறிய செயலாகும்; இப்படிப்பட்டவர்களைக் கிறிஸ்தவர்கள் வரவேற்கக்கூடாது என்கிறார் ஆசிரியர். ஒருவர் மற்றவருக்கு அன்பு செலுத்தி இயேசுவைப் பற்றிய வரலாற்று உண்மைகளை உறுதியாகச் சார்ந்து நிற்க வேண்டும் என அவர் அறிவுறுத்துகிறார்.</p><h2>அமைப்பு</h2><ol><li>முன்னுரை வச 1 - 3</li><li>அன்பின் மேன்மை வச 4 - 6</li><li>எதிர்க்கிறிஸ்துகள் பற்றிய எச்சரிக்கை வச 7 - 11</li><li>முடிவுரை வச 12 - 13</li></ol>'),
(73, '3 John', 'யோவான் எழுதிய மூன்றாம் திருமுகம்', '3 யோவான்', '3 யோவா', 'அருளப்பர் எழுதிய மூன்றாம் திருமுகம்', '<p>யோவான் எழுதிய மூன்றாம் திருமுகம் 15 வசனங்களை மட்டுமே கொண்ட ஒரு மிகச் சிறிய திருமுகம். இத்திருமுகம் ‘காயு’ எனப்படும் தனிநபருக்கு எழுதப்பட்டுள்ளது. இறையியல் கருத்துக்கள் இத்திருமுகத்தில் இல்லை. எனினும் தொடக்க காலத் திருச்சபை வாழ்வைப் புரிந்துகொள்ள இது பெரிதும் உதவுகிறது.</p><h2>ஆசிரியர்</h2><p>இத்திருமுகம் மூப்பர் ஒருவரால் எழுதப்பட்டது. மூப்பரின் பெயர் கொடுக்கப்படவில்லை.</p><h2>சூழல், நோக்கம், உள்ளடக்கம்</h2><p>இத்திருமுகத்தின் பெறுநர் காயு, கிறிஸ்தவ மறைப் பணியாளர்களை வரவேற்று விருந்தோம்பி வந்தார். அவர் தெமேத்திரியு என்பவரையும் ஏற்றுக்கொள்ள வேண்டும் என வேண்டுகோள் விடுக்கிறார் ஆசிரியர்.</p><p>தியோத்திரபு என்பவர் இந்த மூப்பர் அனுப்பும் கடிதத்தையோ அவர் அனுப்பும் ஆள்களையோ ஏற்றுக்கொள்வதில்லை. அவர் கொள்கையளவில் மூப்பரிடமிருந்து மாறுபட்டவராக அல்லது மூப்பரின் தலைமையை ஏற்றுக்கொள்ளாதவராக இருந்திருக்க வேண்டும்.</p><p>இத்திருமுகம் தொடக்க காலக் கிறிஸ்தவ நிலையை ஓரளவு சுட்டிக்காட்டுகிறது. தொடக்ககாலத் திருச்சபையின் அதிகார அமைப்பையும் சபைகளின் தனித்தன்மையையும் இத்திருமுகம் எடுத்துக் காட்டுகிறது. சில மூப்பர்கள் பிற சபைகள் மீதும் அதிகாரம் கொண்டு விளங்கினாலும், நாள் செல்லச் செல்ல ஒரு சபையின் மூப்பர் இன்னொரு சபையின் மீது செலுத்தும் அதிகாரம் வரவேற்கப்படவில்லை என்பதை இது காட்டுகிறது. தியோத்திரபுவின் சிக்கலைத் தாமே நேரில் வந்து தீர்க்கப்போவதாகக் கூறுகிறார் திருமுக ஆசிரியர் (வ. 10).</p><h2>அமைப்பு</h2><ol><li>முன்னுரை (வாழ்த்து) வச 1 - 4</li><li>காயுவுக்குப் பாராட்டும் பரிந்துரையும் வச 5 - 8</li><li>தியோத்திரபு கண்டிக்கப்படல் வச 9 - 10</li><li>திமேத்திரியுவுக்கு நற்சான்று வச 11 - 12</li><li>முடிவுரை வச 13 - 15</li></ol>'),
(74, 'Jude', 'யூதா எழுதிய திருமுகம்', 'யூதா', 'யூதா', '', '<p>இத்திருமுகம் அனைத்துக் கிறிஸ்தவர்களுக்கும் எழுதப்பட்டுள்ளது. இது காலத்தால் மிகவும் பிந்தியது; பேதுரு எழுதிய இரண்டாம் திருமுகத்தோடு மிகவும் தொடர்புடையது. இத்திருமுகத்தின் சில வசனங்கள் (13,14,15) ஏனோக்கு நூலிலிருந்து எடுக்கப்பட்டுள்ளன.</p><h2>ஆசிரியர்</h2><p>இத்திருமுகத்தின் ஆசிரியர் தம்மை, “இயேசு கிறிஸ்துவின் பணியாளனும் யாக்கோபின் சகோதரனுமாகிய யூதா” எனக் குறிப்பிடுகிறார். எனினும் இவர் திருத்தூதருள் ஒருவரான யூதாவாக இருக்க மிகுதியான வாய்ப்பு இல்லை. ஏனெனில் இத்திருமுகத்தில் திருத்தூதர்கள் கடந்த காலத்தவராகக் குறிப்பிடப்படுகின்றனர் (17-18); நம்பிக்கை (விசுவாசம்) உண்மைகளின் தொகுப்பாகக் காட்டப்படுகிறது. கி.பி. இரண்டாம் நூற்றாண்டோடு தொடர்புடைய ஞான உணர்வுக் கொள்கைகள் கண்டிக்கப்படுகின்றன; இத்திருமுகம் நல்ல கிரேக்க மொழி நடையில் அமைந்துள்ளது.</p><p>“இவருடைய சகோதரர் யாக்கோபு, யோசேப்பு, சீமோன் யூதா அல்லவா?” (மத் 13:15) என நற்செய்தி நூல்களில் படிக்கிறோம். இதன் அடிப்படையில் இத்திருமுக ஆசிரியர் நற்செய்தி நூல்களில் கூறப்படும் இயேசுவின் சகோதரருள் ஒருவராக இருக்கலாம் என்று ஒரு சிலர் கருதுகின்றனர். எனினும் மேலே கூறிய காரணங்களை முன்னிட்டு இக்கருத்தையும் ஏற்றுக்கொள்வது கடினம்.</p><h2>நோக்கம்</h2><p>சில சபைகளில் போலிப் போதகர்களின் தவறான போதனைகளால் ஆபத்து ஏற்பட்டிருக்கலாம். அவர்களிடமிருந்து வாசகர்களைக் காத்துக் கொள்ள ஆசிரியர் இத்திருமுகத்தை எழுதியிருக்கவேண்டும்.</p><h2>உள்ளடக்கம்</h2><p>போலிப் போதகர்கள் பற்றிப் பேசும் திருமுக ஆசிரியர், அவர்களுக்குக் கடவுளின் தண்டனை வரப்போகிறது என்கிறார்; யூத மரபின் அடிப்படையில் இதை விளக்குகிறார் (1-16). தம் வாசகர்கள் திருத்தூதர்களின் போதனைகளை உண்மையாய்க் கடைப்பிடிக்குமாறு ஆசிரியர் அறிவுரை கூறுகிறார்; அத்துடன் கிறிஸ்தவ அன்பின் கடமைகளைப் பொறுப்புடன் ஆற்றவும் பணிக்கிறார் (17-23).</p><h2>அமைப்பு</h2><ol><li>முன்னுரை (வாழ்த்து) வச. 1 - 2</li><li>போலிப் போதகர்கள் வச. 3 - 16</li><li>விசுவாசத்தைக் காக்குமாறு எச்சரிக்கையும் அறிவுரையும் வச. 17 - 23</li><li>முடிவுரை (இறையாசி) வச. 24 - 25</li></ol>'),
(75, 'Revelation', 'யோவானுக்கு அருளப்பெற்ற திருவெளிப்பாடு', 'திருவெளிப்பாடு', 'திவெ', '', '<p>புதிய ஏற்பாட்டின் இறுதி நூலாகிய திருவெளிப்பாடு வேறுபட்ட ஓர் இலக்கிய வகையைச் சார்ந்தது. இதில் அடையாளங்கள் மிகுதியாய் உள்ளன. இத்தகு இலக்கிய வகை கி.மு. 2ஆம் நூற்றாண்டையொட்டித் தோன்றி வளர்ந்ததாகத் தெரிகிறது. யூதர்கள் பிறஇனத்தாரால் துன்புறுத்தப்பட்ட காலத்தில் நம்பிக்கையும் ஊக்கமும் ஆறுதலும் ஊட்டுவதற்காகவும் அக்கால நிகழ்ச்சிகளை ஒரு குறிப்பிட்ட பார்வையில் புரிந்துகொள்ள உதவுவதற்காகவும் திருவெளிப்பாட்டு வகை நூல்கள் உருவாயின. தானியேல் நூல் இத்தன்மை கொண்டது.</p><h2>ஆசிரியர்</h2><p>இந்நூலின் ஆசிரியர் தம்மை யோவான் எனக் குறிப்பிடுகிறார் (1:1,4,9; 22:8). தாம் ஒரு திருத்தூதர் என்றோ இயேசுவின் சீடர் என்றோ அவர் தம்மைப் பற்றிக் கூறிக் கொள்ளவில்லை. மொழிநடை, இலக்கணம், இறையியல் கருத்துகள் ஆகியவற்றை ஆராய்ந்து பார்த்தால் இது நான்காம் நற்செய்தியை எழுதிய ஆசிரியரால் எழுதப்பட்டிருக்க இயலாது என்பது புலனாகும். தொடக்கக் காலத்திலிருந்தே கிறிஸ்தவ எழுத்தாளர்கள் இந்நூலின் ஆசிரியர் திருத்தூதர் யோவானே என்று கூறி வந்திருப்பது உண்மை எனினும், அகச்சான்று அடிப்படையில் பார்க்கும்போது இந்நூலாசிரியர் யோவான் என்னும் பெயருடைய தொடக்க கால மூப்பர் ஒருவராய் இருந்திருக்க வேண்டும் என்று சொல்வதே முறையாகத் தெரிகிறது.</p><h2>சூழலும் நோக்கமும்</h2><p>இந்நூல் எழுதப்பட்ட காலத்தில் கிறிஸ்தவர்கள் கொடிய துன்பங்களுக்கு இலக்காயினர். அது நீரோவின் காலம் (கி.பி. 54-68) எனச் சிலர் கூறுகின்றனர். ஆனால் தொமீசியன் காலமாக (கி.பி. 89-96) இருக்கும் எனக் கூறுவதே ஏற்புடையதாகத் தெரிகிறது. உரோமை அதிகாரிகள் அரச வணக்கத்தைப் பரப்பிய நேரத்தில் உரோமைப் பேரரசர் சீசரை வணங்க மறுத்த கிறிஸ்தவர்கள் வெறுப்புக்கும் இகழ்ச்சிக்கும் இன்னலுக்கும் உள்ளானார்கள். இந்நிலையில்தான் இதன் ஆசிரியர் பத்மு தீவிலிருந்து இதனை எழுதியிருக்க வேண்டும்.</p><h2>உள்ளடக்கம்</h2><p>வாசகர்கள் துணிச்சலோடு அரச வழிபாட்டை எதிர்க்க வேண்டும். கடவுளுக்கும் சாத்தானுக்கும் இடையிலான இறுதிப் போர் விரைவில் வர இருக்கிறது. சாத்தான் கிறிஸ்துவின் சீடர்களுக்கு எதிராகத் துன்பங்களை மிகுதிப்படுத்துவான். ஆனால், கிறிஸ்தவர்கள் சாவுவரை உறுதியோடு இருக்க வேண்டும். அவர்களுக்கு அழிவு வராது என்பது உறுதி. கிறிஸ்து இயேசு வரும்போது அவர்கள் வெற்றி பெறுவார்கள். அப்போது கொடியவர்கள் என்றென்றைக்கும் அழிக்கப்படுவார்கள். கடவுளின் மக்களோ நிலைவாழ்வு பெறுவார்கள்.</p><h2>அடையாளங்கள்</h2><p>மேற்சொல்லப்பட்ட கருத்தை நேரடியாகச் சொல்வது ஆபத்தாக முடியும். எனவே இந்நூலில் பற்பல அடையாளங்கள் பயன்படுத்தப்பட்டுள்ளன; காட்சிகள் மூலமாகவும் செய்திகள் சொல்லப்படுகின்றன.</p><p>7 என்னும் எண் 52 முறை இடம்பெறுகிறது. இது முழுமையைக் குறிக்கும் எண். ஒரு வேளை இது கடவுளையும் உலகையும் உள்ளடக்கும் எண்ணாக இருக்கலாம். ஏனெனில் மூன்று என்னும் எண் கடவுளைக் குறிக்கும்; நான்கு என்னும் எண் உலகைக் குறிக்கும். இவ்வாறு அனைத்தையும் உள்ளடக்கிய முழுமையைக் குறிக்கின்ற எண்ணாக ஏழு கருதப்பட்டிருக்கலாம். மேலும் 10, 1000 என்பவையும் முழுமையைக் குறிப்பனவாக உள்ளன.</p><p>144 என்னும் எண்ணும், 12000, 1,44,000 என்பவையும் திருச்சபையைக் குறித்து நிற்கின்றன. 7 என்னும் முழுமையில் பாதியான 3&frac12; என்னும் எண்ணைப் பயன்படுத்தி, இன்னும் எத்தனை ஆண்டுகளுக்குத் துன்பங்கள் நிகழும் என்பதை அறுதியிட்டுக் கூற முடியாது என ஆசிரியர் சுட்டிக்காட்டுகிறார்.</p><p>கிறிஸ்தவர்களின் துன்பத்திற்குச் சின்னமாய் இருப்பவை ஏழு முத்திரைகள் (அதி 7,8), ஏழு எக்காளங்கள் (அதி 8,9), ஏழு கிண்ணங்கள் (அதி 16).</p><p>துன்புறுத்துவோருக்குச் சின்னங்களாய் இருப்பவை விலங்கு (அதி 13), விலைமகள் (அதி 17), பாபிலோன் (அதி 18). இங்கு பாபிலோன் எதிரிகளின் அரசான உரோமையைக் குறித்து நிற்கிறது.</p><p>இயேசு குரு என்றும் (1:12-16), சிங்கம் என்றும் (5:5), ஆட்டுக்குட்டி என்றும் (5:6), குழந்தை என்றும் (12:5), மணமகன் என்றும் (22:12), விண்மீன் என்றும் (21:13, 22:16) குறிப்பிடப்படுகிறார். கதிரவனை ஆடையாக உடுத்திய பெண் (12:1-16) புதிய இஸ்ரயேலாகிய திருச்சபையைக் குறித்து நிற்கிறார்.</p><h2>அமைப்பு</h2><ol><li>முன்னுரை (நூன்முகம்) 1: 1 - 3</li><li>ஆசியாவிலுள்ள திருச்சபைகளுக்குக் கடிதம் 1:4 - 3:22</li><li>ஏழு முத்திரைகளுள்ள சுருளேடு 4:1 - 8:1</li><li>ஏழு எக்காளங்கள் 8:2 - 11:19</li><li>அரக்கப் பாம்பும் இரு விலங்குகளும் 12:1 - 13:18</li><li>காட்சிகள் 14:1 - 15:8</li><li>ஏழு கிண்ணங்கள் 16:1 - 21</li><li>பாபிலோனின் அழிவும் எதிரிகளின் தோல்வியும் 17:1 - 20:15</li><li>புதிய விண்ணகமும் புதிய மண்ணகமும் 21:1 - 22:15</li><li>முடிவுரை 22:16 - 21</li></ol>'),
(100, 'Preface', 'முன்னுரை', 'முன்னுரை', 'intro', NULL, 'விவிலியச் சங்கங்களின் இணையமும் (United Bible Societies) வத்திக்கான் ஒன்றிப்புச் செயலகமும் (Secretariate for Christian Unity) இணைந்து பொது மொழிபெயர்ப்பு செய்யும் கோட்பாட்டை 1968 இல் வெளியிட்டன. அதன் அடிப்படையில் 1972-ஆம் ஆண்டு தமிழ் விவிலியப் பொது மொழிபெயர்ப்புப் பணி தொடங்கியது. உரோமைக் கத்தோலிக்கத் திருச்சபை சார்பில் தமிழ்நாடு விவிலிய ஆணைக்குழுவினரும் ஏனையத் திருச்சபைகளின் சார்பில் விவிலியச் சங்கங்களின் பிரதிநிதிகளும் இப்பணியைச் செய்தனர். பல்வேறு திருச்சபை மரபுகளைச் சேர்ந்த தமிழ், எபிரேய, கிரேக்க மொழிகளில் புலமை பெற்ற மொழிபெயர்ப்பாளர்கள் பங்குகொண்டார்கள்.<br/> <br/>பழைய ஏற்பாடு, எபிரேய பாடமான ‘பிபிலியா எபிராயிக்கா’ நூலையும் (Biblia Hebraica, Stuttgart Edition – Masoretic Text) புதிய ஏற்பாடு, விவிலியச் சங்கங்களின் இணையத்தின் வெளியீடான கிரேக்க புதிய ஏற்பாட்டு நூலின் மூன்றாம் பதிப்பையும் (The Greek New Testament, UBS, third editinon) சார்ந்து மொழிபெயர்க்கப்பட்டன.<br/> <br/>தொன்மையும் தரமுமான மூலபாடங்களையே அடிப்படையாகக் கொண்டாலும், மரபு மொழிபெயர்ப்புகளுக்கு மதிப்புத் தரும் வகையில் சில இடங்களில் காலத்தால் பின்னைய பாடங்களும் இடம்பெறுகின்றன. அவை சதுர அடைப்புக்குறிக்குள் [………] இடப்பட்டுள்ளன. ஒன்றுக்கு மேற்பட்ட முறையில் மொழிபெயர்க்கத்தக்க சொற்றொடர்கள் வருமிடத்து, மாற்று மொழிபெயர்ப்புகள் அடிக்குறிப்புகளில் தரப்பட்டுள்ளன. மூலச்சொல்லின் பொருள் தெளிவில்லாதபொழுது, பழங்கால மொழிபெயர்ப்புகள், இணைமொழிச் சொற்கள் ஆகியவற்றின் உதவியுடன் மொழிபெயர்ப்பு செய்யப்பட்டுள்ளது. அவ்விடத்து மூலச்சொல்லின் பொருள் அடிக்குறிப்பில் தரப்பட்டுள்ளது. நீண்ட ஆய்வுக் குறிப்புகளும் விவாதத்துக்குரிய கொள்கை விளக்கங்களும் அடிக்குறிப்புகளில் தவிர்க்கப்படுகின்றன. ஒவ்வொரு நூலிலும் முன்னுரை, துணைத் தலைப்புக்கள், இணைவசனங்கள் ஆகியவை தரப்பட்டுள்ளன. இவை நூல்களைப் புரிந்துகொள்ள உதவியாக அமையும்.<br/> <br/>இம்மொழிபெயர்ப்பில் இயல்பான, தரமான, எளிய தமிழ்மொழிநடை இடம்பெற்றுள்ளது. தெளிபொருள் மொழிபெயர்ப்பு முறைக்கு (Dynamic Equivalence) முன்னுரிமை வழங்கப்பெற்றுள்ளது. வழக்கற்ற சொற்களும், திசைச் சொற்களும் இயன்றவரை தவிர்க்கப்பட்டுள்ளன. கவிதைப்பகுதிகள் முடிந்தவரை கவிதை நடையில் அமைக்கப்பெற்றாலும், பொருள்தெளிவே இன்றியமையாததாகக் கருதப்பட்டுள்ளது.<br/> <br/>கலைச் சொற்களும் பெயர்ச்சொற்களும் நூல் தலைப்புகளும் பல்வேறு கலந்துரையாடல்களுக்குப்பின் மேல்மட்டக்குழுவின் (High Power Committee) ஒப்புதல் பெற்றுக் கையாளப்பட்டுள்ளன. பெயர்ச்சொற்கள் அனைத்தும் ஒலி பெயர்ப்பே (Transliteration) செய்யப்பட்டன. அவை தமிழில் ஒலிப்பதற்கு எளிமையான, இயல்பான இறுதி வடிவம் பெற்றுள்ளன என்பது குறிப்பிடத்தக்கது. பழைய ஏற்பாட்டுப் பெயர்கள் புதிய ஏற்பாட்டில் வரும்போது, பழையஏற்பாட்டு ஒலிவடிவத்தையே பெறுகின்றன (பிலயாம்/ பாலாம் בִּלְעָם - Βαλαὰμ - பிலயாம்) . தற்கால வழக்கிலுள்ள பெயர்கள் விவிலியத்தில் வேற்றுப் பெயர்களாக இருந்தாலும் அவை தற்கால வடிவிலேயே கையாளப்பட்டுள்ளன (மிஸ்ராயிம் - מִצְרָ֫יִם - எகிப்து). அவ்வாறே நீட்டலளவைகளுக்கும் நிறுத்தலளவைகளுக்கும் பல இடங்களில் தற்கால வழக்கிலுள்ள மெட்ரிக் இணை வழங்கப்பட்டுள்ளது.<br/> <br/>மரபு காரணமாக முதல் ஐந்து நூல்கள் கிரேக்கவழி தலைப்புகளையும், மற்ற எல்லா நூல்களும் எபிரேயவழி தலைப்புகளையும் கொண்டுள்ளன. அனைத்து நூல்களின் வசன எண்கள் உலகப் பொது வழக்கு மொழிபெயர்ப்புகளின் (Standard Versions) அடிப்படையில் அமைந்துள்ளன.<br/> <br/>தமிழின் சிறப்புப் பண்பான மரியாதைப் பன்மையை (Honorific Plural) மனதிற்கொண்டு விவிலியத்தில் பெரும்பாலோர்க்கும் மரியாதைப்பன்மை வழங்கப்படுகிறது. நோயுற்றோர், உடல் ஊனமுற்றோர், செல்வாக்கற்ற பணியாளர், பெண்டிர் ஆகியோரை மதிப்பின்றி வழங்கும் முறை தவிர்க்கப்படுகிறது. எடுத்துக்காட்டாக, குருடன் என்னும் சொல் பார்வையற்றவர் என்று மாற்றம் பெறுவதோடு, மரியாதைப் பன்மையும் பெறுகிறது. இறைத்திட்டத்திற்கு எதிரானவரைக் குறிப்பிடும்போதும், பழித்துரைக்கும் சூழலிலும், நெருங்கிய நட்புறவை வெளிப்படுத்தும்போதும் மரியாதைப்பன்மை தவிர்க்கப்படுகிறது.<br/> <br/>இருபாலார்க்கும் பொதுவான கருத்துக்கள் மூலநூலில் ஆண்பால் விகுதி பெற்று வந்தாலும் இருபாலார்க்கும் பொருந்தும் முறையில் (Inclusive Language) மொழிபெயர்க்கப்பட்டுள்ளன. இவ்வாறு, மூல மொழியிலும் தமிழ் மொழியிலும் காணப்படும் பண்பாட்டு ஏற்றத்தாழ்வு களையப்படுகிறது.<br/> <br/>இம்மொழிபெயர்ப்பு பல கட்டங்கள் தாண்டி வந்துள்ளது. அடிப்படை நகல்கள் தயாரித்த மொழிபெயர்ப்பாளர், திருத்தம் அளித்த பரிசீலனைக்குழுவினர், மாற்றுக்கருத்துக்கள் வழங்கிய ஆலோசனைக்குழுவினர், பேராயர்களும் ஆயர்களும் தலைவர்களும் அடங்கிய மேல்மட்டக் குழுவினர் ஆகியோரின் ஒத்துழைப்புடன் இம்மொழிபெயர்ப்பு இறுதி வடிவம் பெற்றுள்ளது. மேலும் முன்னோட்டப் பதிப்புகள் (Trial Editions) வழியாக ஆயிரக்கணக்கானோரின் கருத்துக்கள் வரவேற்க்கப்பட்டு, தரமானவை ஏற்றுக் கொள்ளப்பட்டன.<br/> <br/>தூய ஆவியின் ஆற்றலோடும் மிகுந்த உழைப்போடும் இறைவேண்டலோடும், கடந்த இருபத்திரண்டு ஆண்டுகளாக திருச்சபையின் கண்காணிப்பில், முப்பத்தைந்து இறைத் தொண்டர்கள் செய்த இம்மொழிபெயர்ப்பை, கிறிஸ்தவர்கள் அனைவரும் ஆண்டவர் இயேசு விரும்பும் ஒற்றுமையை வளர்க்கும் நோக்கில் பயன்படுத்துவர் என நம்புகிறோம்.<br/> <br/>பொருள் துல்லியம், தெளிவு, எளிமை, மக்கள் மொழிநடை, மனிதநேயம், இருபால் சமத்துவநோக்கு ஆகியவற்றைக் கண்முன்கொண்டு செய்யப்பட்ட இம்மொழியாக்கம் கிறிஸ்தவ சமுதாயம் மட்டுமல்ல, தமிழ்கூறும் நல்லுலகம் முழுவதும் இறையுளத்தை அறிந்து செயல்படத் தூண்டும் என நம்புகிறோம். திருவிவிலியம் திருச்சபை வழிபாடுகளிலும், கருத்தரங்குகளிலும், விவிலியப் பள்ளிகளிலும், வீதிகளிலும் பயன்பட வேண்டும்; இல்லங்களிலும் உள்ளங்களிலும் இடம் பெற வேண்டும்.<br/> <br/><p align="center">இதன் மூலம் இறையாட்சி வருக! இறைவாக்கு ஒளிர்க! </p>'),
(400, 'Deuterocanonical books', 'இணைத் திருமுறை நூல்கள்', 'இணைத் திருமுறை நூல்கள்', 'intro', NULL, '<p>தோபித்து, யூதித்து, சாலமோனின் ஞானம், சீராக்கின் ஞானம், பாரூக்கு, 1 மக்கபேயர், 2 மக்கபேயர் ஆகிய ஏழு நூல்களும் எஸ்தர், தானியேல் நூல்களில் காணப்படும் கிரேக்க இணைப்புகளும் எபிரேய விவிலியத்தில் இடம்பெறவில்லை. இருப்பினும் ‘செப்துவசிந்தா’ (எழுபதின்மர் மொழிபெயர்ப்பு) எனப்படும் கிரேக்க விவிலியத்தில் மேற்காணும் நூல்கள் இடம்பெறுகின்றன.</p><p>இந்நூல்களின் இறைஏவுதல் பற்றிக் கிறிஸ்தவர்கள் நடுவில் கருத்து வேறுபாடு தொடக்கத்திலிருந்தே நிலவிவந்துள்ளது. திரெந்து நகரில் கூடிய திருச்சங்கம் இவற்றை இறைஏவுதலால் எழுதப்பட்ட திருநூல்கள் என்று கி.பி. 1546இல் அதிகாரப்பூர்வமாக அறிக்கையிட்டது. இதனால் இவை “இணைத் திருமுறை நூல்கள்” (Deutero-canonical books) எனப் பெயர் பெறுகின்றன.<br /><br />திருச்சபைச் சீர்திருத்தத்தில் ஈடுபட்ட மார்ட்டின் லூத்தர் இந்நூல்களை விவிலியத்தைச் சேர்ந்த நூல்களாக ஏற்றுக்கொள்ளவில்லை; எனினும் இவை பயனுள்ளவை, படிக்கத் தகுந்தவை என்று ஒப்புக்கொண்டார். 1534இல் தாம் வெளியிட்ட ஜெர்மன் விவிலிய மொழிபெயர்ப்பில் இவற்றைப் பழைய ஏற்பாட்டின் இறுதியில் “திருமுறைப் புற நூல்கள்” (Apocrypha) என்னும் தலைப்பின்கீழ் வெளியிட்டார். அதைத் தொடர்ந்து வெளிவந்த பல மொழிபெயர்ப்புகளும் இம்முறையையே பின்பற்றின.</p><p>சீர்திருத்தச் சபைகளின் அமைப்பான விவிலியச் சங்கங்களின் இணையமும் கத்தோலிக்கத் திருச்சபையின் கிறிஸ்தவ ஒன்றிப்புச் செயலகமும் இணைந்து பொது விவிலிய மொழிபெயர்ப்புக்குரிய விதிமுறைகளை 1968-இல் வெளியிட்டன. பின் அவ்விதிமுறைகளை 1987-இல் திருத்தியமைத்தன. இப்பொழுது வெளிவந்து கொண்டிருக்கும் பொது விவிலிய மொழிபெயர்ப்புகள் எல்லாமே மேற்கண்ட விதிமுறைகளைக் கடைப்பிடித்து வருகின்றன. அவற்றின்படி இணைத் திருமுறை நூல்களைப் புதிய ஏற்பாட்டு நூல்களுக்கு முன்னர் அச்சிடும் வழக்கம் இப்பதிப்பில் பின்பற்றப்பட்டுள்ளது.<br /><br />முனைவர் ஆல்பிரட் ரால்வ்ஸ் என்பவரால் பதிப்பிக்கப்பட்டு, 1935-இல் ஜெர்மன் விவிலியச் சங்கத்தால் ஸ்டுட்கார்ட் நகரில் வெளியிடப்பட்ட ‘செப்துவசிந்தா’ கிரேக்கப் பதிப்பு இப்பகுதிக்கு மூலபாடமாய் அமைகிறது.<br /><br />கிறிஸ்து பெருமான் தோன்றுவதற்குச் சற்றே முற்பட்ட யூத வரலாறு, வாழ்க்கை முறை, சிந்தனை, சமயப் பழக்கவழக்கங்கள் முதலியன பற்றிப் பல செய்திகள் இந்நூல்களிலிருந்து நமக்குக் கிடைப்பதால், வரலாற்று இயேசுவைப் புரிந்துகொள்ள இவை நமக்குப் பெரிதும் துணைபுரிகின்றன. இதனால் பிரிவு மனப்பான்மையை விடுத்துத் திறந்த உள்ளத்தோடு இந்நூல்களைப் படிக்கும் நிலை இன்று உருவாகிவருகிறது.</p>'),
(540, 'Epistles', 'திருமுகங்கள்', 'திருமுகங்கள்', 'intro', NULL, '<p>திருத்தூதர்கள், திருச்சபைத் தலைவர்கள் ஆகியோர் அக்காலத் தேவைகளின் அடிப்படையில் எழும் கேள்விகளுக்கு விடையளிக்கவும், மாற்றுக் கருத்துகளுக்கு மறுப்புத் தெரிவிக்கவும், மக்களை இறைநம்பிக்கையில் வலுவடையச் செய்யவும் திருமுகங்களை எழுதினர். அவை குறிப்பிட்ட தலத் திருச்சபைக்கு எழுதப்பட்டவை போலத் தோன்றினாலும் சுற்றுமடல்களாகவே கருதப்பட்டன. பவுல் எழுதிய திருமுகங்கள் அவை எழுதப்பட்ட காலத்தின் அடிப்படையில் அல்ல, மாறாக அவற்றின் அளவைக் கொண்டே வரிசைப்படுத்தப்பட்டுள்ளன.</p><h2>அமைப்பு</h2><p>அக்கால மடல்இலக்கியப் பாணியிலேயே விவிலியத் திருமுகங்கள் அமைந்துள்ளன.</p><ol><li><strong>முன்னுரையும் வாழ்த்தும்:</strong><br />பொதுவாக விவிலியத் திருமுகங்களில் “கைரே” என்னும் கிரேக்க வாழ்த்து “அருள்” எனக் கிறிஸ்தவ மயமாக்கப்பட்டு, யூத வாழ்த்தாகிய “சாலோம்” (அமைதி) என்னும் சொல்லுடன் இணைக்கப் பெற்று, தந்தையாம் கடவுளும் ஆண்டவர் இயேசு கிறிஸ்துவும் வழங்கும் அருளும் அமைதியும் உரித்தாகுக, என வருகிறது.</li><li><strong>நன்றி கூறுதல்: </strong><br />இப்பகுதியில் திருமுக வாசகர்கள் அளிக்கும் உதவிகளுக்காக, நன்கொடைகளுக்காக, வேண்டுதல்களுக்காக, தாமே கற்பித்தவற்றின்படி ஒழுகுவதற்காக, முன் மாதிரியான கிறிஸ்தவ வாழ்வு வாழ்ந்து வருவதற்காக ஆசிரியர் இறைவனுக்கு நன்றி செலுத்துவார். சில நிகழ்ச்சிகளும் இப்பகுதியில் இடம் பெறலாம்.</li><li><strong>உள்ளடக்கம்:</strong><br />திருமுகங்களின் பெரும் பகுதி இது; முக்கிய பகுதியும்கூட. இது பொதுவாக கொள்கைப் பகுதி, அறிவுரைப் பகுதி (பரிந்துரைப் பகுதி) என்னும் இரு பகுதிகளைக் கொண்டதாக அமைந்திருக்கும்.</li><li><strong>முடிவுரையும், இறுதி வாழ்த்தும்:</strong><br />இங்கு ஆசிரியர் தம்மைப் பற்றிய செய்திகளையும், உடன் உழைப்பாளர்கள் பற்றிய செய்திகளையும் வழங்குவார். அனைவருடைய வாழ்த்துகளோடும் திருமுகம் முடிவுறும்.</li></ol><h2>பவுல் எழுதிய திருமுகங்கள்</h2><p>புதிய ஏற்பாட்டுத் திருமுகங்களுள் பல பவுலோடு தொடர்புபடுத்தப்பட்டுள்ளன. அவற்றுள் சில அவரே நேரடியாக எழுதியவை. மற்றவை அவருடைய சிந்தனை அடிப்படையில் எழுந்தவை. பவுல் எழுதிய திருமுகங்களை அவரது வாழ்க்கைப் பின்னணியில் படிக்கும்போது கிறிஸ்தவ உண்மைகளை ஆழமாகக் கற்றுக்கொள்ள முடிகிறது. அவர்தம் திருமுகங்கள் வாயிலாகவும் திருத்தூதர் பணிநூல் மூலமாகவும்தான் அவரது வாழ்க்கையைப் பற்றி நாம் அறிகிறோம்.</p><h2>பவுலது வாழ்க்கை</h2><h3>இளமைப் பருவமும் கல்வியும்</h3><p>பவுல் பென்யமின் குலத்தைச் சேர்ந்த யூதக் குடும்பத்தில் கி.பி. 11ஆம் ஆண்டளவில் பிறந்தார். இவரது யூதப் பெயர் சவுல். இன்றைய துருக்கி நாட்டின் பகுதியான சிலிசியா மாநிலத்தின் உரோமைக் குடியிருப்பான தர்சு நகரத்தில் இவரது குடும்பம் வாழ்ந்து வந்தது. செல்வமும் செல்வாக்கும் பெற்றிருந்த இவரது குடும்பத்திற்கு உரோமைக் குடியுரிமையும் இருந்தது.<br /><br />இவர் இளமையிலிருந்தே யூதச் சட்டங்களையும் நெறிமுறைகளையும் கற்றறிந்தார்; உலகப் பொதுமொழியாயிருந்த கிரேக்கத்தையும் கற்றுத் தெளிந்தார்; பின்னர் எருசலேம் சென்று புகழ் பெற்ற கமாலியேல் என்னும் யூத ரபியிடம் கல்வி பயின்றார்.</p><p>யூதக் கோட்பாடுகளைக் கில்லேல் என்பவரது விளக்கங்களைத் தழுவிக் கடைப்பிடிக்கும் பரிசேயச் சமயப் பிரிவின் ஆர்வமிக்க உறுப்பினராக இருந்தார். இயேசு வாழ்ந்த காலத்தில் இவர் பாலஸ்தீனத்தில் இருந்திருக்கலாம் எனக் கூற இடம் உண்டு.</p><h3>மனம் மாற்றம்</h3><p>முதலில் தொடக்கக் காலக் கிறிஸ்தவர்களைத் துன்புறுத்தும் கூட்டத்தில்தான் இவரை நாம் சந்திக்கிறோம். சவுல் ஸ்தேவானைக் கல்லெறிந்து கொல்வதற்கு உடன்பட்டிருந்தார் (திப 8:1). கிறிஸ்தவர்களைத் துன்புறுத்துவதற்காகத் தமஸ்கு நகருக்குச் செல்லும் வழியில் கிறிஸ்து அவரைத் தடுத்து ஆட்கொண்டார். அனனியா என்னும் கிறிஸ்தவர் வாயிலாகப் பிற இனத்தவருக்கு நற்செய்தி அறிவிக்கும் திருத்தூதராகக் கிறிஸ்து தம்மை அழைப்பதை அவர் அறிந்துகொண்டு, தம்மை அர்ப்பணித்தார். சமயப் பற்றும் சட்டப் பற்றும் மிக்கவராக இருந்த சவுல் மனம் மாறியபின் கடவுளின் பேரருளைப் பறைசாற்றும் ஆர்வமிக்க திருத்தூதர் ஆனார். அதற்குமுன் அரேபியாவுக்குச் சென்று தம்மைத் தயார் செய்து கொண்டார் (கலா 2:17). பின்னர் தமஸ்கு, எருசலேம் பகுதிகளுக்குச் சென்று தம் பணியைத் தொடங்கினார்.</p><h3>நற்செய்திப் பணி</h3><p>பவுல் தம் முதல் நற்செய்திப் பயணத்தைக் கி.பி. 46-48 ஆண்டுகளில் மேற்கொண்டு, சைப்பிரசுக்கும் சின்ன ஆசியா நாட்டுப் பகுதிகளுக்கும் சென்று திருச்சபையை நிறுவினார் (திப 13, 14; 2 திமொ 3:11). கி.பி. 49ஆம் ஆண்டில் எருசலேம் பொதுச் சங்கத்தில் கலந்து கொண்டு பிற இனத்தாரிடையே தூய ஆவி செயல்படுதலைப் பற்றி எடுத்துரைத்துத் தமது பணிக்குச் சங்கத்தின் ஒப்புதலைப் பெற்றுக் கொண்டார் (திப 15; கலா 2:3-9).</p><p>கி.பி. 50-52க்கு உட்பட்ட காலத்தில் தமது இரண்டாவது நற்செய்திப் பயணத்தை மேற்கொண்டு தாம் ஏற்கெனவே நிறுவிய சபைகளை வலுப்படுத்தினார்; பின்னர் மாசிதோனியா, அக்காயா பகுதிகளுக்குச் சென்று நற்செய்தியை அறிவித்து, அங்கும் திருச்சபைகளை நிறுவினார் (திப 15-18). கி.பி. 53-57 வரை மூன்றாம் நற்செய்திப் பயணத்தின்போது கலாத்தியா, பிரிகியா, கொரிந்து, மாசிதோனியா, இல்லிரிக்கம் ஆகிய இடங்களுக்குச் சென்று திருப்பணி ஆற்றினார்; எபேசை மையமான பணித்தளமாகக் கொண்டு செயல்பட்டார்; அங்குச் சிறைப்பட்டார். அக்காலத்தில் அவர் சில சிறைக்கூட மடல்களை எழுதியிருக்கலாம். பின் கி.பி. 58இல் எருசலேமில் கைதானார்; கி.பி. 60 வரை செசரியாவில் சிறைப்பட்டிருந்தார்; உரோமைப் பேரரசர் சீசரே தமக்குத் தீர்ப்பு அளிக்க வேண்டும் என்று கேட்டுக்கொண்டதால் உரோமைக்கு அனுப்பப்பெற்றார்; அங்குப் போகும் வழியில் கப்பல் அழிவுற நேரிட்டதால் மால்தா தீவினருக்கு நற்செய்தி அறிவிக்கும் வாய்ப்பைப் பெற்றார்; பின்பு உரோமை வந்தடைந்து இரு ஆண்டுகள் வீட்டுக் கைதியாகவே இருந்துகொண்டு நற்செய்தியை அறிவித்து வந்தார்.</p><p>பின்பு பவுல் விடுதலை பெற்று ஸ்பெயின் நாட்டிற்குச் சென்றிருப்பார் என நம்ப இடமிருக்கிறது. மீண்டும் கி.பி. 60இல் கைது செய்யப்பட்டு நீரோ மன்னன் காலத்தில் மரண தண்டனை பெற்றார் என மரபு கூறுகிறது.</p><p>திருத்தூதுப் பணி செய்த பவுல் தாம் நிறுவிய சபைகளை மீண்டும் போய்ப் பார்த்து, கிறிஸ்தவ நம்பிக்கையில் திடப்படுத்தும் மேய்ப்புப் பணியைத் தொடர்ந்து ஆற்றினார்; தாம் செல்ல முடியாத இடங்களுக்கு உடன் பணியாளர்களை அனுப்பினார்; அம்மக்களுக்குப் பல திருமுகங்களைச் சுற்றறிக்கை மடல்களாக அனுப்பினார். தாம் சென்றிராத உரோமை நகரத் திருச்சபைக்கும் மடல் எழுதி உலகத் திருச்சபை ஏற்றுக்கொள்ள வேண்டிய ஆழமான பொதுவான இறையியல் கருத்துக்களை வெளியிட்டார். இவ்வாறு திருத்தூதராகவும் ஆயராகவும் இறையியலாளராகவும் இலங்குகிறார் தூய பவுல்.</p>'),
(680, 'General epistles', 'பொதுத் திருமுகங்கள்', 'பொதுத் திருமுகங்கள்', 'intro', NULL, '<p>புதிய ஏற்பாட்டில் உள்ள திருமுகங்களுள் பவுலோடு தொடர்புபடுத்தப்பட்டு வந்த திருமுகங்கள் பதினான்கு. பிற ஏழு திருமுகங்களும் பொதுத் திருமுகங்கள் எனப்படுகின்றன. இவை யாக்கோபு, 1,2 பேதுரு, 1,2,3 யோவான், யூதா திருமுகங்கள் ஆகும்.</p><p>இவை குறிப்பிட்ட சபைக்கு என இல்லாமல், பொதுவாக அனைத்துலகத் திருச்சபைக்கும் எழுதப்பட்டுள்ளதால் பொதுத் திருமுகங்கள் எனப்படுகின்றன. ஆயினும் எல்லாப் பொதுத் திருமுகங்களும் அனைத்துலகத் திருச்சபைக்கு எழுதப்பட்டவை எனக் கூற இயலாது. மூன்றாம் யோவான் திருமுகம் தனிப்பட்ட ஒரு நபருக்கு எழுதப்பட்டது. இரண்டாம் யோவான் திருமுகம் தனிப்பட்ட ஒரு சபைக்கு எழுதப்பட்டது. பேதுருவின் முதல் திருமுகம் சின்ன ஆசியாவிலுள்ள பல சபைகளுக்கு எழுதப்பட்டுள்ளது. எனினும் பழங்காலத்திலிருந்தே இவை ‘பொதுத் திருமுகங்கள்’ எனப் பெயர் பெற்று வந்துள்ளன.</p>');
