--
-- Table structure for table `t_bookkey`
--

CREATE TABLE `t_bookkey` (
  `bn` int(3) NOT NULL COMMENT 'Book #',
  `osis_id` varchar(10) NOT NULL COMMENT 'OSIS Book ID',
  `en` text NOT NULL COMMENT 'English Name',
  `tn_f` text NOT NULL COMMENT 'Tamil Name Full',
  `tn_s` text NOT NULL COMMENT 'Tamil Name Short',
  `tn_a` text NOT NULL COMMENT 'Tamil Name Abbreviation',
  `tn_o` text DEFAULT NULL COMMENT 'Tamil Name Old',
  `intro` text NOT NULL COMMENT 'Book Introduction'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

